INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `description`, `status`, `pos`, `seo_title`, `seo_keyword`, `seo_description`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, ' Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay ', 'cau-chuyen-kinh-hai-nay-se-ly-giai-vi-sao-ban-khong-duoc-bo-qua-mot-vet-soc-den-tren-mong-tay', 'http://afamilycdn.com/zoom/100_62/2017/y1-1507803446227-0-0-406-650-crop-1507866109666.jpg', 'Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay          <div data-check-position=\"af_detail_body_start\"></div>          <h2>Phát hiện ung thư tế bào hắc tố từ vết sọc đen trong móng tay</h2><p><a class=\"seo-suggest-link\" href=\"http://afamily.vn/vet-soc-den-tren-mong-tay.html\" target=\"_blank\" title=\"vết sọc đen trên móng tay\">Vết sọc đen trên móng tay</a> có thể nghiêm trọng hơn bạn nghĩ rất nhiều. Đó là lời cảnh báo từ một viên làm móng từ Anh đưa cho khách hàng của cô sau khi người phụ nữ này phàn nàn về một đường thẳng màu đen trên móng tay cô.  </p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/y1-1507803446227.jpg\" id=\"img_8a7b9860-af36-11e7-a065-7937920d296b\" w=\"650\" h=\"413\" alt=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 1.\" title=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 1.\" rel=\"lightbox\" photoid=\"8a7b9860-af36-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/y1-1507803446227.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Vết sọc đen trên móng tay có thể nghiêm trọng hơn bạn nghĩ rất nhiều.</p></div></div><p>Ở bài viết đăng tải trên facebook, nhân viên làm móng Jean Skinner chia sẻ rằng, một khách hàng đến cửa hàng của cô yêu cầu sơn móng tay để che giấu vết sọc đen trên ngón tay cô ấy. Cô đã đi đến các tiệm khác trong nhiều năm, và các nhân viên nói với cô rằng sọc đen trên móng tay là một vết tụ máu, dấu hiệu của sự thiếu hụt canxi, hoặc thậm chí chỉ là do di truyền.  </p><p>Skinner nói với người phụ nữ rằng cô cần phải đến gặp bác sĩ ngay lập tức và người phụ nữ đã làm theo. Vài tuần sau, cô ấy đã gọi điện cho Skinner và chia sẻ vết sọc đen trên móng tay được chẩn đoán là khối u ác tính, đã lan đến các <a class=\"seo-suggest-link\" href=\"http://afamily.vn/hach-bach-huyet.html\" target=\"_blank\" title=\"hạch bạch huyết\">hạch bạch huyết</a>.  </p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/y2-1507803446228.jpg\" id=\"img_8a927bc0-af36-11e7-a047-73f8081241a6\" w=\"675\" h=\"400\" alt=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 2.\" title=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 2.\" rel=\"lightbox\" photoid=\"8a927bc0-af36-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/y2-1507803446228.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Nail melanoma được gọi là u hắc tố dưới móng, u ác tính dưới da, hay còn gọi là ung thư da.</p></div></div><p>Nail melanoma được gọi là u hắc tố dưới móng,&nbsp;u ác tính dưới da, hay còn gọi là ung thư da. Đây là căn bệnh ung thư rất hiếm gặp, có thể gây tử vong. Theo một nghiên cứu của Dermatologic Surgery, nó chiếm ít hơn 2,5% trường hợp u ác tính ở người da trắng, nhưng đến 35% trường hợp u ác tính ở những người da đen , và khoảng một nửa trường hợp u ác tính ở người châu Á.</p><h3>Ung thư tế bào hắc tố - Đừng vội bỏ qua từ những dấu hiệu cực nhỏ</h3><p>Một lý do nghiêm trọng là vì bệnh nhân thường không được chẩn đoán chính xác ngay lập tức. Trên thực tế, theo nghiên cứu, sự chậm trễ trung bình của chẩn đoán cho các bệnh u hắc dưới da là 2,5 năm. Đó không phải vì người ta không để ý đến vết sọc trên ngón tay ngón chân mà là nghĩ đó là dấu hiệu của cái gì đơn giản hơn là u ác tính.  </p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/y3-1507803446229.jpg\" id=\"img_8aa2cf70-af36-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"348\" alt=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 3.\" title=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 3.\" rel=\"lightbox\" photoid=\"8aa2cf70-af36-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/y3-1507803446229.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Thông tin từ Bệnh viện Da liễu Trung ương cho thấy, ung thư tế bào hắc tố chiếm khoảng 5% các ung thư da và khoảng 1% các loại ung thư.</p></div></div><p>Trong nhiều trường hợp, một khối u ác tính dưới da giống như một cái gì đó gọi là một khối u dưới da, hoặc một \"mụn nước\". Điều này thường xuất phát từ một số chấn thương như bấm móng tay vào cửa xe bus, chấn thương tại phòng gym… Các mạch máu nhỏ bên dưới móng tay bị đứt, làm máu chảy ra và làm mất màu móng.  </p><p>Thông tin từ Bệnh viện Da liễu Trung ương cho thấy, ung thư tế bào hắc tố chiếm khoảng 5% các ung thư da và khoảng 1% các loại ung thư. Theo nhiều nghiên cứu thì số lượng bệnh nhân ung thư tế bào hắc tố ngày càng tăng, đặc biệt ở châu Úc, Bắc Mỹ, và các nước bắc Âu. Ở Mỹ năm 2006 có 111.900 trường hợp ung thư tế bào hắc tố được phát hiện.  </p><p> Ánh nắng mặt trời là tác nhân chủ yếu gây ung thư tế bào hắc tố, trong đó tia cực tím ultra violet (ƯV), thể gây nên những biến đổi của nhiễm sắc thể. 2 đến 5% ung thư tế bào hắc tố có tính chất gia đình. 30% những bệnh nhân có tiền sử gia đình mắc ung thư tế bào hắc tố có những bất thường của gen pl6 trên chromosome 9p21.  </p><p>BS Đỗ Hữu Thảnh (Nguyên bác sĩ tại Bệnh viện đa khoa tỉnh Hà Nam) cho biết, ung thư tế bào hắc tố có biểu hiện ban đầu rất đa dạng và đơn giản thường dễ bị bỏ qua mặc dù tổn thương ở ngay trên da, đến khi phát hiện thì đã ở giai đoạn muộn, tế bào ung thư đã di căn xa, khó chữa trị. Bệnh tiến triển từ những nốt ruồi, các bớt hắc tố trên da, u hắc tố...</p><p>Ung thư tế bào hắc tố xuất hiện trên phần da bình thường. Cụ thể:  </p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/y4-1507803446230.jpg\" id=\"img_8aba2800-af36-11e7-a047-73f8081241a6\" w=\"1200\" h=\"800\" alt=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 4.\" title=\"Câu chuyện kinh hãi này sẽ lý giải vì sao bạn không được bỏ qua một vết sọc đen trên móng tay - Ảnh 4.\" rel=\"lightbox\" photoid=\"8aba2800-af36-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/y4-1507803446230.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Ung thư tế bào hắc tố có biểu hiện ban đầu rất đa dạng và đơn giản thường dễ bị bỏ qua mặc dù tổn thương ở ngay trên da.</p></div></div><p>- Trên da xuất hiện thêm các nốt ruồi mới, nhất là ở tuổi trên 30, nốt ruồi có hình ảnh không cân đối, bờ không đều hoặc hình răng cưa, chỗ lồi chỗ lõm, màu sắc không đều, đường kính lớn bất thường (trên 6 mm).</p><p>- Trên da xuất hiện một vùng da nhỏ nhám ráp, to nhanh dần trong một thời gian ngắn.</p><p>- Vết bầm tím trên da không lành, mảng màu nâu xám ở đầu ngón hoặc vết tím trên móng đột ngột đổi màu và chuyển thành cục hoặc chảy máu.  </p><p>- Vết lở loét có bờ cứng khó lành có u nhú hạt và gây chảy máu nhiều…  </p><p>Với màng tụ dưới da, một cái móng khỏe mạnh mới sẽ bắt đầu phát triển, và móng bị đổi màu sẽ phát triển. Các mô hình của sự đổi màu móng cũng có thay đổi bất thường như một chấm đen trong móng. Tuy nhiên, chỉ có bác sĩ mới biết được bạn có bị khối u ác tính dưới da hay không.  </p><p>Vì vậy, tóm lại là: Nếu bạn nhận thấy một vết sọc đen trên móng tay hoặc móng chân và không thể nhớ bất kỳ loại chấn thương nào có thể gây ra nó, hoặc nếu nó không có vẻ như nó đang phát triển – hãy đến thăm khám bác sĩ da liễu càng sớm càng tốt. Đây cũng chính là khuyến cáo của TS.Tsippora Shainhouse (bác sĩ trên lâm sàng về Da liễu, Đại học Nam California). Bác sĩ của bạn có thể sẽ làm sinh thiết để kiểm tra khối u ác tính. Tin vui là nếu bạn phát hiện khối u kịp thời, phẫu thuật loại bỏ khối u hoàn toàn có thể được thực hiện trước khi chúng có thể lây lan.</p><p style=\"text-align: right;\"><i>(Nguồn: Tổng hợp)</i></p>          <div data-check-position=\"af_detail_body_end\"></div>      ', 'Dấu hiệu kh&#225;c thường tr&#234;n cơ thể, d&#249; chỉ l&#224; cực nhỏ như vết sọc đen tr&#234;n m&#243;ng tay cũng c&#243; thể l&#224; dấu hiệu căn bệnh nguy hiểm ngo&#224;i sức tưởng tượng của bạn. ', 'publish', 1, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL),
(2, ' Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục ', 'chi-em-nen-bo-ngay-ao-nguc-o-nha-vao-ngay-hom-nay-va-day-la-ly-do-cuc-ki-thuyet-phuc', 'http://afamilycdn.com/zoom/100_62/2017/ung-thu-vu-1310-2-1507873395493-176-22-711-878-crop-1507873669647.jpg', 'Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục          <div data-check-position=\"af_detail_body_start\"></div>          <h2>Hashtag #nobraday và mục đích nâng cao nhận thức về ung thư vú</h2><p>National No Bra Day diễn ra vào ngày 13/10 hàng năm và rơi vào giữa tháng nâng cao nhận thức về <a class=\"seo-suggest-link\" href=\"http://afamily.vn/ung-thu-vu.html\" target=\"_blank\" title=\"ung thư vú\">Ung thư vú</a> (Tháng 10). Ngày kỉ niệm này bắt đầu từ năm 2011, và đã trở nên phổ biến. Rất nhiều chị em sử dụng hashtag #nobraday trên các phương tiện truyền thông xã hội để hỗ trợ cho chiến dịch.<br></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/ung-thu-vu-1310-2-1507873395493.jpg\" id=\"img_6b9943e0-afd9-11e7-aaaa-51ac0faef9da\" w=\"950\" h=\"750\" alt=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 1.\" title=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 1.\" rel=\"lightbox\" photoid=\"6b9943e0-afd9-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/ung-thu-vu-1310-2-1507873395493.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">National No Bra Day diễn ra vào ngày 13/10 hàng năm.</p></div></div><p>Mục đích của chiến dịch \"nói không với áo ngực\" này là để nâng cao nhận thức về tầm quan trọng của việc biết cách tự khám vú, có thể nhận ra các triệu chứng của ung thư vú và khuyến khích phụ nữ tham gia kiểm tra định kì. Đó cũng là ngày trước lễ hội The Big Pink vào ngày 14/10 - ngày gây quỹ quốc gia của Quỹ chống ung thư vú.</p><p>Năm nay cũng là năm đánh dấu kỷ niệm 25 năm ngày ra đời của chiếc băng rôn màu hồng, bây giờ là một biểu tượng quốc tế về nhận thức về ung thư vú được nhiều tổ chức từ thiện trên khắp thế giới chấp nhận.</p><p>Cho tới nay, việc chẩn đoán triệu chứng, các phương pháp điều trị ung thư vú đã được cải thiện, các chương trình tầm soát và nhận thức của cộng đồng cũng được đẩy mạnh và đây là tất cả các yếu tố dẫn đến sự cải thiện tỉ lệ sống sót lâu dài.</p><p>Kết quả là, theo NHS, hơn 8/10 phụ nữ bị ung thư vú sẽ sống sót ít nhất 5 năm sau khi chẩn đoán bị bệnh.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/ung-thu-vu-1310-1-1507873395495.jpg\" id=\"img_6b82aea0-afd9-11e7-a89b-d53b14af2463\" w=\"634\" h=\"786\" alt=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 2.\" title=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 2.\" rel=\"lightbox\" photoid=\"6b82aea0-afd9-11e7-a89b-d53b14af2463\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/ung-thu-vu-1310-1-1507873395495.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Những thay đổi ở ngực cần chú ý.</p></div></div><p><b>Những lợi ích khi không mặc áo ngực</b></p><p>Ngoài việc góp phần ủng hộ phong trào Nobra nâng cao nhận thức về ung thư vú, chị em cũng nên hiểu hơn về lợi ích của việc không mặc áo ngực để củng cố thêm niềm tin cho việc mình đã làm.</p><p>Một số lợi ích của việc cởi bỏ áo ngực, nhất là về ban đêm khi bạn ngủ, bao gồm:</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/ung-thu-vu-1310-4-1507873395489.jpg\" id=\"img_6bc0c910-afd9-11e7-aeea-a394e698d7aa\" w=\"644\" h=\"514\" alt=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 3.\" title=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 3.\" rel=\"lightbox\" photoid=\"6bc0c910-afd9-11e7-aeea-a394e698d7aa\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/ung-thu-vu-1310-4-1507873395489.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Bạn cũng nên cởi bỏ áo ngực vì nhiều lý do khác.</p></div></div><p><i>- <a class=\"seo-suggest-link\" href=\"http://afamily.vn/hang-nghin-me-duoc-tiep-can-phuong-phap-tang-cuong-mien-dich-giup-han-che-su-dung-khang-sinh-cho-tre-2017062016124114.chn\" target=\"_blank\" title=\"Hàng nghìn mẹ được tiếp cận phương pháp tăng cường miễn dịch giúp hạn chế sử dụng kháng sinh cho trẻ\">Tăng cường miễn dịch</a>: </i>Thường xuyên mặc áo ngực có thể khiến da bị bí, khó thoát mồ hôi. Vào mùa hè, điều này càng tạo điều kiện cho vi khuẩn phát triển. Bỏ áo ngực ra, lưu thông trong cơ thể tốt hơn, giảm thiểu nguy cơ nhiễm trùng và cũng đồng thời tăng cường miễn dịch, thải độc tố.</p><p><i>- Ngủ ngon hơn: </i>Không mặc áo ngực khi ngủ giúp bạn tránh tình trạng gò bó, toát mồ hôi và đè nén ngực sai tư thế. Điều này không những tốt cho lưu thông, cho bạn giấc ngủ ngon mà còn giữ cho ngực ở đúng hình dạng.</p><p><i>- Giảm nguy cơ ung thư vú:</i> Mặc áo ngực thường xuyên là một trong những nguyên nhân khiến các mạch máu, nhất là bạch huyết bị tắc nghẽn lưu thông. Về lâu dài, nó có thể khiến cho khả năng miễn dịch, bảo vệ ngực suy giảm. Điều này đồng nghĩa với việc tăng tình trạng xơ cứng, u xơ, ung thư vú… Không mặc áo ngực thường xuyên sẽ có tác dụng cữu vãn nguy cơ này.</p><p><i>Lưu ý: </i>Trong trường hợp phải mặc áo ngực, chị em hãy lưu ý để chọn áo ngực thích hợp nhất nhé. Áo ngực quá chật hay quá lỏng đều có thể tác động đến vòng ngực, thậm chí gây ra cả các vấn đề về lưng, cổ và tư thế.</p><div class=\"VCSortableInPreviewMode active noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/ung-thu-vu-1310-3-1507873395492.jpg\" id=\"img_6bb27130-afd9-11e7-8dbb-355ae774403f\" w=\"1920\" h=\"1080\" alt=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 4.\" title=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 4.\" rel=\"lightbox\" photoid=\"6bb27130-afd9-11e7-8dbb-355ae774403f\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/ung-thu-vu-1310-3-1507873395492.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><h3>Các triệu chứng của bệnh ung thư vú</h3><p>Triệu chứng đầu tiên của ung thư vú hầu hết phụ nữ chú ý là một khối u hoặc một vùng mô dày lên trong vú của họ.</p><p>Hầu hết các khối u vú (90%) không phải là ung thư, nhưng tốt nhất là bạn nên đi khám bác sĩ.</p><p>Bạn nên gặp bác sĩ đa khoa nếu bạn nhận thấy bất cứ điều nào sau đây:</p><p>- Một khối u mới hoặc vùng mô dày lên ở 1 hoặc cả 2 bên vú</p><p>- Sự thay đổi kích thước hoặc hình dạng của 1 hoặc cả 2 vú</p><p>- Núm vú có chảy máu</p><p>- Có một cục u hoặc sưng ở cả 2 bên nách</p><p>- Da vùng ngực bị lồi lõm</p><p>- Phát ban trên hoặc quanh núm vú</p><p>- Sự thay đổi ở núm vú, chẳng hạn như bị tụt vào trong</p><p>- Có thể kèm theo đau ngực</p><div class=\"VCSortableInPreviewMode active noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/ung-thu-vu-1310-5-1507873544790.jpg\" id=\"img_c35b3cf0-afd9-11e7-a89b-d53b14af2463\" w=\"634\" h=\"402\" alt=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 5.\" title=\"Chị em nên bỏ ngay áo ngực ở nhà vào ngày hôm nay và đây là lý do cực kì thuyết phục - Ảnh 5.\" rel=\"lightbox\" photoid=\"c35b3cf0-afd9-11e7-a89b-d53b14af2463\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/ung-thu-vu-1310-5-1507873544790.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p style=\"text-align: right;\"><i>Theo DailyMail/Health/WHM</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Để n&#226;ng cao nhận thức về ung thư v&#250;, chị em phụ nữ được khuyến kh&#237;ch cởi bỏ &#225;o ngực trong ng&#224;y h&#244;m nay - ng&#224;y Quốc tế kh&#244;ng &#225;o ngực (National No Bra). ', 'publish', 2, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL),
(3, ' 10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao ', '10-phut-tap-the-duc-danh-cho-nhung-nguoi-ban-ron-khong-co-ly-do-gi-ban-khong-thu-tap-xem-sao', 'http://afamilycdn.com/zoom/100_62/2017/bai-tap-1o-phut-5-1507864877349-302-92-714-751-crop-1507865136401.gif', '10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao          <div data-check-position=\"af_detail_body_start\"></div>          <p>Lịch trình công việc dày đặc những ngày trong tuần khiến nhiều người có suy nghĩ \"để dành\" tập thể dục vào thứ 7 hoặc Chủ nhật. Tuy nhiên, vào những ngày này chúng ta lại thường vướng vào những kế hoạch khác như đi chơi, tụ họp gia đình hoặc lý do đặc biệt khác.<br></p><p>Các chuyên gia sức khỏe cảnh báo công việc bàn giấy cộng với những bữa ăn chỉ chăm chăm vào màn hình tivi chính là con đường dẫn đến tình trạng sức khỏe trở nên tồi tệ hơn trong tương lai.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-1-1507864877357.jpg\" id=\"img_99fca920-afc5-11e7-94d9-7f221b28d2a0\" w=\"634\" h=\"463\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 1.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 1.\" rel=\"lightbox\" photoid=\"99fca920-afc5-11e7-94d9-7f221b28d2a0\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-1-1507864877357.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Huấn luyện viên Faisal của trung tâm Barry và Protein World (nước Anh).</p></div></div><p>Huấn luyện viên Faisal của trung tâm Barry và Protein World (nước Anh) đã đưa ra giải pháp tập luyệt toàn thân dành cho những người bận rộn mà thời gian chỉ kéo dài khoảng 10 phút (gồm cả thời gian nghỉ giải lao). Nếu bạn cũng thuộc nhóm này, không có lý do gì bạn bỏ qua <a class=\"seo-suggest-link\" href=\"http://afamily.vn/bai-tap-cho-nguoi-ban-ron.html\" target=\"_blank\" title=\"bài tập cho người bận rộn\"><b>bài tập cho người bận rộn</b></a> như dưới đây.</p><p><b>Các quy tắc tập luyện cơ bản bao gồm:</b></p><p>• Mỗi bài tập chỉ 45giây</p><p>• 10 động tác di chuyển</p><p>• 15 giây nghỉ giữa các bài</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/bai-tap-1o-phut-12-1507864985904.jpg\" id=\"img_d5e2fb10-afc5-11e7-8e2d-d3f798772c52\" w=\"2048\" h=\"1365\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 2.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 2.\" rel=\"lightbox\" photoid=\"d5e2fb10-afc5-11e7-8e2d-d3f798772c52\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-12-1507864985904.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Tất cả những động tác nên lặp đi lặp lại 2 đến 3 lần.</p></div></div><p><b>Những động tác nên tập luyện:</b></p><p>1. Nhảy squat (nhảy kiểu đứng lên ngồi xuống và sử dụng nhiều nhóm cơ khác nhau trên cơ thể như đùi, mông hông)</p><p>2. Lunge Reach (động tác tập luyện với thân dưới)</p><p>3. Ngồi xổm</p><p>4. Chống đẩy vặn hông</p><p>5. Trườn người</p><p>6. Drop Squats (tập chân tay)</p><p>7. Chống đẩy</p><p>8. Chống đẩy với tay</p><p>9. Leo núi</p><p>10. Động tác cao gối</p><p><i style=\"\">Tất cả những động tác nên lặp đi lặp lại 2 đến 3 lần.</i></p><p><i><b>Nhảy squat</b></i></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-2-1507864877355.gif\" id=\"img_9a31e9f0-afc5-11e7-955a-b919c2eefcf8\" w=\"815\" h=\"815\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 3.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 3.\" rel=\"lightbox\" photoid=\"9a31e9f0-afc5-11e7-955a-b919c2eefcf8\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-2-1507864877355.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Hai tay siết chặt để trước ngực, phần còn lại của cơ thể nhảy lên hạ xuống nhưng vẫn phải giữ nguyên tư thế ngồi xổm. Khi nhảy lên chân phải thẳng, hạ người xuống rồi tiếp tục bật người lên. Lưu ý giữ nguyên 1 vị trí cố định ban đầu.</p><p><b><i>Lunge Reach</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-3-1507864877353.gif\" id=\"img_9a4068e0-afc5-11e7-94d9-7f221b28d2a0\" w=\"815\" h=\"815\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 4.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 4.\" rel=\"lightbox\" photoid=\"9a4068e0-afc5-11e7-94d9-7f221b28d2a0\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-3-1507864877353.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Đan tay vào nhau giơ thẳng lên trên đầu. Chùn chân về phía trước, giữ chân ở góc 90 độ, bàn chân hướng theo phía đầu gối trước. Chân sau gập lại, mũi chân uốn cong. Khi chân bước về phía trước, cánh tay cũng nghiêng theo hướng chân (chân phải bước trước, tay phải cũng nghiêng theo). Lặp lại động tác tương tự với tay và chân bên kia.</p><p><b><i>Ngồi xổm</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-4-1507864877350.gif\" id=\"img_9a4e4b90-afc5-11e7-8d04-55975618b937\" w=\"700\" h=\"700\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 5.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 5.\" rel=\"lightbox\" photoid=\"9a4e4b90-afc5-11e7-8d04-55975618b937\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-4-1507864877350.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Đặt bàn tay đằng sau đầu, chân rộng bằng vai và hạ người xuống theo tư thế ngồi xổm, để trọng lượng cơ thể rơi vào gót chân chứ không phải ngón chân. Đứng lên và thực hiện lại động tác như ban đầu.</p><p><b><i>Chống đẩy vặn hông</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-5-1507864877349.gif\" id=\"img_9a60e930-afc5-11e7-94d9-7f221b28d2a0\" w=\"815\" h=\"815\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 6.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 6.\" rel=\"lightbox\" photoid=\"9a60e930-afc5-11e7-94d9-7f221b28d2a0\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-5-1507864877349.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Đặt 2 khuỷu tay xuống tấm thảm. Sau đó từ từ thả hông xuống, xoay hông sang một bên sao cho mé đùi chạm đất, rồi giữ thân trở về vị trí ban đầu, sau đó lại xoay hông sang bên đối diện với những động tác tương tự.</p><p><b><i>Trườn người</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-6-1507864877346.gif\" id=\"img_9a7e5c40-afc5-11e7-81d9-d137edec0f5b\" w=\"902\" h=\"507\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 7.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 7.\" rel=\"lightbox\" photoid=\"9a7e5c40-afc5-11e7-81d9-d137edec0f5b\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-6-1507864877346.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Đứng thẳng, cúi người gập xuống đầu gối và đặt tay chạm sàn trước mặt. Di chuyển tay lên dần phía trên thảm rồi quay lại vị trí lúc đầu. Khi tay di chuyển, lưng vẫn phải giữ thẳng và chân vẫn giữ ở vị trí ban đầu.</p><p><b><i>Drop Squat</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-7-1507864877344.gif\" id=\"img_9a936ae0-afc5-11e7-94d9-7f221b28d2a0\" w=\"815\" h=\"815\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 8.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 8.\" rel=\"lightbox\" photoid=\"9a936ae0-afc5-11e7-94d9-7f221b28d2a0\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-7-1507864877344.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Đứng thẳng rồi hạ người theo tư thế ngồi xổm và một tay chạm xuống sàn nhà giữa hai chân. Sau đó lại bật lên giữ thăng bằng rồi tiếp tục lặp lại với tay bên kia. Thực hiện động tác này nhanh và cố gắng thả lỏng tay khi chạm xuống sàn.</p><p><b><i>Chống đẩy</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-8-1507864877341.gif\" id=\"img_9aa40cb0-afc5-11e7-aaaa-51ac0faef9da\" w=\"600\" h=\"348\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 9.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 9.\" rel=\"lightbox\" photoid=\"9aa40cb0-afc5-11e7-aaaa-51ac0faef9da\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-8-1507864877341.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Úp mặt vào tấm thảm, cánh tay dang rộng ngang vai nâng người lên. Sau đó, thả khuỷu tay xuống sao cho ngực càng sát tấm thảm càng tốt. Tiếp đó lại nâng người lên và thực hiện lặp lại. Nếu cảm thấy khó, bạn có thể hạ đầu gối xuống thảm.</p><p><b><i>Chống đẩy với tay</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-9-1507864877339.gif\" id=\"img_9ac8abb0-afc5-11e7-aaaa-51ac0faef9da\" w=\"815\" h=\"815\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 10.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 10.\" rel=\"lightbox\" photoid=\"9ac8abb0-afc5-11e7-aaaa-51ac0faef9da\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-9-1507864877339.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Úp mặt xuống thảm, thả khuỷu tay và ngực chạm thảm, nâng một cánh tay thẳng về phía trước mặt, mắt chú ý nhìn theo tay. Thực hiện tương tự với cánh tay bên kia.</p><p><b><i>Leo núi</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto active noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-10-1507864877332.gif\" id=\"img_9a12f040-afc5-11e7-8d04-55975618b937\" w=\"673\" h=\"447\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 11.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 11.\" rel=\"lightbox\" photoid=\"9a12f040-afc5-11e7-8d04-55975618b937\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-10-1507864877332.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Cúi xuống, đặt 2 tay thẳng rộng ngang vai. Đưa đầu gối lên trước ngực rồi thả chân xuống thảm. Lặp lại với bên kia. Động tác thay đổi chân cần được thực hiện nhanh chóng.</p><p><b><i>Động tác cao gối</i></b></p><div class=\"VCSortableInPreviewMode GifPhoto active noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bai-tap-1o-phut-11-1507864877336.gif\" id=\"img_9a219640-afc5-11e7-94d9-7f221b28d2a0\" w=\"815\" h=\"815\" alt=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 12.\" title=\"10 phút tập thể dục dành cho những người bận rộn - không có lý do gì bạn không thử tập xem sao - Ảnh 12.\" rel=\"lightbox\" photoid=\"9a219640-afc5-11e7-94d9-7f221b28d2a0\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bai-tap-1o-phut-11-1507864877336.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Đứng thẳng. Nâng cao đầu gối lên càng gần ngực càng tốt rồi lại thả xuống và lặp lại với chân bên kia. Lặp lại càng nhanh càng tốt và cố gắng giữ đầu gối càng cao càng tốt. Để dễ dàng kiểm soát, bạn có thể đặt tay trước mặt như một điểm để đầu gối tiếp cận.</p><p style=\"text-align: right;\"><i>Theo DailyMail</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'C&#244;ng việc bận rộn, nhiều người tr&#236; ho&#224;n tập thể dục. V&#224; dưới đ&#226;y l&#224; giải ph&#225;p tập thể dục chỉ trong v&#242;ng 10 ph&#250;t d&#224;nh cho những người qu&#225; bận rộn. ', 'publish', 3, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL),
(4, ' Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa ', 'chuyen-gia-tiet-lo-su-that-ve-thuc-pham-chuc-nang-vitamin-tong-hop-khien-nhieu-nguoi-nga-ngua', 'http://afamilycdn.com/zoom/100_62/2017/p1-1507699217949-17-0-414-637-crop-1507699383337.jpg', 'Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa          <div data-check-position=\"af_detail_body_start\"></div>          <h2>Đổ xô mua <a class=\"seo-suggest-link\" href=\"http://afamily.vn/thuc-pham-chuc-nang.html\" target=\"_blank\" title=\"thực phẩm chức năng\">thực phẩm chức năng</a>, vitamin để uống bổ sung cho cơ thể</h2><p>Nhiều năm trở lại đây, việc bổ sung thực phẩm chức năng cũng như uống <a class=\"seo-suggest-link\" href=\"http://afamily.vn/vitamin-tong-hop.html\" target=\"_blank\" title=\"vitamin tổng hợp\">vitamin tổng hợp</a> đã trở thành trào lưu được nhiều chị em quan tâm. Theo đó, phụ nữ muốn bản thân cũng như gia đình mình có cơ thể khỏe mạnh, nhưng với nhịp sống hiện đại hối hả, thay vì nghĩ phải ăn uống như nào cho đủ chất thì chúng ta bổ sung thêm vitamin, thực phẩm chức năng cho cơ thể.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/p1-1507699217949.jpg\" id=\"img_df222c10-ae43-11e7-a065-7937920d296b\" w=\"637\" h=\"457\" alt=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 1.\" title=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 1.\" rel=\"lightbox\" photoid=\"df222c10-ae43-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/p1-1507699217949.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Nhiều năm trở lại đây, việc bổ sung thực phẩm chức năng cũng như uống vitamin tổng hợp đã trở thành trào lưu được nhiều chị em quan tâm.</p></div></div><p>Theo Wikipedia, thực phẩm chức năng (tiếng Anh: functional foods) là các sản phẩm có nguồn gốc tự nhiên hoặc là thực phẩm trong quá trình chế biến được bổ sung thêm các chất \"chức năng\".</p><p>Cũng như thực phẩm thuốc, thực phẩm chức năng nằm ở nơi giao thoa giữa thực phẩm và thuốc và người ta cũng gọi thực phẩm chức năng là thực phẩm thuốc. Sở dĩ thực phẩm chức năng có khả năng hỗ trợ điều trị bệnh và phòng ngừa bệnh vì nó có khả năng phục hồi tất cả các cấu trúc tế bào trong cơ thể đang bị tổn thương.</p><p>Khái niệm thực phẩm chức năng được người Nhật sử dụng đầu tiên trong những năm 1980 để chỉ những thực phẩm chế biến có chứa những thành phần tuy không có giá trị dinh dưỡng nhưng giúp nâng cao sức khoẻ cho người sử dụng.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/p2-1507699217950.jpg\" id=\"img_df48edf0-ae43-11e7-9564-854b3c797542\" w=\"660\" h=\"437\" alt=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 2.\" title=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 2.\" rel=\"lightbox\" photoid=\"df48edf0-ae43-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/p2-1507699217950.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Thực phẩm chức năng nằm ở nơi giao thoa giữa thực phẩm và thuốc và người ta cũng gọi thực phẩm chức năng là thực phẩm thuốc.</p></div></div><p>Theo Viện Khoa học và Đời sống quốc tế (International Life Science Institute - ILSI), \"thực phẩm chức năng là thực phẩm có lợi cho một hay nhiều hoạt động của cơ thể như cải thiện tình trạng sức khoẻ và làm giảm nguy cơ mắc bệnh hơn là so với giá trị dinh dưỡng mà nó mang lại\". Theo IFIC, thực phẩm chức năng là những thực phẩm hay thành phần của chế độ ăn có thể đem lại lợi ích cho sức khoẻ nhiều hơn giá trị dinh dưỡng cơ bản.</p><p>Bên cạnh đó, uống vitamin tổng hợp được khuyên dùng để giúp sức khỏe cường tráng hơn và tránh được bệnh tật. Vitamin tổng hợp sẽ được bào chế và sản xuất theo hình dạng viên nén, viên nang, chất lỏng, bột... nhằm bổ sung sinh dưỡng, cung cấp nhiều vitamin cho cơ thể. Hầu hết chúng được sử dụng nhằm tránh và chữa trị những bệnh liên quan đến thiếu hụt dinh dưỡng.&nbsp;</p><p>Ngoài ra, chúng còn mang lại nhiều lợi ích tới những người có chế độ ăn uống thiếu dinh dưỡng hoặc những người dễ mắc bệnh thoái hóa. Đặc biệt đối với ai đã lười ăn rau và trái cây thì việc uống vitamin tổng hợp chính là sự lựa chọn không thể tuyệt vời hơn.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/p3-1507699217952.jpg\" id=\"img_df5c75f0-ae43-11e7-9564-854b3c797542\" w=\"1280\" h=\"960\" alt=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 3.\" title=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 3.\" rel=\"lightbox\" photoid=\"df5c75f0-ae43-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/p3-1507699217952.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Đối với ai đã lười ăn rau và trái cây thì việc uống vitamin tổng hợp chính là sự lựa chọn không thể tuyệt vời hơn.</p></div></div><p>Với những lời quảng cáo như vậy, nhiều người đang hiểu lầm việc sử dụng thực phẩm chức năng hay viên uống vitamin tổng hợp là điều vô cùng cần thiết. Thậm chí nhiều người không chăm lo đến bữa ăn hàng ngày vì tin có thực phẩm chức năng hoặc vitamin tổng hợp, thế là tự tin có đủ nguồn dinh dưỡng để cơ thể khỏe mạnh. Ngược lại, nhiều người vì trào lưu thấy ai cũng dùng thực phẩm chức năng, uống vitamin tổng hợp, trong khi mình không dùng lại lo lắng có bệnh vào người. Vậy việc sử dụng thực phẩm chức năng, vitamin tổng hợp có thực sự cần thiết?</p><p><b>Thực phẩm chức năng, vitamin tổng hợp không cần thiết, bữa ăn cân đối mới là yếu tố quan trọng</b></p><p>Theo <a class=\"seo-suggest-link\" href=\"http://afamily.vn/ts-tu-ngu.html\" target=\"_blank\" title=\"TS Từ Ngữ \">TS Từ Ngữ</a> (Tổng thư ký Hội Dinh dưỡng Việt Nam), suy nghĩ phải uống thực phẩm chức năng, vitamin tổng hợp là điều không cần thiết. \"Cơ thể chúng ta hấp thụ chất hữu cơ, trong khi những loại thực phẩm chức năng, vitamin tổng hợp lại chứa nguồn chất vô cơ dồi dào. Do đó, thực sự không cần thiết để tiêu thụ những thực phẩm chức năng, vitamin tổng hợp\", TS Từ Ngữ khẳng định.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/p4-1507699217953.jpg\" id=\"img_df73a770-ae43-11e7-8a08-1bdf9c92da84\" w=\"640\" h=\"346\" alt=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 4.\" title=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 4.\" rel=\"lightbox\" photoid=\"df73a770-ae43-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/p4-1507699217953.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Theo TS Từ Ngữ (Tổng thư ký Hội Dinh dưỡng Việt Nam), suy nghĩ phải uống thực phẩm chức năng, vitamin tổng hợp là điều không cần thiết.</p></div></div><p>Theo TS Từ Ngữ, thực phẩm ăn hàng ngày chính là thuốc, ngược lại, thuốc chính là thực phẩm, thế nên mới có khái niệm thực phẩm chức năng. \"Trong thực phẩm đã có thuốc rồi. Nếu ăn uống cân đối thì bạn không cần thiết phải bổ sung thuốc bổ, không cần thiết dùng thực phẩm chức năng hay vitamin tổng hợp. Do đó, vấn đề của chúng ta là vấn đề bữa ăn với sức khỏe chứ không phải bổ sung thực phẩm chức năng hay viên uống vitamin tổng hợp. Không cần thiết dùng nếu bạn đang ăn uống đủ chất, ăn uống cân đối. Và nếu bạn đang không dùng thực phẩm chức năng hay vitamin tổng hợp cũng không phải lo ngại điều này sẽ khiến sức khỏe đi xuống khi ăn đủ chất\", TS Từ Ngữ nhấn mạnh.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/p5-1507699217955.jpg\" id=\"img_df85cfe0-ae43-11e7-9564-854b3c797542\" w=\"625\" h=\"350\" alt=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 5.\" title=\"Chuyên gia tiết lộ sự thật về thực phẩm chức năng, vitamin tổng hợp khiến nhiều người ngã ngửa - Ảnh 5.\" rel=\"lightbox\" photoid=\"df85cfe0-ae43-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/p5-1507699217955.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Một bữa ăn cân đối sẽ giúp bạn có đủ dinh dưỡng chống chọi với các vấn đề sức khỏe.</p></div></div><p>Theo chuyên gia, không có thực phẩm nào không tốt, chỉ là do cách ăn không đúng. Cái gì thuận theo tự nhiên sẽ tốt hơn cho cơ thể bạn. Nói như vậy có nghĩa là tăng cường sức khỏe bằng bữa ăn hàng ngày rõ ràng tốt hơn so với việc sử dụng thực phẩm chức năng hay vitamin tổng hợp.</p><p>Do đó, thay vì chăm chăm theo quảng cáo chọn thực phẩm chức năng này, chọn viên uống vitamin tổng hợp kia, bạn hãy chăm chút đến bữa ăn hàng ngày của mình cũng như gia đình mình hơn. Một bữa ăn cân đối bao gồm cơm (cung cấp tinh bột), canh (cung cấp vitamin và khoáng chất) và 2 món mặn (cung cấp lipaid, protein), trong đó có một món ăn có nguồn gốc đạm thực vật như đậu phụ. Hãy ăn uống cân đối như vậy, để không thành thảm họa đừng ăn quá nhiều thịt, không ăn quá nhiều dầu mỡ.</p>          <div data-check-position=\"af_detail_body_end\"></div>      ', 'Với những lời quảng c&#225;o như vậy, nhiều người cho rằng việc sử dụng thực phẩm chức năng hay vi&#234;n uống vitamin tổng hợp l&#224; điều v&#244; c&#249;ng cần thiết. ', 'publish', 4, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `description`, `status`, `pos`, `seo_title`, `seo_keyword`, `seo_description`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(5, ' Đừng sợ chất béo mà hãy ăn chúng như thế này thì cơ thể bạn sẽ \"biết ơn\" rất nhiều ', 'dung-so-chat-beo-ma-hay-an-chung-nhu-the-nay-thi-co-the-ban-se-biet-on-rat-nhieu', 'http://afamilycdn.com/zoom/100_62/2017/an-chat-beo-2-1507802695574-15-0-681-1067-crop-1507803240577.jpg', 'Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn          <div data-check-position=\"af_detail_body_start\"></div>          <p>Khi cộng tác với Women’s Health để viết cuốn sách \"The Women\'s Health Big Book Of Smoothies &amp; Soups\", chuyên gia dinh dưỡng Liza Defazio (đến từ Mỹ) đã quyết định tham gia vào các chiến dịch và đưa ra lời khuyên tốt nhất, giúp mọi người có được chế độ ăn uống đúng cách.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/an-chat-beo-1-1507802695575.jpg\" id=\"img_cf218490-af34-11e7-a047-73f8081241a6\" w=\"650\" h=\"413\" alt=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 1.\" title=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 1.\" rel=\"lightbox\" photoid=\"cf218490-af34-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/an-chat-beo-1-1507802695575.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Ăn uống cân bằng là yếu tố giúp bạn khỏe mạnh.</p></div></div><p>Qua những nghiên cứu thực tế nhằm cung cấp những điều có lợi nhất cho sức khỏe, bà Liza Defazio và các cộng sự đã đưa ra một số <a class=\"seo-suggest-link\" href=\"http://afamily.vn/loi-khuyen-ve-an-uong.html\" target=\"_blank\" title=\"lời khuyên về ăn uống \"><b>lời khuyên về ăn uống</b></a> với chất béo và đường.</p><h3><b>Đừng sợ chất béo</b></h3><p>Theo một cuộc khảo sát của Hội đồng Thông tin Thực phẩm Quốc tế, chỉ có 20% người nghĩ rằng tất cả các chất béo đều \"bình thường\" khi nói đến sức khoẻ, nhưng 67% cố gắng cắt giảm tất cả các chất béo khỏi chế độ ăn khi có thể. Đó là một suy nghĩ sai lầm, bởi bạn ăn bao nhiêu chất béo không thực sự ảnh hưởng đến trọng lượng hay nguy cơ bệnh tật của bạn. Vấn đề nằm ở loại chất béo độc hại và tổng lượng calo mà bạn dùng trong thực tế.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/an-chat-beo-7-1507802695568.jpg\" id=\"img_d02e39a0-af34-11e7-a047-73f8081241a6\" w=\"620\" h=\"400\" alt=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 2.\" title=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 2.\" rel=\"lightbox\" photoid=\"d02e39a0-af34-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/an-chat-beo-7-1507802695568.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Vấn đề nằm ở loại chất béo độc hại và tổng lượng calo mà bạn dùng trong thực tế.</p></div></div><p>Có 4 loại chất béo chung: Không bão hoà đa, không bão hoà đơn, bão hoà và chất béo chuyển vị (trans). Ngoại trừ chất béo chuyển vị (cần tránh), cơ thể bạn gần như cần tất cả 3 loại chất béo trên.</p><p>Chất béo là thành phần chính của mỗi tế bào trong cơ thể, nó giúp bạn hấp thụ các chất dinh dưỡng hoà tan từ thực phẩm, giữ cho da và mái tóc của bạn khoẻ mạnh, và làm cho bộ não hoạt động hiệu quả hơn. Một số loại chất béo không no và không bão hoà đơn cũng giúp bảo vệ cơ thể chống lại bệnh tật và kiểm soát sự viêm nhiễm.</p><p>Chất béo bão hoà làm tăng mức cholesterol và cũng làm tăng khả năng phát triển sức đề <a class=\"seo-suggest-link\" href=\"http://afamily.vn/khang-insulin.html\" target=\"_blank\" title=\"kháng insulin\">kháng insulin</a> (có thể dẫn đến bệnh tiểu đường), nhưng bạn vẫn cần một ít chúng trong chế độ ăn của mình. Cholesterol, chủ yếu xuất phát từ chất béo bão hoà, là một khối xây dựng quan trọng của các hormone trong cơ thể.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/an-chat-beo-5-1507802695570.jpg\" id=\"img_d00c32b0-af34-11e7-a047-73f8081241a6\" w=\"1024\" h=\"750\" alt=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 3.\" title=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 3.\" rel=\"lightbox\" photoid=\"d00c32b0-af34-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/an-chat-beo-5-1507802695570.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Chất béo là thành phần chính của mỗi tế bào trong cơ thể.</p></div></div><p>Cuối cùng, đây là một điểm quan trọng cần ghi nhớ: Chất béo làm cho thức ăn ngon hơn. Một mặt, nó có thể khiến bạn ăn quá nhiều, nhưng mặt khác, nó kích thích bạn ăn nhiều rau và thực phẩm lành mạnh khác mà bạn nên ăn trong chế độ ăn uống. Tôi chắc rằng ngay cả những người yêu rau quả nhất trên đời cũng phải thừa nhận rằng, một ít dầu oliu, pho mát, các loại hạt dẻ nướng, hay bơ đều khiến món ăn của họ thêm hương vị.</p><h3><b>Kết hợp carbs với protein hoặc chất béo</b></h3><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/an-chat-beo-2-1507802695574.jpg\" id=\"img_cf36ba40-af34-11e7-a065-7937920d296b\" w=\"1067\" h=\"800\" alt=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 4.\" title=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 4.\" rel=\"lightbox\" photoid=\"cf36ba40-af34-11e7-a065-7937920d296b\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/an-chat-beo-2-1507802695574.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Carb  là nguồn nhiên liệu thiết yếu của cơ thể.</p></div></div><p>Giảm cân, ăn uống lành mạnh không có nghĩa là phải loại bỏ carbs. Chúng là nguồn nhiên liệu thiết yếu, cũng là nguồn năng lượng mà chính cơ thể bạn \"yêu thích\". Ngoài ra, các loại thực phẩm chứa carbs như ngũ cốc nguyên hạt, trái cây, rau quả - có đầy đủ các vitamin khác, khoáng chất và chất phytochemicals, rất quan trọng cho sức khoẻ cơ thể. Chúng đồng thời chứa nhiều chất xơ, giúp cơ thể \"cảm thấy đầy đủ và hài lòng\".</p><p>Tuy nhiên, khi ăn carbohydrate, cơ thể chuyển đổi chúng thành glucose nhanh hơn nếu bạn ăn cùng loại thực phẩm làm chậm quá trình tiêu hoá (như protein hoặc chất béo).</p><p>Khi bạn chọn các thực phẩm chứ carbs, cần chú ý chọn các loại carb lành mạnh. Ví dụ như chọn các loại ngũ cốc nguyên hạt thay vì các loại bánh mì trắng, mì ống và gạo. Đó là những carbs tinh chế, như bột trắng và đường, chúng gần giống với đường hoá học, do đó nhanh chóng bị phân huỷ khi vào cơ thể.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/an-chat-beo-3-1507802695572.jpg\" id=\"img_cf4b7ac0-af34-11e7-a047-73f8081241a6\" w=\"1798\" h=\"977\" alt=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 5.\" title=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 5.\" rel=\"lightbox\" photoid=\"cf4b7ac0-af34-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/an-chat-beo-3-1507802695572.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Cơ thể chuyển đổi carbs thành glucose nhanh hơn nếu kết hợp với protein hay chất béo.</p></div></div><h3><b>Giảm đường trong chế độ ăn</b></h3><p>Bằng chứng cho thấy thêm đường trong bữa ăn không chỉ khiến bạn <a class=\"seo-suggest-link\" href=\"http://afamily.vn/tang-can.html\" target=\"_blank\" title=\"tăng cân\">tăng cân</a> mà còn tăng nguy cơ mắc bệnh tim, tiểu đường, ung thư, hình thành các nếp nhăn xấu xí…</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/an-chat-beo-6-1507802695569.jpg\" id=\"img_d01c5f50-af34-11e7-a047-73f8081241a6\" w=\"724\" h=\"362\" alt=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 6.\" title=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 6.\" rel=\"lightbox\" photoid=\"d01c5f50-af34-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/an-chat-beo-6-1507802695569.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Thêm đường trong bữa ăn không chỉ khiến bạn tăng cân mà còn tăng nguy cơ mắc bệnh tim.</p></div></div><p>Người bình thường trung bình ăn 22 muỗng cà phê, tương đương 88 gram đường mỗi ngày. Hiệp hội Tim Mạch Hoa Kỳ khuyến cáo nên ăn tối đa mỗi ngày chỉ 5 muỗng cà phê đối với phụ nữ và 6 muỗng  cà phê đối với nam, điều này nghĩa là nếu bạn uống một cốc soda là đã vượt quá giới hạn cần thiết.</p><p>Tuy nhiên, việc kiểm soát đường khá khó khăn. Đường có nhiều trong các sản phẩm chế biến và đóng gói, nó cũng có nhiều trong trái cây, một số loại rau, các sản phẩm từ sữa và ngũ cốc nguyên hạt. Lượng đường được liệt kê trên nhãn thực phẩm là sự kết hợp của cả đường tự nhiên và đường bổ sung. </p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/an-chat-beo-4-1507802695571.jpg\" id=\"img_cf6396a0-af34-11e7-a065-7937920d296b\" w=\"650\" h=\"470\" alt=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 7.\" title=\"Lời khuyên về ăn uống của chuyên gia dinh dưỡng giúp bạn ăn uống lành mạnh hơn - Ảnh 7.\" rel=\"lightbox\" photoid=\"cf6396a0-af34-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/an-chat-beo-4-1507802695571.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Nên tiêu thụ đường từ việc ăn các loại trái cây.</p></div></div><p>Đường có rất nhiều tên như xiro bắp, sucrose, đường của cái, mía… vì vậy hãy cảnh giác với những gì bạn sẽ sử dụng. Tốt nhất hãy để cơ thể hấp thụ đường một cách lành mạnh thông qua việc ăn trái cây, rau quả…</p><p style=\"text-align: right;\"><i>Theo WHM/Health</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Chuy&#234;n gia dinh dưỡng Liza Defazio v&#224; c&#225;c cộng sự đ&#227; đưa ra một số lời khuy&#234;n về ăn uống với chất b&#233;o v&#224; đường m&#224; nhất định bạn kh&#244;ng được bỏ qua. ', 'publish', 5, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL),
(6, ' Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng ', 'khi-an-dung-quen-lam-4-dieu-nay-thi-khong-bao-gio-phai-lo-day-hoi-chuong-bung', 'http://afamilycdn.com/zoom/100_62/2017/tot-cho-tieu-hoa-1-1507787133359-43-0-439-634-crop-1507787277967.jpg', 'Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng          <div data-check-position=\"af_detail_body_start\"></div>          <p>Bạn nghĩ rằng mình đã làm đúng mọi thứ, từ việc ăn salad lành mạnh để  cố gắng chống lại cám dỗ của thực phẩm đầy chất béo. Nhưng rồi đến khi gặp các biểu hiện như đầy bụng, <a class=\"seo-suggest-link\" href=\"http://afamily.vn/chuong-bung.html\" target=\"_blank\" title=\"chướng bụng\">chướng bụng</a>... thì chắc chắn bạn sẽ tin rằng mọi cố gắng của mình đã \"đổ sông đổ bể\" và khó tránh chuyện chất béo đã được \"chuyển\" tới bụng, đùi, mông.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-1-1507787133359.jpg\" id=\"img_924d81a0-af10-11e7-80e3-897aa0f332fe\" w=\"634\" h=\"509\" alt=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 1.\" title=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 1.\" rel=\"lightbox\" photoid=\"924d81a0-af10-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-1-1507787133359.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Chuyện tiêu hóa thức ăn không chỉ phụ thuộc vào những gì bạn ăn mà còn liên quan cả đến cách bạn ăn.</p></div></div><p>Thực tế, chuyện tiêu hóa thức ăn không chỉ phụ thuộc vào những gì bạn ăn mà còn liên quan cả đến cách bạn ăn. Bạn ăn như thế nào thì sẽ thúc đẩy các các enzym tiêu hóa trong cơ thể như thế đó và điều này quyết định chất lượng của chế độ ăn uống bạn đang theo.</p><p>Daily Mail Online đã nói chuyện với chuyên gia dinh dưỡng và nhà dinh dưỡng học Amy Shapiro, người sáng lập Nutrition NYC về những thay đổi trong chế độ ăn uống mà mọi người nên thực hiện để <a class=\"seo-suggest-link\" href=\"http://afamily.vn/tot-cho-tieu-hoa.html\" target=\"_blank\" title=\"tốt cho tiêu hóa\"><b>tốt cho tiêu hóa</b></a>.</p><h3><b>1. Tránh uống đồ uống lạnh trong khi ăn - thay vào đó hãy uống một tách trà</b></h3><p>Chuyên gia dinh dưỡng Shapiro khuyến cáo khách hàng nên tập trung vào thực phẩm thay vì đồ uống trong khi ăn. Bởi theo bà, uống trong khi ăn sẽ làm loãng các enzyme tiêu hóa.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/tot-cho-tieu-hoa-2-1507787133358.jpg\" id=\"img_9261ccf0-af10-11e7-80e3-897aa0f332fe\" w=\"1280\" h=\"720\" alt=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 2.\" title=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 2.\" rel=\"lightbox\" photoid=\"9261ccf0-af10-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-2-1507787133358.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Tránh uống đồ uống lạnh trong khi ăn - thay vào đó hãy uống một tách trà.</p></div></div><p>Các enzyme tiêu hóa được giải phóng trong nước bọt khi bạn bắt đầu bữa ăn. Nhưng nếu trộn với rất nhiều nước hoặc chất lỏng khác, thì các enzyme này sẽ không thể phá vỡ thức ăn trước khi nó vào dạ dày. Điều này có thể cản trở quá trình tiêu hóa và khiến cho bạn cảm thấy đầy bụng.</p><p>Ngoài ra, Shapiro khuyên khách hàng nên uống đồ uống mát hoặc ấm chứ không nên uống lạnh. \"Nước mát hoặc ấm <i>tốt cho tiêu hóa</i> hơn, giúp cơ thể dễ tiêu hóa vì cơ thể không mất thời gian làm cho chất lỏng nóng lên bằng nhiệt độ cơ thể\", cô nói.</p><p>Ngoài ra, nước ấm làm giảm chất thải chuyển hóa trong cơ thể (có thể tích tụ từ hệ thống miễn dịch), nhờ đó phá vỡ thức ăn nhanh hơn. Uống đồ uống ấm cũng có thể thúc đẩy nhu động ruột khỏe mạnh hơn và ngăn ngừa cảm giác cồng kềnh, chướng bụng suốt cả ngày.</p><h3><b>2. Tránh những thực phẩm cản trở quá trình tiêu hóa</b></h3><p>Cơ thể không thể dễ dàng phá vỡ một số loại thực phẩm nhất định trong quá trình tiêu hóa.  Shapiro cho biết cả lactose và cải xoăn là hai loại thực phẩm phổ biến mà nhiều khách hàng sau khi ăn đã cảm thấy rất khó tiêu.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-3-1507787133357.jpg\" id=\"img_9270e820-af10-11e7-a047-73f8081241a6\" w=\"640\" h=\"360\" alt=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 3.\" title=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 3.\" rel=\"lightbox\" photoid=\"9270e820-af10-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-3-1507787133357.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Cơ thể không thể dễ dàng phá vỡ một số loại thực phẩm nhất định trong quá trình tiêu hóa.</p></div></div><p>Cải xoăn chứa đầy chất xơ không hòa tan và một số loại đường nên có thể gây khó khăn cho hệ thống tiêu hóa nếu được tiêu thụ với số lượng lớn. Hệ thống tiêu hóa không thể tiêu hóa phần này của các loại thực phẩm thực vật nên khi vào cơ thể nó trở thành chất thải và cản trở hệ thống tiêu hóa. Nếu vẫn muốn ăn thực phẩm này, bạn nên hấp hoặc luộc chúng thì cơ thể sẽ dễ tiêu hóa hơn.</p><p>Lactose là loại đường chính được tìm thấy trong hầu hết các sản phẩm từ sữa. Một số người có thể gặp khó khăn khi tiêu hóa loại đường này nếu ruột non của họ không sản sinh lactase - enzyme tiêu thụ lactose.</p><h3><b>3. Biết nhai đúng cách</b></h3><p>\"Bước đầu tiên của tiêu hóa xảy ra trong miệng và cảm giác cồng kềnh trong bụng sau khi ăn có thể là dấu hiệu cho thấy bạn nhai thức ăn không hiệu quả\", Shapiro nói.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-4-1507787133360.jpg\" id=\"img_929ed5f0-af10-11e7-80e3-897aa0f332fe\" w=\"616\" h=\"410\" alt=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 4.\" title=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 4.\" rel=\"lightbox\" photoid=\"929ed5f0-af10-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-4-1507787133360.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Nhai thức ăn trong miệng quá nhanh khiến tăng gánh nặng cho dạ dày, không tốt cho tiêu hóa.</p></div></div><p>Ăn và nuốt quá nhanh có thể thường làm cho người ta cảm thấy chướng bụng sau bữa ăn. Đó là vì \"chúng ta có xu hướng nuốt không khí dư thừa trong quá trình ăn, từ đó có thể gây ra nôn và đầy khí trong bụng - chướng bụng\", Shapiro nói.</p><p>Nhai nhiều và ăn chậm hơn có thể ngăn ngừa điều này xảy ra khi cơ thể tiêu hóa thực phẩm. Điều này sẽ làm giảm bớt gánh nặng cho dạ dày để nghiền thức ăn và tối đa hóa lượng chất dinh dưỡng mà cơ thể nhận được từ mỗi món ăn. Nước bọt có chứa các enzyme tiêu hóa được sử dụng để giúp phá vỡ thực phẩm trước khi nó được chuyển xuống dạ dày. Nhưng quá trình này không xảy ra nếu bạn nhai thức ăn trong miệng quá nhanh.</p><h3><b>4. Thêm một số \"nhiên liệu tiêu hóa\" để quá trình tiêu hóa diễn ra tốt hơn</b></h3><p>Theo chuyên gia dinh dưỡng Shapiro, đu đủ và dứa là những thực phẩm rất giàu enzym tiêu hóa. Hai loại trái cây này giúp đỡ hệ thống tiêu hóa phá vỡ các chất dinh dưỡng trong thực phẩm khi chúng được đẩy qua đường tiêu hóa.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/tot-cho-tieu-hoa-5-1507787133356.jpg\" id=\"img_92ae8d60-af10-11e7-80e3-897aa0f332fe\" w=\"744\" h=\"561\" alt=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 5.\" title=\"Khi ăn, đừng quên làm 4 điều này thì không bao giờ phải lo đầy hơi, chướng bụng - Ảnh 5.\" rel=\"lightbox\" photoid=\"92ae8d60-af10-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/tot-cho-tieu-hoa-5-1507787133356.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Đu đủ và dứa là những thực phẩm rất giàu enzym tiêu hóa.</p></div></div><p>Bạc hà và gừng cũng có tác dụng tương tự như vậy. Không những thế, chúng còn có đặc tính chống co thắt, có nghĩa là chúng có tác dụng làm thư giãn hệ thống tiêu hóa và chống lại bất kì sự co thắt nào không cần thiết.</p><p>Chúng cũng hỗ trợ trong quá trình tiêu hóa bằng cách thúc đẩy sự phóng thích nước bọt, ép dạ dày và mật tiết dịch để phá vỡ các protein và các đặc tính khác của thực phẩm.</p><p>Cà rốt và cây thì cũng có tác dụng tương tự với bạc hà và gừng. Khi tiêu thụ, gan sẽ giải phóng mật, giúp tiêu hóa chất béo và chất dinh dưỡng khác trong dạ dày tốt hơn.</p><p>Bạn có thể ăn các loại trái cây như đu đủ và dứa trước bữa ăn để thúc đẩy quá trình tiêu hóa. CÒn gừng, bạc hà, cà rốt và thì là thì có thể thêm vào bữa ăn để đạt chất lượng ăn uống tốt hơn.</p><p style=\"text-align: right;\"><i>Theo DailyMail</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Chuy&#234;n gia dinh dưỡng chỉ ra những thay đổi trong chế độ ăn uống m&#224; mọi người n&#234;n thực hiện để tốt cho ti&#234;u h&#243;a, tr&#225;nh đầy hơi chướng bụng. ', 'publish', 6, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL),
(7, ' 9 cách giúp cơ thể \"giữ\" nước, chống mất nước để da dẻ luôn căng mọng ', '9-cach-giup-co-the-giu-nuoc-chong-mat-nuoc-de-da-de-luon-cang-mong', 'http://afamilycdn.com/zoom/100_62/2017/m5-1507778382851-0-0-1313-2101-crop-1507778763057.jpg', '9 cách giúp cơ thể \"giữ\" nước, chống mất nước để da dẻ luôn căng mọng          <div data-check-position=\"af_detail_body_start\"></div>          <p>Tiến sĩ Rachel Berman, một chuyên gia dinh dưỡng hàng đầu cho biết: \"Cơ thể chúng ta có hơn một nửa là nước, và chúng ta sử dụng nó cho mọi chức năng của cơ thể - từ việc điều chỉnh nhiệt độ cơ thể đến loại bỏ các chất thải, bôi trơn các khớp, vận chuyển oxy đến các tế bào… Đó là lý do tại sao bạn cảm thấy mệt mỏi, chóng mặt và buồn chán khi <a class=\"seo-suggest-link\" href=\"http://afamily.vn/co-the-bi-mat-nuoc.html\" target=\"_blank\" title=\"cơ thể bị mất nước\">cơ thể bị mất nước</a>\".<br></p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bo-sung-nuoc-3-1507779680830.jpg\" id=\"img_37b7dee0-aeff-11e7-ad77-9f411bebcc1b\" w=\"620\" h=\"400\" alt=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 1.\" title=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 1.\" rel=\"lightbox\" photoid=\"37b7dee0-aeff-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bo-sung-nuoc-3-1507779680830.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Nên ăn thêm súp, sữa chua và bột yến mạch - đều là những thực phẩm đáp ứng nhu cầu cấp nước cho cơ thể.</p></div></div><p>Mặc dù uống nước là cách tốt để cung cấp nước, nhưng ngoài việc uống nước như thông thường, bạn có thể bổ sung nó bằng những cách khác.  </p><p>Theo khuyến cáo của Viện y học, chúng ta nên ăn thêm súp, sữa chua và bột yến mạch - đều là những thực phẩm đáp ứng nhu cầu cấp nước cho cơ thể, chống mất nước, ngoài ra còn có những trái cây thân thiện trong mùa hè và rau cũng có thể giúp hydrat hóa.</p><p>Ngoài ra, bạn cũng có thể bổ sung một số loại thực phẩm chứa nhiều nước như dưới đây, giúp cơ thể chống mất nước hiệu quả:</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/m1-1507778382846.jpg\" id=\"img_2ff95330-aefc-11e7-94d9-7f221b28d2a0\" w=\"750\" h=\"500\" alt=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 2.\" title=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 2.\" rel=\"lightbox\" photoid=\"2ff95330-aefc-11e7-94d9-7f221b28d2a0\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/m1-1507778382846.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Mặc dù một ly nước đầy là cách tốt để cung cấp nước, nhưng ngoài việc uống nước như thông thường, bạn có thể bổ sung nó bằng nước trái cây như chanh và quả mâm xôi.</p></div></div><h2>1.	Dưa hấu</h2><p>Đây là loại trái cây có tới 92% là nước. Theo một nghiên cứu năm 2009 tại trường Đại học Aberdeen Medical School, muối, canxi và magiê cũng được chứng minh là những dưỡng chất lý tưởng cho việc bù nước. Dưa hấu chủ yếu có nhiều vào mùa hè, nó cũng là nguồn cung cấp kali, vitamin A và vitamin C dồi dào.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/m2-1507778382847.jpg\" id=\"img_300efe10-aefc-11e7-a065-7937920d296b\" w=\"600\" h=\"450\" alt=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 3.\" title=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 3.\" rel=\"lightbox\" photoid=\"300efe10-aefc-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/m2-1507778382847.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Dưa hấu là nguồn cung cấp kali, vitamin A và vitamin C dồi dào.</p></div></div><p><b>2.	Cần tây</b></p><p>Thực phẩm này thường được dùng để trang trí nhiều hơn là ăn. Tuy nhiên, cần tây lại chứa khoảng 95% nước, nhiều chất xơ và giàu chất khoáng bao gồm kali và vitamin K. Hãy nhớ rằng, \"chúng không giàu dinh dưỡng, nhưng lại rất tuyệt vời vì chúng không giàu calorie\", Berman nói.</p><p><b>3.	Dưa chuột</b></p><p>Với 96% nước, dưa chuột hiển nhiên có trong danh sách này. Không có chất béo no hay cholesterol, và rất giàu vitamin K, vitamin B6 và sắt, bạn thấy dưa chuột đã đủ tuyệt vời chưa?</p><p><b>4.	Dâu tây</b></p><p>Dâu tây là loại quả có hương vị cực kì tuyệt vời, với 92% nước (giống như hầu hết các loại quả mọng), và chứa chất xơ, vitamin C. Bạn có lẽ sẽ thích thú với một ly cocktail dâu tây vào mùa hè này đấy!</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/m3-1507778382848.jpg\" id=\"img_30234960-aefc-11e7-80e3-897aa0f332fe\" w=\"600\" h=\"450\" alt=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 4.\" title=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 4.\" rel=\"lightbox\" photoid=\"30234960-aefc-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/m3-1507778382848.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Dâu tây là loại quả có hương vị cực kì tuyệt vời, với 92% nước (giống như hầu hết các loại quả mọng), và chứa chất xơ, vitamin C.</p></div></div><p><b>5.	Xà lách</b></p><p>Xà lách chứa tới 96% là nước, nhưng nó lại không chứa nhiều dinh dưỡng. Kết hợp nó với salad, bánh sandwich và rau bina là đều là sự những lựa chọn tuyệt vời.</p><p><i><b>Không chỉ ăn, bạn nên uống để cấp đủ nước cho cơ thể</b></i></p><p>Hầu hết các đồ uống không chứa cồn sẽ giúp cơ thể nạp thêm lượng nước cần thiết. Dưới đây là 5 loại đồ uống không chỉ ngon còn vô cùng lợi ích với bạn.</p><p><b>1.	Sữa không chất béo</b></p><p>Mọi người đều biết sữa là nguồn cung cấp canxi tuyệt vời giúp xương vững chắc. Nhưng nghiên cứu cũng chỉ ra rằng sữa cấp nước còn tốt hơn cả nước và thức uống thể thao để hồi phục trước và sau khi tập thể dục.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/m4-1507778382850.jpg\" id=\"img_303a2cc0-aefc-11e7-8a08-1bdf9c92da84\" w=\"600\" h=\"382\" alt=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 5.\" title=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 5.\" rel=\"lightbox\" photoid=\"303a2cc0-aefc-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/m4-1507778382850.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Sữa cấp nước còn tốt hơn cả nước và thức uống thể thao để hồi phục trước và sau khi tập thể dục.</p></div></div><p><b>2.	Nước ép rau củ quả</b></p><p>Không tốn nhiều thời gian để làm lại thơm ngon, bổ dưỡng, các loại nước ép rau củ quả chắc hẳn là sự lựa chọn lý tưởng cho những bạn thích trái cây. </p><p><b>3.	Nước uống thể thao</b></p><p>Đường và natri là những chất có trong đồ uống thể thao. Ngoài ra, các chất điện giải và chất đạm có trong hầu hết các sản phẩm này, chúng giúp cơ thể bạn cân bằng nhanh hơn sau một thời gian dài tập luyện trong hơn 90 phút. </p><p>Đối với các bài tập ngắn hơn, bạn có thể không cần đến loại thức uống này, bởi chúng cung cấp khá nhiều carbs cho cơ thể. Để giảm lượng calo (và tiết kiệm được một ít tiền), bạn có thể tự pha chế đồ uống thể thao theo khẩu vị riêng tại nhà.</p><p><b>4.	Nước dừa</b></p><p>Không giống như nước giải khát thể thao, nước dừa chứa ít carbohydrate, trong khi vẫn giàu kali. Theo một nghiên cứu về Y khoa và Khoa học Thể Thao Thể dục, loại nước giải khát tự nhiên này có hiệu quả hơn trong việc tái tạo nước và chất cần thiết sau khi tập thể dục nhẹ.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/m5-1507778382851.jpg\" id=\"img_306cae70-aefc-11e7-955a-b919c2eefcf8\" w=\"2101\" h=\"1400\" alt=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 6.\" title=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 6.\" rel=\"lightbox\" photoid=\"306cae70-aefc-11e7-955a-b919c2eefcf8\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/m5-1507778382851.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Không giống như nước giải khát thể thao, nước dừa chứa ít carbohydrate, trong khi vẫn giàu kali.</p></div></div><p><b>Lưu ý:</b></p><p>Bạn hãy nhớ, mỗi ngày, cơ thể cần được cung cấp 20% nước từ các nguồn là thực phẩm. </p><p>Khi bạn tập thể dục, đừng quên cung cấp thêm nước cho cơ thể để đạt hiệu quả tối ưu nhất, Tiến sĩ Berman nói thêm. \"Lượng nước cơ thể cần thay đổi dựa trên trọng lượng của cơ thể và cường độ bạn làm việc, nhưng trường Đại học Y khoa về Thể Thao Hoa Kỳ đề nghị 500 – 700 ml nước ít nhất 4 giờ trước khi tập thể dục và 100 – 250 ml mỗi 15 phút trong khi luyện tập. Còn nếu bạn làm việc với cường độ cao, cứ mỗi 50ml bị mất, bạn phải bổ sung từ 500 – 750 ml\".</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bo-sung-nuoc-2-1507779680827.jpg\" id=\"img_379fc300-aeff-11e7-ad77-9f411bebcc1b\" w=\"620\" h=\"400\" alt=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 7.\" title=\"9 cách giúp cơ thể giữ nước, chống mất nước để da dẻ luôn căng mọng - Ảnh 7.\" rel=\"lightbox\" photoid=\"379fc300-aeff-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bo-sung-nuoc-2-1507779680827.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p style=\"text-align: right;\"><i>(Nguồn: Dailyburn)</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Bạn ho&#224;n to&#224;n c&#243; thể tiếp cận với một số loại thực phẩm quen thuộc gi&#224;u nước, gi&#250;p cơ thể chống mất nước hiệu quả. ', 'publish', 7, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL),
(8, ' Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con ', 'benh-tay-chan-mieng-vao-mua-cac-bac-cha-me-nhat-dinh-phai-biet-dieu-nay-de-phong-benh-cho-con', 'http://afamilycdn.com/zoom/100_62/2017/t2-1507695791599-30-0-480-719-crop-1507778817926.jpg', 'Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con          <div data-check-position=\"af_detail_body_start\"></div>          <p>Hiện nay, thời tiết thất thường là nguyên nhân của nhiều dịch bệnh phát triển, trong đó phải kể đến <a class=\"seo-suggest-link\" href=\"http://afamily.vn/benh-tay-chan-mieng.html\" target=\"_blank\" title=\"bệnh tay chân miệng\">bệnh tay chân miệng</a>. <br></p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/t1-1507695692824.jpg\" id=\"img_a8ce10f0-ae3b-11e7-ad77-9f411bebcc1b\" w=\"543\" h=\"414\" alt=\"Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con - Ảnh 1.\" title=\"Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con - Ảnh 1.\" rel=\"lightbox\" photoid=\"a8ce10f0-ae3b-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/t1-1507695692824.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Trong phòng bệnh cho con, các mẹ tuyệt đối không nên sử dụng các bài thuốc dân gian</p></div></div><p><b>Hà Nội ghi nhận 531 trường hợp mắc tay chân miệng</b></p><p>Theo thông báo của Bộ Y tế, từ đầu năm đến nay cả nước đã ghi nhận trên 70 nghìn trường hợp mắc tại 63/63 tỉnh/thành phố. Trong đó, theo báo cáo Sở Y tế Hà Nội trong tuần từ 2/10-8/10/2017 trên địa bàn Hà Nội ghi nhận 97 trường hợp mắc tay chân miệng. Lũy tích từ đầu năm đến nay Hà Nội ghi nhận 531 trường hợp, không có trường hợp tử vong</p><p>Theo PGS.TS Hoàng Đức Hạnh (Phó Giám đốc Sở Y tế Hà Nội) cho biết, bệnh tay chân miệng vẫn chưa có vắc-xin và thuốc điều trị đặc hiệu, cộng thêm vấn đề ô nhiễm môi trường, diễn biến khí hậu bất lợi và điều kiện vệ sinh cá nhân, vệ sinh môi trường tại một số nơi còn chưa đáp ứng được yêu cầu là nguyên nhân gây ra bệnh tay chân miệng.</p><p>\" Để <a class=\"seo-suggest-link\" href=\"http://afamily.vn/phong-chong-benh-tay-chan-mieng.html\" target=\"_blank\" title=\"phòng chống bệnh tay chân miệng\">phòng chống bệnh tay chân miệng</a> nên rửa tay cho trẻ thường xuyên bằng xà phòng dưới vòi nước chảy nhiều lần trong ngày; vệ sinh ăn uống; làm sạch đồ chơi, nơi sinh hoạt; sử dụng nhà tiêu hợp vệ sinh và theo dõi phát hiện sớm các vấn đề sức khỏe trẻ em để kịp thời phát hiện, tổ chức cách ly, điều trị các trường hợp mắc bệnh, tránh lây bệnh cho các trẻ khác. Trẻ bị bệnh phải được cách ly ít nhất là 10 ngày kể từ khi khởi bệnh, không cho trẻ có biểu hiện bệnh đến lớp và chơi với các trẻ khác\", PGS.TS Hoàng Đức Hạnh lưu ý.</p><p>Theo Ông Đặng Quang Tuấn - Phó Cục trưởng Cục Y tế dự phòng – Bộ Y tế từ tháng 3 đến tháng 5 và tháng 9 – tháng 12 là cao điểm của dịch tay chân miệng, đặc biệt thời điểm hiện nay khi các trường vừa khai giảng năm học  mới, nguy cơ lây lan bệnh tay chân miệng rất nhanh.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/dsc0020-1507695551971.jpg\" id=\"img_5523ea10-ae3b-11e7-9564-854b3c797542\" w=\"3216\" h=\"2136\" alt=\"Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con - Ảnh 2.\" title=\"Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con - Ảnh 2.\" rel=\"lightbox\" photoid=\"5523ea10-ae3b-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/dsc0020-1507695551971.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Trẻ rửa tay trước khi ăn và sau khi đi vệ sinh phòng bệnh tay chân miệng.</p></div></div><p><b>Không nên chữa bệnh tay chân miệng theo kiểu truyền miệng</b></p><p>Hiện nay, trên một số diễn đàn trên mạng xã hội chia sẻ  phương pháp điều trị bệnh tay chân miệng bằng việc dùng lá bàng. Theo các mẹ bỉm sữa truyền tai nhau, phương thuốc này khá tốt chỉ cần dùng 7 – 10 lá bàng bánh tẻ, 1 lít nước, 2 thìa ăn cơm muối hạt xay nát, đun sôi nhỏ lửa khoảng 5 – 7 phút để nguội, lọc lấy nước cho vào chai để tủ lạnh dùng dần. Mỗi lần lấy ra dùng nên làm cho nóng lên. Dùng cho bé súc miệng chữa các bệnh nói trên hoặc dùng khăn thấm nước lá bàng đó lau trong mồm. Với các vết lở loét cho chân tay miệng thì lau bằng nước lá bàng 3-4 lần một ngày, sau đó bôi dầu dừa. Chỉ 3 ngày là khỏi.</p><p>Tuy nhiên, theo PGS.TS Nguyễn Tiến Dũng (Nguyên Trưởng khoa nhi, Bệnh viện Bạch Mai) phân tích, bệnh tay chân miệng tác nhân gây bệnh tay chân miệng thường gặp là Coxsackievirus A16 và Enterovirus 71 (EV71). Virus lây lan rất nhanh qua đường miệng, qua các chất tiết mũi miệng, phân hay bọt nước từ trẻ bệnh sang trẻ lành. Virus xâm nhập vào cơ thể qua niêm mạc miệng hay ruột vào hệ thống hạch bạch huyết và từ đó sẽ phát triễn rất nhanh và gây ra các tổn thương ở da và niêm mạc.</p><p>Biểu hiện bệnh tay chân miệng thường thấy ở trẻ nhỏ số, đau họng, mệt mỏi... Một hoặc hai ngày sau khi bắt đầu sốt, những vết lở loét có thể xuất hiện trong miệng. và nổi dạng nốt bọng nước long ở tay và chân, có thể cả trên đầu gối, khuỷu tay và mông bé. Phát ban này có thể có mủ nhưng thường sẽ không bị ngứa. Một số trẻ chỉ phát ban, có những trẻ không có triệu chứng nào đáng kể, nhưng vẫn có thể truyền bệnh cho người khác.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/t2-1507695791599.jpg\" id=\"img_e3b2f870-ae3b-11e7-84cf-011ebaba2567\" w=\"720\" h=\"480\" alt=\"Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con - Ảnh 3.\" title=\"Bệnh tay chân miệng vào mùa, các bậc cha mẹ nhất định phải biết điều này để phòng bệnh cho con - Ảnh 3.\" rel=\"lightbox\" photoid=\"e3b2f870-ae3b-11e7-84cf-011ebaba2567\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/t2-1507695791599.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Biểu hiện bệnh tay chân miệng thường thấy ở trẻ nhỏ số, đau họng, mệt mỏi...</p></div></div><p>\"Vì vậy, việc dùng lá bàng để điều trị bệnh tay chân miệng hoàn toàn không đúng. Khi trẻ bị tay chân miệng các mẹ không nên dùng các biện pháp chữa bệnh theo kiểu truyền miệng hay các loại lá gì khi chưa có sở khoa học\", PGS.TS Nguyễn Tiên Dũng cho biết.</p><p>Theo PGS.TS Nguyễn Tiến Dũng, biến chứng thường gặp của bệnh tay  chân  miệng là: Viêm màng não,  liệt mềm cấp, viêm cơ tim, phù phổi cấp do thần kinh. Các biến chứng có thể phối hợp với nhau như viêm não màng não, phù phổi và Nguy hiểm hơn khi trẻ có biến chứng não thường không hôn mê sâu mà có những triệu chứng khó nhận biết như khó ngủ, quấy khóc liên tục, giật mình lúc thức hay lúc bắt đầu thiu thiu ngủ, trẻ có thể biểu hiện hốt hoảng, nói lảm nhảm, run chân tay, co giật… Khi trẻ có biến chứng nếu không điều trị đúng và kịp thời có thể tử vong trong vài giờ.</p><p>Trẻ bị bệnh tay chân miệng thường rất biếng ăn, thậm chí có thể bỏ ăn do các vết loét trong niêm mạc miệng gây đau. Vì vậy, thức ăn cho trẻ cần chọn lựa sao cho mềm, mịn, mát lạnh nhằm tạo cảm giác dễ chịu khi thức ăn, chia các bữa ăn cho ra thành nhiều bữa… để tăng cường sức đề kháng cho trẻ.</p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Bệnh tay ch&#226;n miệng hiện chưa c&#243; vắc xin điều trị. V&#236; vậy, để ph&#242;ng bệnh cho con, c&#225;c mẹ tuyệt đối kh&#244;ng n&#234;n sử dụng c&#225;c b&#224;i thuốc chưa c&#243; cơ sở khoa học kiểm chứng. ', 'publish', 8, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `description`, `status`, `pos`, `seo_title`, `seo_keyword`, `seo_description`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(9, ' Không khuyến khích bạn nhịn ăn nhưng nếu nhịn ăn đúng cách thì sẽ vừa giảm cân vừa lợi cho sức khỏe ', 'khong-khuyen-khich-ban-nhin-an-nhung-neu-nhin-an-dung-cach-thi-se-vua-giam-can-vua-loi-cho-suc-khoe', 'http://afamilycdn.com/zoom/100_62/2017/nhin-an-gian-doan-2-1507713558259-12-0-637-1000-crop-1507713682513.jpg', 'Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết          <div data-check-position=\"af_detail_body_start\"></div>          <p>Nói một cách đơn giản, \"<a class=\"seo-suggest-link\" href=\"http://afamily.vn/nhin-an-gian-doan.html\" target=\"_blank\" title=\"nhịn ăn gián đoạn\"><i>nhịn ăn gián đoạn</i></a>\" là một phương pháp phổ biến để hạn chế lượng calorie giúp bạn giảm cân, do đó sẽ ngăn ngừa được một số căn bệnh và giúp bạn sống lâu, khỏe mạnh hơn.<br></p><p>\"<b>Nhịn ăn gián đoạn</b>\" là một thuật ngữ ám chỉ nhiều <a class=\"seo-suggest-link\" href=\"http://afamily.vn/che-do-an-kieng.html\" target=\"_blank\" title=\"chế độ ăn kiêng\">chế độ ăn kiêng</a> khác nhau, trong đó lượng calo bị hạn chế một phần hoặc là toàn bộ, trong 1 ngày hay 1 vài ngày...</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/nhin-an-gian-doan-2-1507713558259.jpg\" id=\"img_45b18040-ae65-11e7-8dbb-355ae774403f\" w=\"1000\" h=\"667\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 1.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 1.\" rel=\"lightbox\" photoid=\"45b18040-ae65-11e7-8dbb-355ae774403f\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-2-1507713558259.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><b>Đây là ba chế độ nhịn ăn gián đoạn phổ biến nhất được nhiều người ưa chuộng:</b></p><p><i>- Chế độ ăn 16/8:</i> Theo cách này, bạn sẽ bỏ qua bữa sáng và hạn chế ăn uống (ăn ít) trong 8 giờ. Sau đó, nhịn ăn trong 16 giờ.</p><p><i>- Chế độ ăn Eat-Stop-Eat: </i>Phương pháp này đòi hỏi phải nhịn ăn 24 giờ, 1-2 ngày mỗi tuần, không nhất thiết phải là 2 ngày liền nhau.</p><p><i>- Chế độ ăn 5:2: </i>Theo cách này, bạn chỉ ăn từ 500-600 calo trong 2 ngày không liên tục trong tuần. Trong 5 ngày khác, bạn ăn bình thường.</p><p><i style=\"\">Lưu ý:</i> Việc nhịn ăn có thể không phù hợp với phụ nữ mang thai và cho con bú. Thêm vào đó, những người mắc bệnh tiểu đường hoặc các bệnh mạn tính khác nên tham khảo ý kiến ​​bác sĩ trước khi bắt đầu bất kì chế độ ăn kiêng nào.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/nhin-an-gian-doan-1-1507713558277.jpg\" id=\"img_45a04230-ae65-11e7-955a-b919c2eefcf8\" w=\"600\" h=\"150\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 2.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 2.\" rel=\"lightbox\" photoid=\"45a04230-ae65-11e7-955a-b919c2eefcf8\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-1-1507713558277.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><h3><b>Một số lợi ích của việc <a class=\"seo-suggest-link\" href=\"http://afamily.vn/nhin-an-khong-lien-tuc.html\" target=\"_blank\" title=\"nhịn ăn không liên tục\">nhịn ăn không liên tục</a></b></h3><p><i>1. Giúp bạn giảm cân và giảm béo bụng</i><br></p><p>Nhờ đó, nhịn ăn gián đoạn có thể giảm béo bụng tốt hơn. Chất béo có hại ở vùng bụng là yếu tố nguy cơ cho nhiều bệnh như tiểu đường, cholesterol cao và các vấn đề về tim.<br></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/nhin-an-gian-doan-3-1507713558262.jpg\" id=\"img_45c1fb00-ae65-11e7-94d9-7f221b28d2a0\" w=\"600\" h=\"400\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 3.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 3.\" rel=\"lightbox\" photoid=\"45c1fb00-ae65-11e7-94d9-7f221b28d2a0\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-3-1507713558262.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Một nghiên cứu năm 2011 công bố trong tạp chí Obesity Reviews báo cáo rằng việc <b>nhịn ăn không liên tục</b> làm giảm sự mất cơ so với việc liên tục hạn chế calorie trong một khoảng thời gian. Kiểu ăn kiêng này cũng có hiệu quả trong việc giảm cân và trọng lượng cơ thể.</p><p>Một nghiên cứu năm 2015 được công bố trên Tạp chí Quốc tế về Bệnh béo phì (International Journal of Obesity) báo cáo rằng việc nhịn ăn luân phiên có thể là một lựa chọn để đạt được sự giảm cân và duy trì cân nặng. Tuy nhiên, bạn cần phải cảm thấy ngon miệng, vui vẻ và vẫn phải đáp ứng các yêu cầu dinh dưỡng tối thiểu khi áp dụng cách ăn uống này.</p><p><i>2. Giảm nguy cơ tim mạch</i></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/nhin-an-gian-doan-4-1507793820269.jpg\" id=\"img_23642770-af20-11e7-8a08-1bdf9c92da84\" w=\"600\" h=\"400\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 4.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 4.\" rel=\"lightbox\" photoid=\"23642770-af20-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-4-1507793820269.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Việc nhịn ăn gián đoạn có tác dụng cải thiện các yếu tố nguy cơ khác nhau góp phần vào bệnh tim vì nó giúp giảm huyết áp, cholesterol \"xấu\" và mức đường trong máu. Nó cũng giúp làm giảm các dấu hiệu viêm liên quan đến bệnh tim mạch.</p><p>Một nghiên cứu năm 2014 xuất bản trong Tạp chí Quốc tế về Khoa học Y tế (International Journal of Health Sciences) ghi nhận rằng việc nhịn ăn không liên tục có thể có hiệu quả <a class=\"seo-suggest-link\" href=\"http://afamily.vn/bao-ve-tim.html\" target=\"_blank\" title=\"bảo vệ tim\">bảo vệ tim</a>.</p><p><i>3. Sửa chữa các tế bào hỏng</i><br></p><p>Các tế bào có thể bị hư hại do stress oxy hóa. Điều này có thể đẩy nhanh <a class=\"seo-suggest-link\" href=\"http://afamily.vn/qua-trinh-lao-hoa.html\" target=\"_blank\" title=\"quá trình lão hóa\">quá trình lão hóa</a> và gây ra nhiều vấn đề về sức khỏe. Nhịn ăn gián đoạn là một cách an toàn để ngăn ngừa hoặc sửa chữa tế bào bị thiệt hại do stress oxy hóa.<br></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/nhin-an-gian-doan-5-1507713558268.jpg\" id=\"img_45e169e0-ae65-11e7-8e2d-d3f798772c52\" w=\"600\" h=\"400\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 5.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 5.\" rel=\"lightbox\" photoid=\"45e169e0-ae65-11e7-8e2d-d3f798772c52\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-5-1507713558268.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Kết quả của việc nhịn ăn là sự tăng trưởng nhỏ trong sản xuất gốc tự do ngay từ khi nhịn ăn. Các tế bào phản ứng bằng cách tăng mức độ các chất chống oxy hóa tự nhiên để chống lại thiệt hại tự do trong tương lai. Điều này kích hoạt tế bào để đối phó tốt hơn với các căng thẳng oxy hóa nghiêm trọng hơn trong tương lai.</p><p>Kiểu ăn kiêng này thậm chí còn giúp bạn sống lâu hơn bằng cách thay đổi biểu hiện gen.</p><p>Một nghiên cứu năm 2006 được công bố trên tạp chí Nghiên cứu Người cao tuổi (Ageing Research Reviews) báo cáo rằng việc hạn chế calori thông qua việc nhịn ăn không liên tục kích thích sự sản sinh chất đạm, các yếu tố thần kinh và các enzyme chống oxy hóa, tất cả đều giúp các tế bào đối phó với stress và chống lại bệnh tật.</p><p><i>4. Giảm nguy cơ bị bệnh tiểu đường</i></p><p>Bệnh tiểu đường là một trong những tình trạng rối loạn phổ biến nhất hiện nay mà mọi người đang hết sức chú ý để ngăn ngừa. Mức đường huyết không kiểm soát được là một vấn đề nghiêm trọng có thể ảnh hưởng đến nhiều cơ quan của cơ thể, bao gồm mắt, tim, thận...</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/nhin-an-gian-doan-6-1507713558270.jpg\" id=\"img_45ef2580-ae65-11e7-94d9-7f221b28d2a0\" w=\"600\" h=\"400\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 6.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 6.\" rel=\"lightbox\" photoid=\"45ef2580-ae65-11e7-94d9-7f221b28d2a0\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-6-1507713558270.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Một nghiên cứu năm 2007 xuất bản trong FEBS Letters cho thấy rằng việc nhịn ăn không liên tục cải thiện đáng kể các thông số sinh hóa liên quan đến sự phát triển của bệnh tiểu đường.</p><p>Một nghiên cứu năm 2014 xuất bản trong các báo cáo nghiên cứu của Translational Research rằng việc nhịn ăn không liên tục có thể làm giảm cân và giảm nguy cơ tiểu đường tuýp 2 ở những người thừa cân và béo phì. Tuy nhiên, kết luận này vẫn cần được nghiên cứu nhiều hơn nữa trước khi có thể kết luận chắc chắn.</p><p><i>5. Chống viêm</i></p><p>Viêm là phản ứng miễn dịch giúp loại bỏ các kích thích có hại, bao gồm tế bào bị hư hỏng hoặc mầm bệnh. Nhưng khi tình trạng viêm mất kiểm soát, nó có thể dẫn đến nhiều vấn đề sức khỏe, bao gồm hen, viêm xoang, xơ vữa động mạch, bệnh celiac, sốt cao, viêm ruột, loét đại tràng, viêm khớp dạng thấp, viêm bàng quang kẽ và thậm chí là ung thư.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/nhin-an-gian-doan-7-1507713558273.jpg\" id=\"img_45fdcb80-ae65-11e7-8dbb-355ae774403f\" w=\"600\" h=\"400\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 7.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 7.\" rel=\"lightbox\" photoid=\"45fdcb80-ae65-11e7-8dbb-355ae774403f\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-7-1507713558273.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Một nghiên cứu năm 2007 được công bố trên tạp chí Biology &amp; Medicine cho biết rằng hạn chế calo có thể cải thiện các kết quả lâm sàng và làm giảm các dấu hiệu stress oxy hóa và viêm ở người lớn thừa cân với bệnh hen suyễn vừa phải. Nghiên cứu này cho thấy rằng việc nhịn ăn là một cách tiếp cận mới đối với sự can thiệp điều trị trong điều trị hen.</p><p>Một nghiên cứu khác được công bố trong Nghiên cứu Dinh dưỡng năm 2012 báo cáo rằng việc nhịn ăn trong thời Ramadan làm giảm các cytokine viêm tế bào và các tế bào miễn dịch ở những người khỏe mạnh. Ramadan là một tháng tôn giáo trong đó người Hồi giáo không ăn hoặc uống trong những giờ ban ngày.</p><p><i>6. Tăng cường hormone tăng trưởng của con người (HGH)</i></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/nhin-an-gian-doan-8-1507793820271.jpg\" id=\"img_237c1c40-af20-11e7-a065-7937920d296b\" w=\"600\" h=\"400\" alt=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 8.\" title=\"Nhịn ăn gián đoạn và những lợi ích cho sức khỏe không phải ai cũng biết - Ảnh 8.\" rel=\"lightbox\" photoid=\"237c1c40-af20-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/nhin-an-gian-doan-8-1507793820271.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>HGH được sản xuất tự nhiên trong tuyến yên. Hormone đóng một vai trò quan trọng trong việc tái tạo tế bào, tăng trưởng và duy trì mô lành mạnh trong não và các cơ quan quan trọng khác. HGH cũng làm cho tóc và móng của bạn phát triển nhanh hơn.</p><p>Trên thực tế, giảm hormone tăng trưởng chính là nguyên nhân làm cho da mỏng đi ở tuổi già.</p><p>Một nghiên cứu năm 1988 được công bố trên Tạp chí Clinical Investigation báo cáo rằng nhịn ăn tăng cường bài tiết hormone tăng trưởng và làm tăng nhịp điệu phức tạp của tiết hormone tăng trưởng ở nam giới.</p><p>Một nghiên cứu năm 2011 của các nhà nghiên cứu tim tại Trung tâm Tim mạch Trung tâm Y tế Intermountain cho biết rằng việc nhịn ăn định kì là tốt cho sức khỏe và trái tim của bạn. Nó có thể làm giảm các yếu tố nguy cơ tim, như triglyceride, thừa cân nặng và lượng đường trong máu. Nghiên cứu cũng khẳng định rằng kiêng ăn có ảnh hưởng tích cực đến HGH. Trong suốt thời gian nhịn ăn 24 giờ, HGH đã tăng trung bình 1.300% ở phụ nữ và gần 2.000% ở nam giới.</p><p style=\"text-align: right;\"><i>Theo Top10homeRes/Sciendirect/Ncbi</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Thực h&#224;nh &quot;nhịn ăn gi&#225;n đoạn&quot; (nhịn ăn kh&#244;ng li&#234;n tục) đ&#227; trở n&#234;n rất phổ biến trong thời gian gần đ&#226;y, v&#236; n&#243; gi&#250;p mọi người c&#243; cuộc sống l&#226;u d&#224;i v&#224; khỏe mạnh hơn. ', 'publish', 9, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:32', '2017-10-13 14:44:32', NULL),
(10, ' Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong ', 'chi-1-nam-sau-khi-dat-que-cay-tranh-thai-co-gai-tre-bi-tang-ap-luc-trong-nao-nhin-mo-va-suyt-tu-vong', 'http://afamilycdn.com/zoom/100_62/2017/dat-que-cay-tranh-thai-7-1507700276617-0-0-396-634-crop-1507700477830.jpg', 'Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong          <div data-check-position=\"af_detail_body_start\"></div>          <p>Sau 1 năm đặt <a class=\"seo-suggest-link\" href=\"http://afamily.vn/que-cay-tranh-thai.html\" target=\"_blank\" title=\"que cấy tránh thai\">que cấy tránh thai</a>, Tasha Hughes, 20 tuổi, sống tại Walsall, đã bị một lượng chất lỏng tăng lên trên não, có thể gây ra tử vong.<br></p><p>Trước đó, cô có gặp những cơn nhức đầu đến tê liệt nhưng thường xuyên bị bác sĩ bỏ qua. Thậm chí họ cũng không chú ý đến triệu chứng <a class=\"seo-suggest-link\" href=\"http://afamily.vn/suy-giam-thi-luc.html\" target=\"_blank\" title=\"suy giảm thị lực\">suy giảm thị lực</a> của cô. Chỉ cho tới khi đến gặp bác sĩ nhãn khoa cô mới được cứu.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-1-1507700276624.jpg\" id=\"img_57e26cd0-ae46-11e7-a065-7937920d296b\" w=\"634\" h=\"423\" alt=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 1.\" title=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 1.\" rel=\"lightbox\" photoid=\"57e26cd0-ae46-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-1-1507700276624.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\"></p><p>Sau 1 năm đặt que cấy tránh thai, Tasha Hughes, bị một lượng chất lỏng tăng lên trên não.</p><p></p></div></div><p><b>\"Nó gần như đã hủy hoại cuộc sống của tôi\"</b></p><p>Tasha chia sẻ: \"Thật là kinh khủng khi nghĩ rằng que cấy đó đã gần như làm tôi mất thị lực và suýt giết chết tôi. Mặc dù tôi vẫn tiếp tục quay trở lại với các bác sĩ phàn nàn về những cơn đau đầu nhưng họ lại không nghiêm túc kiểm tra cho tôi. Cuối cùng, khi đến gặp bác sĩ nhãn khoa thì mọi người mới nhận ra tình trạng của tôi nghiêm trọng tới mức nào. Tôi như hóa đá, tầm nhìn của tôi trở nên mờ đến nỗi mẹ tôi phải giúp tôi đi vệ sinh, và nếu chất lỏng không bị thoát ra khỏi não thì có thể tôi đã chết.</p><p>Sau cuộc giải phẫu, tôi vẫn còn đau đớn, và mặc dù nó đã cứu vãn thị lực nhưng tôi vẫn không thể nhìn xa. Vết sẹo trên đầu khiến tôi cảm thấy mình như Frankenstein, và tôi không dám ra khỏi nhà vì cảm thấy quá tự ti\".</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-2-1507700276623.jpg\" id=\"img_57f77b70-ae46-11e7-a065-7937920d296b\" w=\"634\" h=\"951\" alt=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 2.\" title=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 2.\" rel=\"lightbox\" photoid=\"57f77b70-ae46-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-2-1507700276623.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Cô đã phải vào phòng cấp cứu ngay sau khi được bác sĩ nhãn khoa đưa tới viện.</p></div></div><p>\"Tôi không nghĩ mình sẽ đặt que cấy tránh thai lần thứ 2 vì tôi không bao giờ biết hết những phản ứng phụ nghiêm trọng đến mức nào, nhưng bằng cách chia sẻ câu chuyện của tôi, hy vọng sẽ cảnh báo những người khác về những gì có thể xảy ra\", Tasha nói thêm.</p><p><b>Tại sao Tasha lựa chọn đặt que cấy tránh thai?</b></p><p>Tasha Hughes đã quyết định đặt que cấy tránh thai sau khi nhận thấy uống thuốc làm cho cân nặng của cô tăng lên nhanh chóng. Trong một năm, cô nghĩ rằng việc đặt que cấy tránh thai có hiệu quả tốt nhưng không ngờ sau đó cô lại bị đau đầu nghiêm trọng.</p><p>Khi đó, Tasha mới lên đại học, những cơn đau đầu trầm trọng đến mức cô không thể tập trung. Những người bạn của cô cho rằng cô đã làm quá lên. Sau hai tuần đau đầu nghiêm trọng, mẹ cô, bà Katina, 40 tuổi, đưa cô đến bệnh viện gặp bác sĩ sản phụ khoa 8 lần nhưng các bác sĩ đều cho rằng đó là do sự mất cân bằng hormone.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-4-1507700276620.jpg\" id=\"img_5853cc40-ae46-11e7-aaaa-51ac0faef9da\" w=\"634\" h=\"423\" alt=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 3.\" title=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 3.\" rel=\"lightbox\" photoid=\"5853cc40-ae46-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-4-1507700276620.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Tasha lựa chọn cấy que tránh thai vì thuốc uống tránh thai khiến cô tăng cân.</p></div></div><p><b>Tới gặp bác sĩ nhãn khoa</b></p><p>Trong tuyệt vọng, Tasha Hughes đã đến gặp bác sĩ nhãn khoa vì thấy thị lực của mình giảm. Và chính bác sĩ này đã nhận ra rằng thần kinh thị giác của cô bị tổn hại và chuyển cô đến bệnh viện để điều trị.</p><p>Tasha nói: \"Nhức đầu kéo dài 2 tuần, vậy là tôi biết có điều gì đó nghiêm trọng đang xảy ra vì tôi chưa bao giờ gặp điều tương tự như vậy trước đây. Tôi đã đi khám nhiều lần nhưng đều bị từ chối và đến gặp bác sĩ nhãn khoa là lựa chọn duy nhất tôi\".</p><p>Bác sĩ nhãn khoa của Tasha đã đưa cô tới bệnh viện vì dây thần kinh thị giác của cô đã bị hư hại đến mức cô có thể bị mù trong vòng vài tuần tới. Tại bệnh viện, cô được chụp CT và kết quả rõ ràng hơn. Nhưng cũng từ đó mà các bác sĩ phát hiện ra cô bị <a class=\"seo-suggest-link\" href=\"http://afamily.vn/tang-huyet-ap-trong-so.html\" target=\"_blank\" title=\"tăng huyết áp trong sọ\">tăng huyết áp trong sọ</a> - trường hợp bệnh chỉ chiếm 1/100.000 người. Theo các bác sĩ, nguyên nhân dẫn tới tình trạng này có thể là do các hormone giải phóng ra bởi chất tránh thai. Bác sĩ cảnh báo cô có thể sẽ bị mù trong vài tuần tới nếu không phát hiện kịp thời.</p><p>Ngay sau đó, Tasha Hughes được điều trị cấp cứu. Các bác sĩ phẫu thuật cắt lát hộp sọ của cô và chèn một ống dẫn để đưa chất lỏng ra khỏi não, giảm mức độ nguy hiểm tính mạng. Cho tới nay đã được 2 năm, Tasha vẫn đang phải chịu đựng những ảnh hưởng này. Cô chia sẻ câu chuyện của mình với mục đích cảnh báo những phụ nữ khác về phản ứng phụ bất ngờ của <a class=\"seo-suggest-link\" href=\"http://afamily.vn/bien-phap-tranh-thai.html\" target=\"_blank\" title=\"biện pháp tránh thai\">biện pháp tránh thai</a> nội tiết.</p><div class=\"VCSortableInPreviewMode active noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-5-1507700276619.jpg\" id=\"img_5866b800-ae46-11e7-a065-7937920d296b\" w=\"600\" h=\"150\" alt=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 4.\" title=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 4.\" rel=\"lightbox\" photoid=\"5866b800-ae46-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-5-1507700276619.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><b>Những vết sẹo sẽ còn lại vĩnh viễn</b></p><p>Mặc dù đã hồi phục nhưng Tasha có những vết sẹo vĩnh viễn trên đầu và bụng, tầm nhìn của cô cũng bị ảnh hưởng. Để có thể chấp nhận được mình như ngày hôm nay, cô đã phải cố gắng rất nhiều. Trong nhiều tháng cô không dám ra ngoài vì những vết sẹo trên đầu khiến cô cảm thấy mình giống như \'Frankenstein\'.</p><p>Giờ đây, cô hy vọng sẽ cảnh báo được cho những người khác về tác dụng phụ tiềm ẩn nguy hiểm và ít được biết đến của việc cấy que tránh thai.</p><p>Cô nói: \"Thị giác của tôi đã bị suy yếu, vì vậy tôi sẽ không bao giờ có thể lái xe mặc dù tôi thực sự cố gắng. Tôi có thể giấu đi những gì đã xảy ra với mình nhưng tôi quyết tâm dùng câu chuyện của chính mình để cảnh báo những người khác - những người có ý định cấy que tránh thai như tôi\".</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-6-1507700276618.jpg\" id=\"img_5879cad0-ae46-11e7-a065-7937920d296b\" w=\"602\" h=\"300\" alt=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 5.\" title=\"Chỉ 1 năm sau khi đặt que cấy tránh thai, cô gái trẻ bị tăng áp lực trong não, nhìn mờ và suýt tử vong - Ảnh 5.\" rel=\"lightbox\" photoid=\"5879cad0-ae46-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/dat-que-cay-tranh-thai-6-1507700276618.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">dat-que-cay-tranh-thai-6Những vết sẹo từng khiến cô khổ sở và không dám ra ngoài.</p></div></div><p><b>Các chuyên gia cho biết que cấy tránh thai là an toàn</b></p><p>Theo các chuyên gia y tế, rõ rằng biện pháp cấy que tránh thai là an toàn và những hiệu quả của nó đã được chứng minh.</p><p>Bekki Burbidge, từ tổ chức từ thiện chăm sóc <a class=\"seo-suggest-link\" href=\"http://afamily.vn/suc-khoe-tinh-duc.html\" target=\"_blank\" title=\"sức khỏe tình dục\">sức khỏe tình dục</a> FPA cho biết: \"Que cấy tránh thai là phương pháp tránh thai an toàn cho hầu hết phụ nữ và là một trong những biện pháp hiệu quả nhất để ngừa thai. Tuy nhiên, biện pháp này không phù hợp với một số phụ nữ mắc bệnh nào đó. Vì vậy, chuyên gia y tế sẽ luôn phải tính đến các yếu tố nguy cơ riêng lẻ khi kê toa đặt que cấy hoặc phương pháp ngừa thai khác cho bệnh nhân\".</p><div class=\"VCSortableInPreviewMode\" data-back=\"#FFFEC7\" data-border=\"#FF7D00\" style=\"background-color:#FFFBF1;\" id=\"ObjectBoxContent_1507699232736\" type=\"content\"><div placeholder=\"[nhập nội dung]\"><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><b>Nguyên nhân tăng huyết áp trong hộp sọ</b></p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><i>Các nguyên nhân có thể bao gồm:</i></p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Một cục máu đông trên bề mặt của não - được biết đến như một khối máu tụ dưới màng cứng.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Có một khối u não.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Nhiễm trùng não - như viêm màng não hoặc viêm não.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Bệnh tràn dịch não - sự tích tụ chất lỏng quanh và bên trong não.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Chứng bất thường của mạch máu - như là có lỗ rò ngoài động mạch hoặc dị dạng động mạch.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Một cục máu đông trong một hoặc những tĩnh mạch của não - được gọi là huyết khối xoang tĩnh mạch.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><i>Trong nhiều trường hợp, nguyên nhân của tăng huyết áp trong hộp sọ mãn tính không rõ ràng. Nó có thể có liên quan tới các tình trạng:</i></p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Đang thừa cân hoặc béo phì - phần lớn trường hợp xảy ra ở phụ nữ thừa cân, mặc dù không rõ tại sao.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Các vấn đề về hormone - chẳng hạn như hội chứng phát phì hoặc bệnh tuyến giáp.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Sử dụng một số loại thuốc - bao gồm một số kháng sinh, thuốc steroid và thuốc ngừa thai.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Thiếu máu hồng cầu (thiếu máu thiếu sắt) hoặc quá nhiều tế bào hồng cầu (đa u tứ huyết).</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Bệnh thận mãn tính.</p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">- Lupus - một vấn đề với hệ thống miễn dịch.</p><p><!--StartFragment--><!--EndFragment--></p><p style=\"text-align: right; margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><i>Nguồn: NHS Choices</i></p></div></div><p style=\"text-align: right;\"><i>Theo DailyMail/Sun</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Một thiếu ni&#234;n đ&#227; phải chiến đấu sống c&#242;n khi gặp phản ứng dẫn đến tăng huyết &#225;p trong hộp sọ do đặt que cấy tr&#225;nh thai. ', 'publish', 10, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(11, ' Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc ', 'bat-mi-cac-buoc-don-gian-de-chi-em-nhanh-chong-co-bap-dui-thon-gon-va-san-chac', 'http://afamilycdn.com/zoom/100_62/2017/bap-dui-thon-7-1507692298246-17-50-259-437-crop-1507692500397.gif', 'Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc          <div data-check-position=\"af_detail_body_start\"></div>          <p>Mặc dù rất nhiều chị em lo lắng về việc khó biến được cặp \"chân giò\" thành <a class=\"seo-suggest-link\" href=\"http://afamily.vn/cap-chan-san-chac.html\" target=\"_blank\" title=\"cặp chân săn chắc\">cặp chân săn chắc</a> thì đây là những gợi ý dễ dàng nhất sẽ giúp các chị em đạt được mong muốn của mình. Dưới đây là một vài bước đơn giản, dễ thực hiện ngay tại nhà riêng để chị em nhanh có <a class=\"seo-suggest-link\" href=\"http://afamily.vn/bap-dui-thon-gon.html\" target=\"_blank\" title=\"bắp đùi thon gọn\">bắp đùi thon gọn</a> và đôi chân săn chắc, quyến rũ:<br></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/bap-dui-thon-1-1507692298254.jpg\" id=\"img_c405a980-ae33-11e7-9564-854b3c797542\" w=\"768\" h=\"510\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 1.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 1.\" rel=\"lightbox\" photoid=\"c405a980-ae33-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-1-1507692298254.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><b>Ăn uống lành mạnh</b></p><p>Chỉ tập thể dục sẽ không giúp được lượng chất béo không mong muốn có trong cơ thể bạn tự nhiên biến mất bởi vì công thức vàng cho việc giảm cân và sở hữu được cơ bắp săn chắc là ăn kiêng và tập thể dục. Mặc dù chúng ta cũng không cổ xúy cho rằng tất cả các thực phẩm có đường và gluten đều là xấu nhưng việc ăn kiêng những thực phẩm có nhiều đường và gluten là điều cần thiết. </p><p>Hãy bắt đầu bằng cách cắt giảm ăn, uống những thực phẩm có đường và thay thế chúng bằng các đồ ăn nhẹ và đồ uống có chứa calorie lành mạnh hơn. Một số thực phẩm tuyệt vời để thay thế chúng là hạnh nhân, bơ, quả mọng...</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/bap-dui-thon-2-1507692298252.jpg\" id=\"img_c41f24f0-ae33-11e7-a047-73f8081241a6\" w=\"1024\" h=\"1024\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 2.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 2.\" rel=\"lightbox\" photoid=\"c41f24f0-ae33-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-2-1507692298252.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Thêm nữa, bạn hãy bắt đầu tăng tần suất các bữa ăn của bạn lên hơn là cắt bỏ đi bất kì bữa ăn nào. Bạn có thể ăn một bữa ăn nhỏ sau khi thức dậy và sau đó tiếp tục có những bữa ăn nhỏ khác cách nhau từ 2-3 giờ cho đến hết ngày. Những bữa ăn nhỏ này cũng không hẳn phải là \"bữa ăn\"; \"\"bữa ăn\" này có thể có thực đơn đơn giản như: một bát súp hoặc sữa chua trộn với trái cây.</p><p><b>Tập các bài tập cardio (bài tập tim mạch)</b></p><p>Cùng với chế độ ăn kiêng thì việc tập thể dục cần phải đi kèm với bài tập cardio để giúp cho tim mạch khỏe mạnh. Việc tập cardio hiện tại có rất nhiều thay đổi giúp bạn cảm thấy vui vẻ và thích thú hơn là chỉ đơn thuần chạy xung quanh nơi bạn ở. Nếu việc cắt giảm lượng calo là điều khó khăn với cơ thể của bạn thì bạn hãy cân bằng điều này bằng cách tập cardio. </p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/bap-dui-thon-3-1507692298251.jpg\" id=\"img_c432d400-ae33-11e7-9564-854b3c797542\" w=\"1367\" h=\"2048\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 3.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 3.\" rel=\"lightbox\" photoid=\"c432d400-ae33-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-3-1507692298251.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Tập cardio sẽ giúp cơ thể đốt cháy được calo tốt hơn. Mỗi tuần bạn chỉ cần tập 3 lần, mỗi lần 30 phút, đảm bảo bạn sẽ có thân hình chắc, khỏe, đẹp như mong muốn của bạn.</p><p>Một số bài tập giúp cho cơ đùi thêm săn chắc, mời các bạn tham khảo:</p><p><i>Side Lunge (1 biến thể của bài tập Lunge)</i></p><p><i><br></i></p><div class=\"VCSortableInPreviewMode noCaption active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/bap-dui-thon-4-1507692298250.png\" id=\"img_c441c820-ae33-11e7-a047-73f8081241a6\" w=\"765\" h=\"554\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 4.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 4.\" rel=\"lightbox\" photoid=\"c441c820-ae33-11e7-a047-73f8081241a6\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-4-1507692298250.png\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Tập lunge bên không chỉ giúp tăng sức mạnh cho bắp chân mà nó còn cải thiện cả sự dẻo dai của chân. Nếu bạn muốn thêm tăng thêm thử thách khi tập side lunge thì hãy lấy thêm tạ để tập cùng.</p><p>Bắt đầu với tư thế đứng, hai chân mở rộng bằng vai. Căng cứng cơ bụng, bước một chân sải rộng sang ngang, chân còn lại gập gối xuống, hạ hông về phía sau, giữ người thằng, hai chân đặt thẳng ở trên sàn. Tiếp theo, đẩy người ngược lại như tư thế ban đầu và tập lặp lại cho các bên còn lại.</p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bap-dui-thon-7-1507692298246.gif\" id=\"img_c47f1f40-ae33-11e7-a047-73f8081241a6\" w=\"461\" h=\"259\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 5.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 5.\" rel=\"lightbox\" photoid=\"c47f1f40-ae33-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-7-1507692298246.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><i>Leg Lift (Nâng chân)</i></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bap-dui-thon-5-1507692298248.jpg\" id=\"img_c452b810-ae33-11e7-81d9-d137edec0f5b\" w=\"550\" h=\"471\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 6.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 6.\" rel=\"lightbox\" photoid=\"c452b810-ae33-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-5-1507692298248.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Nằm nghiêng trên sàn nhà với tư thế chân trái của bạn phải duỗi thẳng, chân phải co lên vuông góc sao cho chân phải đặt trên sàn nhà. Bạn có thể dùng tay để đỡ đầu của bạn. </p><p>Tiếp theo, bạn nâng chân trái lên khỏi sàn, giữ thẳng. Lặp lại điều này trong 30 giây ở mỗi bên. </p><p>Nếu bạn không chắc chắn rằng nếu bạn có đúng kỹ thuật tập không thì bạn đừng ngại tìm kiếm video để giúp bạn thực hiện đúng.</p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bap-dui-thon-8-1507692298245.gif\" id=\"img_c48cdae0-ae33-11e7-9564-854b3c797542\" w=\"1024\" h=\"683\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 7.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 7.\" rel=\"lightbox\" photoid=\"c48cdae0-ae33-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-8-1507692298245.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><i>Fire Hydrants + Fire Hydrant Kicks</i></p><p><i><br></i></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/bap-dui-thon-6-1507692298247.png\" id=\"img_c46f19b0-ae33-11e7-9564-854b3c797542\" w=\"726\" h=\"535\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 8.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 8.\" rel=\"lightbox\" photoid=\"c46f19b0-ae33-11e7-9564-854b3c797542\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-6-1507692298247.png\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Trong tư thế chống 2 tay thẳng và chân quỳ xuống sàn tập, 2 tay mở rộng bằng vai, cẳng chân đặt trên sàn tập. Bắt đầu với chân trái của bạn, nâng đầu gối của bạn lên và đưa sang bên cạnh. Căng cứng cơ bụng và không di chuyển hông, gối luôn giữ 1 góc 90 độ, lưu ý bạn đừng bao giờ để đầu gối trái chạm vào mặt đất. Lặp lại động tác này cho mỗi chân trong 30 giây.<br></p><p>Fire Hydrant Kicks tương tự như Fire Hydrants. Khi bạn nâng đầu gối trái lên khỏi mặt đất, hãy nhớ là đá chân trái sang và giữ chân thẳng. </p><div class=\"VCSortableInPreviewMode GifPhoto noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/bap-dui-thon-9-1507692298244.gif\" id=\"img_c49e8e20-ae33-11e7-ad77-9f411bebcc1b\" w=\"1024\" h=\"683\" alt=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 9.\" title=\"Bật mí các bước đơn giản để chị em nhanh chóng có bắp đùi thon gọn và săn chắc - Ảnh 9.\" rel=\"lightbox\" photoid=\"c49e8e20-ae33-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/bap-dui-thon-9-1507692298244.gif\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><i>Lưu ý:</i> Khi tập bài tập này các bạn cần khởi động khớp háng trước để tránh bị chuột rút chân trong lần đầu tiên tập nhé!</p><p><br></p><p style=\"text-align: right;\"><i>Theo Lifehack</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Muốn tự tin diện v&#225;y ngắn khoe đ&#249;i thon gọn, săn chắc th&#236; đ&#226;y l&#224; những g&#236; c&#225;c chị em n&#234;n l&#224;m theo ngay lập tức. ', 'publish', 11, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `description`, `status`, `pos`, `seo_title`, `seo_keyword`, `seo_description`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(12, ' 2 tép tỏi và 1 thìa mật ong: Vừa giảm cân nhanh lại cực tốt cho hệ tiêu hóa ', '2-tep-toi-va-1-thia-mat-ong-vua-giam-can-nhanh-lai-cuc-tot-cho-he-tieu-hoa', 'http://afamilycdn.com/zoom/100_62/2017/t4-1507350759678-54-0-512-733-crop-1507350949047.jpg', '2 tép tỏi và 1 thìa mật ong: Vừa giảm cân nhanh lại cực tốt cho hệ tiêu hóa          <div data-check-position=\"af_detail_body_start\"></div>          <p>Hãy nghĩ đến điều này: Bạn đi vào những cửa hàng mua sắm quần áo và thật buồn thay chẳng có bộ đồ nào vừa vặn với thân hình của bạn vì quá bé nhỏ. Nếu bạn đang ở trong tình trạng này, <a class=\"seo-suggest-link\" href=\"http://afamily.vn/giam-can.html\" target=\"_blank\" title=\"giảm cân\">giảm cân</a> chắc chắn không nằm ngoài khát khao dự định của bạn.<br></p><p>Hẳn là nếu sở hữu một chiếc bụng béo, bắp đùi to, cánh tay mũm mĩm… bạn sẽ không cảm thấy tự tin mặc những bộ đồ yêu thích của mình. Mặc dù điều quan trọng là phải chấp nhận bản thân vì những gì chúng ta đang có, nếu không có thể sẽ gặp phải nhiều vấn đề sức khỏe nhất định. Nhưng, quả thực việc thừa cân có thể khiến bạn vô cùng tự ti, thậm chí thành <a class=\"seo-suggest-link\" href=\"http://afamily.vn/tram-cam.html\" target=\"_blank\" title=\"trầm cảm\">trầm cảm</a>.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/t1-1507350759675.jpg\" id=\"img_8d9fbbb0-ab18-11e7-80e3-897aa0f332fe\" w=\"1000\" h=\"647\" alt=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 1.\" title=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 1.\" rel=\"lightbox\" photoid=\"8d9fbbb0-ab18-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/t1-1507350759675.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Theo Boldsky, tỏi sẽ giúp bạn giảm cân nhanh chóng chỉ trong 1 tháng.</p></div></div><p>Thừa cân, ngoài các bệnh tâm thần như trầm cảm, thừa cân hoặc béo phì cũng có thể dẫn đến những biến chứng nghiêm trọng về sức khoẻ như huyết áp cao, tiểu đường, rối loạn nhịp tim, đau khớp, cholesterol cao, khó tiêu...</p><p>Trên thực tế, ngay cả các bệnh nguy hiểm như một số loại ung thư cũng liên quan đến chứng béo phì. Vì vậy, điều quan trọng là phải duy trì một lối sống lành mạnh và đảm bảo rằng bạn làm tất cả mọi thứ trong tay để giữ cho trọng lượng của bạn được kiểm soát. Biện pháp giảm cân tự nhiên dưới đây sẽ giúp bạn giảm cân nhanh chóng chỉ trong 1 tháng duy nhất:</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/t2-1507350759676.jpg\" id=\"img_8daeafd0-ab18-11e7-81d9-d137edec0f5b\" w=\"960\" h=\"599\" alt=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 2.\" title=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 2.\" rel=\"lightbox\" photoid=\"8daeafd0-ab18-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/t2-1507350759676.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Các chất chống oxy hóa trong mật ong cũng có thể giúp các chất béo trong cơ thể bạn được tống ra thường xuyên, do đó giúp bạn giảm cân.</p></div></div><p><b>Cách giảm cân tự nhiên từ tỏi</b></p><p>Bạn chỉ cần sử dụng hai nguyên liệu quen thuộc sau:</p><p>- Tỏi sống 2 tép.</p><p>- Mật ong nguyên chất 1 thìa.</p><p>Bạn chỉ cần nghiền nát tỏi sống hoặc cắt chúng thành những miếng nhỏ. Thêm những miếng tỏi này vào lượng mật ong như trên, trộn đều để tạo thành một hỗn hợp. Tiêu thụ hỗn hợp này vào mỗi buổi sáng khi dạ dày trống rỗng trong một tháng. Bạn có thể tiếp tục sử dụng biện pháp khắc phục này ngay cả sau một tháng để có hiệu quả lâu dài.<br></p><p>Theo Boldsky, điều quan trọng là bạn vẫn phải đảm bảo lối sống khỏe mạnh, ăn uống đủ chất và tập luyện thường xuyên để phương thuốc giảm cân này phát huy tác dụng tốt nhất. Tỏi thô có một thành phần được gọi là allicin, có khả năng tăng tốc độ trao đổi chất, cho phép các tế bào mỡ được đốt nhanh hơn. Các chất chống oxy hóa trong mật ong cũng có thể giúp các chất béo trong cơ thể bạn được tống ra thường xuyên, do đó giúp bạn giảm cân. Phương pháp giảm cân tự nhiên này có thể hoạt động hiệu quả khi sử dụng thường xuyên.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/t3-1507350759677.jpg\" id=\"img_8dc39760-ab18-11e7-a065-7937920d296b\" w=\"600\" h=\"402\" alt=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 3.\" title=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 3.\" rel=\"lightbox\" photoid=\"8dc39760-ab18-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/t3-1507350759677.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Bạn chỉ cần nghiền nát tỏi sống và trộn với mật ong.</p></div></div><p><b>Chuyên gia nói gì về hỗn hợp tỏi mật ong với vấn đề giảm cân?</b><br></p><p>Nói về việc kết hợp tỏi và mật ong, lương y Vũ Quốc Trung (Phòng Chẩn trị Y học Cổ truyền, Hội Đông y Việt Nam) cho biết, tỏi là gia vị, đồng thời là vị thuốc được sử dụng từ lâu đời. Tỏi có vị cay, tính ôn, chủ yếu có các loại tinh dầu, vitamin A, E, B1, B2… Tỏi kích thích tiêu hóa, <a class=\"seo-suggest-link\" href=\"http://afamily.vn/giai-doc.html\" target=\"_blank\" title=\"giải độc\">giải độc</a>, trừ đờm, sát khuẩn, thậm chí là diệt trừ giun.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/t4-1507350759678.jpg\" id=\"img_8dfecba0-ab18-11e7-80e3-897aa0f332fe\" w=\"733\" h=\"571\" alt=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 4.\" title=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 4.\" rel=\"lightbox\" photoid=\"8dfecba0-ab18-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/t4-1507350759678.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Tỏi kích thích tiêu hóa, giải độc, trừ đờm, sát khuẩn, thậm chí là diệt trừ giun.</p></div></div><p>Trong Đông y, tỏi được dùng để chữa các trường hợp tiêu hóa kém, viêm do hô hấp, chữa tả, lị, viêm âm đạo do nhiễm trùng roi, tăng huyết áp, tăng cholesterol trong máu. \"Nên dùng 5 – 15 g mỗi ngày, ở dạng còn sống vì nấu chín tỏi sẽ làm giảm tác dụng chữa bệnh\", ông Trung nói.</p><p>Mật ong là thực phẩm giàu dinh dưỡng, có vị ngọt, tính bình, không nóng. Thành phần chủ yếu của mật ong là đường gluco, lenluzo và fructozo, chất thơm, các loại men, có nhiều vitamin A, B, D, khoáng chất.</p><p>Theo Đông y, mật ong bổ trung tiêu, làm thuốc bổ toàn thân, nhuận tràng, sát khuẩn, chữa đau loét dạ dày, tá tràng, viêm phế quản…</p><p>\"Như vậy có thể nói, mật ong và tỏi có tác dụng tương tự như nhau. Do đó, về mặt nguyên lý, kết hợp ăn mật ong với tỏi là hoàn toàn vô hại. Chỉ có điều khi trộn mật ong với tỏi sẽ tạo ra mùi vị dễ ăn không mà thôi. Tỏi trộn với mật ong hoàn toàn có tác dụng làm thuốc bổ toàn thân. Tuy nhiên, hiệu quả giảm cân thì chưa có bằng chứng khoa học nào ghi nhận mặc dù xét về thành phần thì chúng có khả năng tăng cường trao đổi chất, rất tốt cho hệ tiêu hóa\", lương y Vũ Quốc Trung cho biết.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/t5-1507350835930.jpg\" id=\"img_ba98a460-ab18-11e7-aaaa-51ac0faef9da\" w=\"630\" h=\"420\" alt=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 5.\" title=\"Sử dụng tỏi theo cách này sẽ giúp bạn giảm cân chỉ trong một tháng, bạn có muốn thử kiểu giảm cân này? - Ảnh 5.\" rel=\"lightbox\" photoid=\"ba98a460-ab18-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/t5-1507350835930.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Theo Đông y, mật ong bổ trung tiêu, làm thuốc bổ toàn thân, nhuận tràng, sát khuẩn, chữa đau loét dạ dày, tá tràng, viêm phế quản…</p></div></div><p>Mặc dù vậy, chuyên gia khuyên mọi người nên sử dụng cách này để bồi bổ, tăng cường sức khỏe. Lý do mà chúng ta chỉ nên ăn hỗn hợp tỏi với mật ong lúc đói là bởi vì \"ăn lúc đói sẽ hấp thu tốt hơn cho cơ thể. Nếu ăn vào lúc no thì hỗn hợp sẽ nhào trộn cùng thức ăn, làm loãng hiệu quả tăng cường sức khỏe\". Lương y cũng nhắc, bạn nên đập dập tỏi trước khi cho vào mật ong sẽ giúp phát huy hiệu quả tốt hơn là để nguyên tép tỏi.</p><p>Chuyên gia khuyến cáo: \"Có một số trường hợp không nên dùng tỏi\". Trẻ em bụng dạ yếu, người có da nhạy cảm không nên bôi bên ngoài, người có phản ứng phụ khi ăn hỗn hợp tỏi và mật ong cần dừng lại ngay và đến khám bác sĩ.</p><p>Ngoài ra, ông Trung cũng nói thêm, không chỉ hỗn hợp mật ong với tỏi mới có tác dụng tăng cường sức khỏe, chữa bệnh. Đông y cũng đã khám phá ra nhiều công thức như trộn mật ong với nghệ để chữa bệnh đau dạ dày, hành tá tràng…</p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Theo Boldsky, giảm c&#226;n bằng c&#225;ch sử dụng tỏi kết hợp mật ong l&#250;c đ&#243;i sẽ gi&#250;p bạn giảm c&#226;n chỉ trong 1 th&#225;ng. ', 'publish', 12, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(13, ' 7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích ', '7-loai-thuc-pham-giup-co-the-ban-thom-tho-khien-nguoi-ben-canh-cung-thich', 'http://afamilycdn.com/zoom/100_62/2017/mui-co-the-1-1507614080739-18-9-614-963-crop-1507614226383.jpg', '7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích          <div data-check-position=\"af_detail_body_start\"></div>          <p>Bạn xấu hổ vì có <a class=\"seo-suggest-link\" href=\"http://afamily.vn/mui-co-the.html\" target=\"_blank\" title=\"mùi cơ thể\">mùi cơ thể</a>? Thực ra là có rất nhiều người phải chịu đựng hoàn cảnh giống như bạn. Thế nhưng, rõ ràng bạn sẽ rất mất tự tin nếu như để người khác nhận ra mùi hôi từ cơ thể mình. Vậy thì phải xử lý vấn đề này ngay thôi.<br></p><div class=\"VCSortableInPreviewMode active noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-10-1507614334742.jpg\" id=\"img_3e598c30-ad7e-11e7-9564-854b3c797542\" w=\"637\" h=\"425\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 1.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 1.\" rel=\"lightbox\" photoid=\"3e598c30-ad7e-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-10-1507614334742.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Mùi cơ thể có thể là do sự thay đổi hormone trong cơ thể, đổ mồ hôi quá nhiều, chế độ ăn uống không lành mạnh, di truyền hay vệ sinh kém. Mặc dù tắm, dùng các chất khử mùi hay nước hoa có sẵn để kiểm soát mùi cơ thể, nhưng chỉ là giải pháp tạm thời.</p><p>Trong nhiều trường hợp, vấn đề có thể được giải quyết một cách dễ dàng bằng cách chỉ cần có một chế độ ăn uống lành mạnh. Những gì bạn ăn và không ăn có thể góp phần ảnh hưởng đến mùi tự nhiên của cơ thể đấy. </p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/mui-co-the-1-1507614080739.jpg\" id=\"img_a8a4a850-ad7d-11e7-81d9-d137edec0f5b\" w=\"1000\" h=\"667\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 2.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 2.\" rel=\"lightbox\" photoid=\"a8a4a850-ad7d-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-1-1507614080739.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Trên thực tế, có rất nhiều thực phẩm sẽ giúp bạn khử mùi và làm sạch cơ thể. Nhiều thực phẩm cũng có hiệu quả trong việc chống lại mùi hôi và tạo ra mùi thơm tự nhiên của cơ thể.</p><p>Nếu muốn <a class=\"seo-suggest-link\" href=\"http://afamily.vn/co-the-thom-tho.html\" target=\"_blank\" title=\"cơ thể thơm tho\">cơ thể thơm tho</a>, bạn hãy chuyển sang bổ sung các loại thực phẩm sau vào chế độ ăn uống xem sao.</p><p><b>1. Chanh</b></p><p>Mùi hương mát mẻ giúp tỉnh táo của chanh đóng một vai trò quan trọng trong việc làm cho cơ thể của bạn có mùi thơm tuyệt vời. Các chất khử trùng tự nhiên và khả năng kháng khuẩn của chanh cũng giúp loại bỏ mùi cơ thể và giảm hẳn cả hơi thở hôi do vi khuẩn gây ra.</p><p>Tính axit của nước chanh làm giảm độ pH của da khiến cho vi khuẩn gây mùi khó có thể tồn tại. Thêm vào đó, nhờ tính chất giàu vitamin C (tăng cường miễn dịch), chanh còn giúp loại bỏ độc tố độc hại có trong cơ thể.</p><p>Ngoài ra, một điểm cộng khác là chanh dễ hấp thụ bởi cơ thể, do đó nó sẽ cải thiện mùi hương của cơ thể bạn rất nhanh. </p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-2-1507614080743.jpg\" id=\"img_a8b6f7d0-ad7d-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"400\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 3.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 3.\" rel=\"lightbox\" photoid=\"a8b6f7d0-ad7d-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-2-1507614080743.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Để chống lại mùi hôi ở chân, bạn có thể chà nửa quả chanh bàn chân. Chờ cho nước khô hoàn toàn, sau đó rửa sạch chân. Chịu khó làm như vậy một lần mỗi ngày bạn sẽ thấy mùi hôi nhanh chóng biến mất.</p><p><b>2. Cà chua</b></p><p>Cà chua chứa các chất có khả năng kháng khuẩn và sát trùng tự nhiên giúp giảm lượng vi khuẩn gây mùi trong cơ thể.</p><p>Ngoài ra, nhờ có chất làm se tự nhiên, cà chua giúp thu nhỏ lỗ chân lông và ngăn chặn các đường dẫn tiết ra mồ hôi, do đó kiểm soát quá mồ hôi rất tốt. Ngay cả việc uống nước cà chua cũng có thể giúp làm giảm nhiệt độ cơ thể của bạn, nhờ đó sẽ làm giảm mồ hôi.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-3-1507614080746.jpg\" id=\"img_a8e69350-ad7d-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"400\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 4.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 4.\" rel=\"lightbox\" photoid=\"a8e69350-ad7d-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-3-1507614080746.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Hoặc bạn cũng có thể dùng nước ép cà chua tươi xoa vào những bộ phận cơ thể ra nhiều mồ hôi và để trong 10-15 phút thì tắm, rửa lại sạch sẽ.</p><p><b>3. Trà xanh</b></p><p>Trà xanh giàu chất chống oxy hoá là một loại đồ uống độc đáo có thể giúp ngăn ngừa hơi thở hôi, mùi cơ thể và mùi hôi từ bàn chân.</p><p>Trà xanh hỗ trợ giải độc bằng cách hỗ trợ sản xuất glutathione, một chất chống oxy hóa có tác dụng vận chuyển loại bỏ độc tố độc hại ra khỏi cơ thể. Các chất độc càng ít, cơ thể bạn càng không có mùi hôi.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-4-1507614080750.jpg\" id=\"img_a8f25320-ad7d-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"400\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 5.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 5.\" rel=\"lightbox\" photoid=\"a8f25320-ad7d-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-4-1507614080750.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Các polyphenol trong trà xanh cũng giúp làm cho hơi thở thơm tho, dễ chịu vì nó phá hủy và loại bỏ các hợp chất gây <a class=\"seo-suggest-link\" href=\"http://afamily.vn/chung-hoi-mieng.html\" target=\"_blank\" title=\"chứng hôi miệng \">hôi miệng</a>.</p><p><b>4. Dầu dừa</b><br></p><p>Dầu dừa tạo ra một chất khử mùi tuyệt vời và tự nhiên, giúp cơ thể bạn có mùi thơm dễ chịu. Tính chất kháng khuẩn của chuỗi axit béo trung tính trong dầu dừa giúp hạn chế sự phát triển của vi khuẩn gây mùi.</p><p>Ngoài ra, dầu dừa còn hỗ trợ sức khoẻ tiêu hóa - một yếu tố quan trọng khác trong việc chống lại mùi cơ thể và hôi miệng.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-5-1507614080753.jpg\" id=\"img_a8ffc0a0-ad7d-11e7-9564-854b3c797542\" w=\"600\" h=\"400\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 6.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 6.\" rel=\"lightbox\" photoid=\"a8ffc0a0-ad7d-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-5-1507614080753.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><b>5. Cây hương thảo</b><br></p><p>Loại cây thảo mộc có mùi thơm này cũng có thể giúp tiêu diệt mùi cơ thể. Các loại dầu dễ bay hơi trong cây hương thảo có thể chống lại bất kỳ loại mùi nào xuất phát từ cơ thể bằng cách ức chế sự phát triển của vi khuẩn gây mùi. Ngoài ra, các hợp chất tinh dầu bạc hà và chất diệp lục trong cây hương thảo giúp trung hòa mùi cơ thể và cho làm cho cơ thể có mùi dễ chịu.</p><p>Uống 2 chén trà được làm từ lá hương thảo tươi hoặc lá khô mỗi ngày. </p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-6-1507614080756.jpg\" id=\"img_a911c200-ad7d-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"400\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 7.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 7.\" rel=\"lightbox\" photoid=\"a911c200-ad7d-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-6-1507614080756.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Bạn cũng có thể trộn tinh dầu hương thảo với nước và massage lên những vùng mồ hôi nhiều 2-3 lần/ngày. Tuy nhiên, nếu tinh dầu hương thảo gây kích ứng da thì bạn cần ngưng biện pháp này lại ngay.</p><p><b>6. Lá xô thơm</b></p><p>Cây xô thơm là một loại thảo mộc có thể giúp cơ thể bạn có mùi ngọt ngào và dễ chịu nhờ có các hợp chất thơm như diosmetin, apigenin và luteolin. Tác dụng kháng khuẩn của nó cũng là lý do có thể ngăn ngừa vi khuẩn nhân lên trên da, do đó loại bỏ mùi cơ thể.</p><p>Thêm vào đó, loại thảo mộc này làm giảm hoạt động của tuyến mồ hôi, hỗ trợ việc thải độc tố khỏi cơ thể, nhờ đó bạn ít đổ mồ hôi và <i>cơ thể thơm tho</i> hơn.</p><p>Uống trà từ cây xô thơn 1-2 lần/ngày. </p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-7-1507614080759.jpg\" id=\"img_a921a080-ad7d-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"400\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 8.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 8.\" rel=\"lightbox\" photoid=\"a921a080-ad7d-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-7-1507614080759.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Lưu ý: Loại thảo mộc này không thích hợp cho bà mẹ mang thai hoặc cho con bú. Ngoài ra, bạn nên tránh uống quá nhiều hoặc quá đặc vì có thể gây chóng mặt.</p><p><b>7. Rau mùi</b></p><p>Các loại dầu mạnh mẽ ở rau mùi có thể giúp cơ thể vượt qua các mùi hôi. Rau mùi cũng chứa một lượng chất diệp lục phong phú, có tác dụng kiềm hóa cơ thể, có nghĩa là sẽ giúp làm sạch máu và cải thiện mùi cơ thể.</p><p>Ngoài ra, các hiệu ứng kháng khuẩn của rau mùi sẽ chống lại các vi khuẩn gây mùi trong cơ thể cũng như hỗ trợ giải độc.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/mui-co-the-8-1507614080762.jpg\" id=\"img_a93af4e0-ad7d-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"400\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 9.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 9.\" rel=\"lightbox\" photoid=\"a93af4e0-ad7d-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-8-1507614080762.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Nhai một ít lá rau mùi tươi cũng là biện pháp tạm thời che dấu mùi hôi miệng.</p><h3><b>Một vài mẹo đẩy lui mùi hôi cơ thể:</b></h3><p>- Tránh ăn những thực phẩm như tỏi và hành tây.<br></p><p>- Tránh thức ăn có hàm lượng lưu huỳnh cao như măng tây, măng lá.</p><p>- Tránh các loại thực phẩm như thịt đỏ, rượu và đồ ăn vặt. Các loại thực phẩm này chứa chất độc được giải phóng thông qua mồ hôi và có thể gây ra mùi hôi cơ thể.</p><p>- Nên ăn trái cây và rau tươi để giải độc cơ thể.</p><p>- Uống đủ nước mỗi ngày để ngăn chặn sự tích tụ chất độc trong cơ thể.</p><p>- Tắm ít nhất một lần mỗi ngày.</p><p>- Luôn giặt và mặc quần áo sạch sẽ.</p><p>- Không đi giầy quá chật trong thời gian dài.</p><p>- Giữ cho người khô, tránh ẩm ướt.</p><p>- Mặc quần áo cotton thay vì vải tổng hợp để tránh mồ hôi và ẩm ướt.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/mui-co-the-9-1507614334744.jpg\" id=\"img_3e6ced20-ad7e-11e7-aaaa-51ac0faef9da\" w=\"1920\" h=\"1080\" alt=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 10.\" title=\"7 loại thực phẩm giúp cơ thể bạn thơm tho khiến người bên cạnh cũng thích - Ảnh 10.\" rel=\"lightbox\" photoid=\"3e6ced20-ad7e-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/mui-co-the-9-1507614334744.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p style=\"text-align: right;\"><i>Theo Top10homeremedies/Ncbi/Health</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Nếu muốn cơ thể thơm tho, bạn h&#227;y chuyển sang bổ sung c&#225;c loại thực phẩm sau v&#224;o chế độ ăn uống xem sao. ', 'publish', 13, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(14, ' 5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất ', '5-trieu-chung-khien-ban-chan-ghet-ki-kinh-nguyet-va-cach-khac-phuc-tu-nhien-nhat', 'http://afamilycdn.com/zoom/100_62/2017/c1-1507605163665-0-24-431-714-crop-1507605420202.jpg', '5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất          <div data-check-position=\"af_detail_body_start\"></div>          <p>Hiện nay, Italy đang cho phép phụ nữ nghỉ phép 3 ngày trong một tháng mỗi khi đến kỳ <a class=\"seo-suggest-link\" href=\"http://afamily.vn/kinh-nguyet.html\" target=\"_blank\" title=\"kinh nguyệt\">kinh nguyệt</a>, đây là nước đầu tiên ở châu Âu làm như vậy. Healthista đã nói chuyện với Emma Cannon, chuyên gia về sinh sản và sức khỏe của phụ nữ về những cách đối phó với các triệu chứng thường gặp nhất mỗi khi chị em đến những ngày này.<br></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/c1-1507605163665.jpg\" id=\"img_e161b350-ad68-11e7-a047-73f8081241a6\" w=\"768\" h=\"432\" alt=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 1.\" title=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 1.\" rel=\"lightbox\" photoid=\"e161b350-ad68-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/c1-1507605163665.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Chu kỳ kinh nguyệt chính là thứ riêng biệt của phụ nữ.</p></div></div><p>Việc phụ nữ được nghỉ phép khi tới kỳ kinh nguyệt bắt nguồn từ đất nước Trung Quốc, và Italy là nước tiếp theo cũng là nước đầu tiên ở châu Âu thực hiện điều này. Các nghị sĩ của MP ở Italy đang soạn thảo một dự luật nhằm mang đến cho phụ nữ đất nước họ kỳ nghỉ 3 ngày mỗi tháng trong giai đoạn mà họ gặp phải những \"đau đớn\" của kinh nguyệt. Bạn có nghĩ rằng, chúng ta cũng nên áp dụng điều luật tương tự đối với Việt Nam không?</p><p>Cơ thể phụ nữ thật đáng kinh ngạc, bởi không giống như đàn ông, chúng ta phải trải qua chu kỳ tăng trưởng mỗi tháng, phát triển và đổi mới. Bên trong bạn, một quá trình kỳ diệu xảy ra nhiều lần, để chuẩn bị cho đến khi bạn sẵn sàng có một em bé của mình. </p><p><a class=\"seo-suggest-link\" href=\"http://afamily.vn/chu-ky-kinh-nguyet.html\" target=\"_blank\" title=\"chu kỳ kinh nguyệt\">Chu kỳ kinh nguyệt</a> chính là thứ riêng biệt của phụ nữ, và điều đó mang đến cho chúng ta một khả năng tuyệt vời nhất trái đất này, chính là khả năng sinh sản. Đáng buồn thay, trước khi có được quả ngọt đó, chúng ta phải trải qua những cơn đau tệ hại mỗi tháng mà bản thân phải hoàn toàn chấp nhận.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/c5-1507605290678.jpg\" id=\"img_2cb06ae0-ad69-11e7-aeea-a394e698d7aa\" w=\"600\" h=\"400\" alt=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 2.\" title=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 2.\" rel=\"lightbox\" photoid=\"2cb06ae0-ad69-11e7-aeea-a394e698d7aa\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/c5-1507605290678.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Hãy bắt đầu với cuốn nhật ký của bạn, nơi ghi lại những ngày của chu kỳ.</p></div></div><p>Vì vậy, chúng tôi đã dành thời gian để nghiên cứu những tình trạng của PMS và cách giải quyết tốt nhất giúp bạn giảm thiểu những cơn đau, những nỗi khổ thầm kín mà chu kỳ kinh nguyệt mang lại.</p><h2>Cảm thấy buồn rầu, căng thẳng</h2><p>Hãy bắt đầu với cuốn nhật ký của bạn, nơi ghi lại những ngày của chu kỳ. Bắt đầu từ ngày đầu tiên, đánh dấu nó trong nhật ký. Hãy lưu ý đến những cảm xúc của bạn vào ngày hôm đó và cả những ngày khác trong những ngày rớt dâu. Ghi lại cảm giác của bạn và những gì gây nên những cảm xúc đó. Từ đó giải quyết từng vấn đề trong cuộc sống hàng ngày, kể cả những vấn đề phát sinh từ quá khứ có thể ảnh hưởng đến cảm xúc hiện tại của bạn. Một khi bạn đã xác định được cảm xúc của bản thân, bạn có thể thư giãn bằng cách ngồi thiền giúp tâm trí bình tĩnh hơn, tránh khỏi sự căng thẳng buồn rầu do \"bà dì\" mang tới.</p><p><b>Kiệt sức</b></p><p>Đây là tình trạng thường gặp mỗi khi \"bà dì\" tới thăm, đặc biệt là những người có hội chứng PMS . Để cải thiện tình trạng này, một số nơi thường dùng thuốc bổ cổ truyền có nguồn gốc từ phương Đông. Ở các nước châu Á, nhiều phụ nữ nhận thức được tầm quan trọng của việc ăn một số thực phẩm dinh dưỡng nhất định, đặc biệt là sau chu kỳ để mang lại sự cân bằng cho chu kỳ kinh nguyệt. Bởi chu kỳ kinh nguyệt được coi là một khía cạnh quan trọng của sức khỏe người phụ nữ, và thực phẩm là một công cụ dinh dưỡng tuyệt vời giúp cơ thể cân bằng.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/c2-1507605163666.jpg\" id=\"img_e1adfe90-ad68-11e7-80e3-897aa0f332fe\" w=\"724\" h=\"483\" alt=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 3.\" title=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 3.\" rel=\"lightbox\" photoid=\"e1adfe90-ad68-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/c2-1507605163666.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Gừng đậm đặc giúp làm ấm và nuôi dưỡng máu của tử cung, rất tốt cho phụ nữ trong chu kỳ kinh nguyệt.</p></div></div><p>Thay đổi chế độ ăn uống trong thời kỳ hành kinh có tác động rất lớn đến mức năng lượng của bạn. Dưới đây là một số lời khuyên cho bạn:</p><p>-	Gừng đậm đặc giúp làm ấm và nuôi dưỡng máu của tử cung.</p><p>-	Dầu mè đen (nếu bạn không tìm bạn có thể thay bằng dầu mè thường) giúp nuôi dưỡng máu.</p><p>-	Rượu gạo ấm.</p><p>-	Trứng giúp nuôi dưỡng máu, mang lại năng lượng cho cơ thể.</p><p><b>Chu kỳ không đều, đau bụng kinh</b></p><p><a class=\"seo-suggest-link\" href=\"http://afamily.vn/cham-cuu.html\" target=\"_blank\" title=\"châm cứu\">Châm cứu</a> đã được sử dụng trong nhiều năm giúp phụ nữ quản lý các vấn đề về tuổi tác, và cả chu kỳ. Châm cứu kích thích vùng dưới đồi, một bộ phận của bộ não điều khiển các hoóc-môn tác động đến việc điều chỉnh chu kỳ. Nó cũng được chứng minh có thể điều trị những cơn đau do PMS đem lại, với hàng ngàn năm lịch sử chứng minh sự an toàn và hiệu quả.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/c3-1507605163667.jpg\" id=\"img_e1e2ca30-ad68-11e7-aaaa-51ac0faef9da\" w=\"724\" h=\"483\" alt=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 4.\" title=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 4.\" rel=\"lightbox\" photoid=\"e1e2ca30-ad68-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/c3-1507605163667.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Châm cứu đã được sử dụng trong nhiều năm giúp phụ nữ quản lý các vấn đề về tuổi tác, và cả chu kỳ.</p></div></div><p><b>Cơn đau kinh nguyệt kéo dài</b></p><p>Các cơn đau thường kéo dài do prostaglandin gây ra, và cơ thể bạn cũng tăng nhạy cảm với đau vào những ngày này. Để giảm bớt cơn đau đó, bạn cần bổ sung các chất prostaglandin lành mạnh (chất béo lành mạnh có tác động tích cực lên hoóc-môn) vào cơ thể. Dầu cá Omega-3 là một nguồn prostaglandin tuyệt vời.</p><p>Để giảm căng thẳng trong kỳ kinh nguyệt, bạn có thể uống thêm vitamin B, magie – làm giãn cơ và ngủ ngon hơn, cùng với vitamin D – liên quan đến việc điều chỉnh chu kỳ kinh nguyệt của bạn.</p><p>Ngoài ra, nếu bạn bị <a class=\"seo-suggest-link\" href=\"http://afamily.vn/rong-kinh.html\" target=\"_blank\" title=\"rong kinh\">rong kinh</a> – hiện tượng chu kỳ kinh nguyệt kéo dài quá 7 ngày và lượng máu mất đi nhiều, bạn có thể sử dụng chất bổ sung sắt có lợi dưới sự tư vấn của bác sĩ, giúp tăng cường năng lượng và điều chỉnh kinh nguyệt. Vitamin C rất tốt trong việc giúp cơ thể hấp thụ sắt khi dùng chung với chất bổ sung sắt.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/c4-1507605163668.jpg\" id=\"img_e2023910-ad68-11e7-aaaa-51ac0faef9da\" w=\"724\" h=\"483\" alt=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 5.\" title=\"5 triệu chứng khiến bạn chán ghét kì kinh nguyệt và cách khắc phục tự nhiên nhất - Ảnh 5.\" rel=\"lightbox\" photoid=\"e2023910-ad68-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/c4-1507605163668.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Để giảm căng thẳng trong kỳ kinh nguyệt, bạn có thể uống thêm vitamin B, magie.</p></div></div><p><b>Bloating – đầy hơi</b></p><p>Một sai lầm phổ biến mà mọi người thường làm để tránh đầy hơi là ăn nhiều thực phẩm tươi sống, hoặc thay thế các bữa ăn bằng nước trái cây. Nhưng điều này thực sự chỉ làm tình trạng tồi tệ hơn mà thôi.</p><p>Hầu hết mọi người không dùng quá nhiều thức ăn tươi vì rất khó tiêu hóa. Nó có xu hướng ở trong ruột theo hình thức không tiêu hóa, dẫn đến quá trình lên men và khí gây đầy hơi, tiêu hóa kém. Bổ sung thức ăn nóng vào chế độ ăn và ăn thực phẩm nấu chín mới thực sự giúp bạn giảm tình trạng đầy hơi.</p><p>Lời khuyên cuối cùng là đừng dùng nhiều nước ép, nước giải khát lạnh và tránh uống nhiều nước trong mỗi bữa ăn. Tôi có một quy tắc vàng là \"Tuyệt đối không dùng thực phẩm tươi sống sau 4 giờ chiều\".</p><p style=\"text-align: right;\"><i>(Nguồn: Healthista)</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Chu kỳ kinh nguyệt ch&#237;nh l&#224; thứ ri&#234;ng biệt của phụ nữ, v&#224; điều đ&#243; mang đến cho ch&#250;ng ta một khả năng tuyệt vời nhất tr&#225;i đất n&#224;y, ch&#237;nh l&#224; khả năng sinh sản. ', 'publish', 14, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(15, ' 3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải ', '3-sai-lam-khi-dung-san-pham-bo-sung-dinh-duong-ma-ngay-ca-phu-nu-thong-minh-cung-co-the-mac-phai', 'http://afamilycdn.com/zoom/100_62/2017/san-pham-bo-sung-3-1507543036043-0-14-432-704-crop-1507543316544.jpg', '3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải          <div data-check-position=\"af_detail_body_start\"></div>          <p>Có dùng <a class=\"seo-suggest-link\" href=\"http://afamily.vn/san-pham-bo-sung-dinh-duong.html\" target=\"_blank\" title=\"sản phẩm bổ sung dinh dưỡng\">sản phẩm bổ sung dinh dưỡng</a> hay không là câu hỏi thường gặp của rất nhiều người. Quả thực, đây cũng là vấn đề gây tranh cãi ngay cả đối với các nhà nghiên cứu. </p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/san-pham-bo-sung-3-1507543036043.jpg\" id=\"img_3d9667b0-acd8-11e7-8a08-1bdf9c92da84\" w=\"764\" h=\"432\" alt=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 1.\" title=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 1.\" rel=\"lightbox\" photoid=\"3d9667b0-acd8-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/san-pham-bo-sung-3-1507543036043.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Có nên dùng sản phẩm bổ sung dinh dưỡng hay không?</p></div></div><p>Ví dụ như mới tháng trước, nghiên cứu của Đại học Harvard đã phát hiện ra rằng bà mẹ mang thai mà bổ sung vitamin tổng hợp thì có thể tăng nhận thức của đứa trẻ lên tới 1 năm. Nhưng một nghiên cứu được đăng trên tạp chí The Grocer xuất bản vào năm ngoái cho thấy có vẻ như nhiều người đang mắc phải những sai lầm khi dùng các <a class=\"seo-suggest-link\" href=\"http://afamily.vn/san-pham-bo-sung.html\" target=\"_blank\" title=\"sản phẩm bổ sung\">sản phẩm bổ sung</a>.</p><h2>Vậy ai cần dùng sản phẩm bổ sung dinh dưỡng?</h2><p>Nhà dinh dưỡng học Robert Hobson cho biết: Thực phẩm luôn là thứ quan trọng nhất và sản phẩm bổ sung chỉ có tác dụng bổ sung phần thiếu ít ỏi trong chế độ ăn uống của bạn. Ví dụ, bạn có thể cần phải thêm sản phẩm bổ sung dinh dưỡng trong một số trường hợp:</p><p><i>- Nếu bạn không ăn đủ và có thể cần nhiều vitamin, khoáng chất.</i></p><p><i>- Nếu bạn không ăn cá dầu, bạn có thể cần bổ sung omega 3.</i></p><p><i>- Nếu bạn ăn chay, bạn có thể cần bổ sung canxi, vitamin D, omega 3 và B12.</i></p><p><i>- Nếu bạn đang mang thai, bạn có thể cần bổ sung axit folic và vitamin D.</i></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/san-pham-bo-sung-6-1507543036038.jpg\" id=\"img_3dcf2af0-acd8-11e7-aeea-a394e698d7aa\" w=\"693\" h=\"320\" alt=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 2.\" title=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 2.\" rel=\"lightbox\" photoid=\"3dcf2af0-acd8-11e7-aeea-a394e698d7aa\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/san-pham-bo-sung-6-1507543036038.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Chỉ bổ sung sản phẩm bổ sung dinh dưỡng trong một số trường hợp cần thiết.</p></div></div><p>Tuy nhiên, không phải ai cũng biết bổ sung nhóm dinh dưỡng này đúng cách. Thực tế, dù là sản phẩm có giá trị tốt cho sức khỏe nhưng dùng sản phẩm bổ sung dinh dưỡng sai cách cũng có thể gây ra những tác hại đến không ngờ. Nếu bạn cũng có ý định dùng sản phẩm bổ sung thì cần lưu ý tránh những sai lầm sau đây nhé:</p><p><b>1. Dùng gấp đôi liều lượng</b></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/san-pham-bo-sung-4-1507543036041.jpg\" id=\"img_3dae8390-acd8-11e7-ad77-9f411bebcc1b\" w=\"500\" h=\"699\" alt=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 3.\" title=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 3.\" rel=\"lightbox\" photoid=\"3dae8390-acd8-11e7-ad77-9f411bebcc1b\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/san-pham-bo-sung-4-1507543036041.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Không phải cứ dùng nhiều sản phẩm bổ sung dinh dưỡng là càng tốt.</p></div></div><p>Các báo cáo gần đây về sự nguy hiểm của việc dùng quá liều vitamin dạng kẹo đã làm nổi bật lên mối nguy hiểm thực sự của những sản phẩm được coi là tốt cho sức khỏe. Vậy mối nguy hiểm là gì?</p><p>Robert Hobson, một chuyên gia về dinh dưỡng cho Healthspan, cho biết: \"Không có lợi ích gì khi dùng thực phẩm bổ sung với liều lượng lớn vì lợi ích của chúng không tăng theo cấp số nhân mà thậm chí có khi còn gây hại. Ví dụ, Bổ sung quá nhiều magiê, kẽm hoặc vitamin C có thể gây buồn nôn và khó chịu trong dạ dày; Uống quá nhiều sắt có thể gây táo bón nghiêm trọng. Tồi tệ hơn, bổ sung quá nhiều magiê có thể gây loạn nhịp tim, quá liều vitamin B có thể dẫn đến tổn thương thần kinh và thừa canxi có thể dẫn đến sỏi thận\".</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/san-pham-bo-sung-1-1507542949873.jpg\" id=\"img_0967f210-acd8-11e7-94d9-7f221b28d2a0\" w=\"600\" h=\"171\" alt=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 4.\" title=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 4.\" rel=\"lightbox\" photoid=\"0967f210-acd8-11e7-94d9-7f221b28d2a0\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/san-pham-bo-sung-1-1507542949873.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><b>2. Kết hợp sai cách</b></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/san-pham-bo-sung-5-1507543036040.jpg\" id=\"img_3dbe6210-acd8-11e7-a047-73f8081241a6\" w=\"717\" h=\"487\" alt=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 5.\" title=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 5.\" rel=\"lightbox\" photoid=\"3dbe6210-acd8-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/san-pham-bo-sung-5-1507543036040.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Một số thực phẩm bổ sung có thể kết hợp tốt với nhau, nhưng một số khác thì không.</p></div></div><p>Nếu bạn đang dùng vitamin tổng hợp và khoáng chất, hãy chọn một loại phù hợp cho lứa tuổi của bạn. Vì nhu cầu của mỗi người là khác nhau, thậm chí ở từng giai đoạn tuổi tác nhu cầu của bạn cũng thay đổi, vì vậy cần hết sức lưu ý khi dùng bất kì sản phẩm bổ sung nào.</p><p>Tiến sĩ Sarah Brewer, bác sỹ gia đình quan tâm tới lợi ích đặc biệt về sức khoẻ tự nhiên nói: \"Một số cách kết hợp các vitamin có thể mang lại tác dụng tốt nhưng cũng có cách kết hợp lại sai. Ví dụ dùng đồng thời như sắt và vitamin C giúp tăng sự hấp thụ. Nhưng nếu uống canxi hoặc kẽm với sắt có thể làm giảm sự hấp thu sắt, đặc biệt có liên quan nếu bạn đang cố gắng để chống thiếu máu\".</p><p>Bạn cần phải nhận thức được bất kỳ rủi ro nào liên quan đến việc bổ sung một số chất bổ sung nhất định vì vậy hãy kiểm tra với bác sĩ để được tư vấn thêm trước khi dùng.</p><p><b>3. Uống không đúng thời gian</b><br></p><p>Tiến sĩ Brewer nói: \"Bạn cần tạo thói quen uống vào cùng một thời điểm trong ngày là sẽ giảm khả năng bị quên uống. Ngoài ra, đặt lời nhắc trên đồng hồ, điện thoại di động hoặc máy tính cũng là một cách nhắc nhở rất tốt\".</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/san-pham-bo-sung-2-1507542949871.jpg\" id=\"img_097649f0-acd8-11e7-aeea-a394e698d7aa\" w=\"600\" h=\"171\" alt=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 6.\" title=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 6.\" rel=\"lightbox\" photoid=\"097649f0-acd8-11e7-aeea-a394e698d7aa\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/san-pham-bo-sung-2-1507542949871.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>\"Hầu hết các <b>sản phẩm bổ sung dinh dưỡng</b> được đều có thể dùng ngay sau bữa ăn và uống với nước lọc hoặc nước cam để tối đa sự hấp thụ. Tuy nhiên, bạn cũng cần kiểm tra trên nhãn sản phẩm. Nếu uống 2 hoặc nhiều loại hơn thì có thể tách chúng ra uống\", bác sĩ Brewer khuyên.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/san-pham-bo-sung-7-1507543036037.jpg\" id=\"img_3df10ad0-acd8-11e7-955a-b919c2eefcf8\" w=\"724\" h=\"483\" alt=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 7.\" title=\"3 sai lầm khi dùng sản phẩm bổ sung dinh dưỡng mà ngay cả phụ nữ thông minh cũng có thể mắc phải - Ảnh 7.\" rel=\"lightbox\" photoid=\"3df10ad0-acd8-11e7-955a-b919c2eefcf8\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/san-pham-bo-sung-7-1507543036037.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Hầu hết các sản phẩm bổ sung dinh dưỡng được đều có thể dùng ngay sau bữa ăn.</p></div></div><p>Robert Hobson đã biên soạn một danh sách các ví dụ về thời gian tốt nhất để dùng chất bổ sung như sau:</p><p><i>Probiotics: </i>Được dùng tốt nhất vào buổi sáng vào bữa sáng vì đây là thời điểm mà vi khuẩn có cơ hội sống sót tốt nhất thông qua điều kiện axit trong ruột. Tránh dùng đồ uống nóng vì sẽ làm tiêu tan vi khuẩn.</p><p><i>Vitamin C:</i> Vì vitamin C không kéo dài trong máu nên hãy thử dùng liều phân chia trong ngày.</p><p><i>Vitamin B:</i> Vitamin B chuyển hóa thực phẩm thành năng lượng vì vậy hãy dùng vào buổi sáng để chúng có thể \"làm việc\" ngay từ bữa ăn sáng.</p><p><i>Kẽm: </i>Bổ sung kẽm có thể khiến bạn cảm thấy buồn nôn nên hãy dùng cùng với bữa ăn. Nếu bạn bỏ bữa sáng thì hãy dùng cùng với bữa trưa.</p><p><i>Canxi: </i>Canxi được sử dụng tốt nhất vào ban đêm vì đây là cách tốt nhất mà cơ thể chúng ta sử dụng nó.</p><p style=\"text-align: right;\"><i>Theo Thehealthis</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'D&#249; c&#243; gi&#225; trị tốt cho sức khỏe nhưng d&#249;ng sản phẩm bổ sung dinh dưỡng sai c&#225;ch cũng c&#243; thể g&#226;y ra những t&#225;c hại đến kh&#244;ng ngờ. ', 'publish', 15, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `description`, `status`, `pos`, `seo_title`, `seo_keyword`, `seo_description`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(16, ' Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai ', 'de-giam-can-khong-tang-can-hay-giu-dang-hay-hoc-7-dieu-nay-tu-nhung-phu-nu-co-than-hinh-manh-mai', 'http://afamilycdn.com/zoom/100_62/2017/pn-manh-mai-8-1507529407243-14-1-434-673-crop-1507530763106.jpg', 'Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai          <div data-check-position=\"af_detail_body_start\"></div>          <p>Sue Peckham, đồng tác giả của Chương trình \"12 Weeks to Wow’ Weight Loss Programmes\" (Chương trình <a class=\"seo-suggest-link\" href=\"http://afamily.vn/giam-can.html\" target=\"_blank\" title=\"giảm cân\">giảm cân</a> trong 12 tuần), chia sẻ suy nghĩ của mình về bí quyết làm sao để chị em phụ nữ dễ dàng có được vóc dáng mảnh mai một cách tự nhiên.<br></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-7-1507528445938.jpg\" id=\"img_43b68570-acb6-11e7-a065-7937920d296b\" w=\"1280\" h=\"720\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 1.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 1.\" rel=\"lightbox\" photoid=\"43b68570-acb6-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-7-1507528445938.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Sue peckham và đối tác kinh doanh James Holmes.</p></div></div><p>Từ năm 2004, đối tác kinh doanh của tôi là James Holmes và tôi đã tạo ra Chương trình \"12 Weeks to Wow’ Weight Loss Programmes\" giúp mọi người trên toàn thế giới trở nên thon thả hơn một cách tự nhiên. Tôi cũng đã áp dụng theo cách này, giảm tới hơn 22kg và chưa bao giờ tăng cân trở lại.</p><p>Bất cứ ai biết tôi đều biết rằng tôi đã dành nhiều năm để giảm cân nhưng lại không thành công lắm. Sự thật là đôi khi tôi làm khá tốt khi áp dụng một số chế độ ăn uống nghiêm ngặt nhưng sau đó tôi lại tăng cân trở lại, thậm chí còn nặng cân hơn cả lúc trước.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/pn-manh-mai-2-1507528289697.jpg\" id=\"img_e7ae5730-acb5-11e7-8e2d-d3f798772c52\" w=\"600\" h=\"182\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 2.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 2.\" rel=\"lightbox\" photoid=\"e7ae5730-acb5-11e7-8e2d-d3f798772c52\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-2-1507528289697.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Tôi nghĩ rằng James phải có sự kiên nhẫn của một vị thánh. Tôi sẽ vào văn phòng rên rỉ về trọng lượng của mình, rên riết về \"chế độ ăn uống\" và mọi thứ. Không dễ dàng để cảm thấy hạnh phúc và vui vẻ về cuộc sống khi bạn khổ sở về bản thân, phải không nào? Giống như hầu hết những người thừa cân ăn kiêng, tôi nghĩ về thức ăn từ khi tôi thức dậy cho đến khi tôi đi ngủ.</p><p>Cho tới khi nghe một bản bản ghi âm của Tony Robbins, tôi chợt nhận ra mình đang mình đang theo một \"nguyên lý điên loạn\" - cứ làm những điều tương tự nhưng lại mong kết quả khác nhau. Tôi muốn giúp mọi người giảm cân nhưng bản thân lại không thể làm điều đó. Vậy ai sẽ đến với tôi?</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-8-1507529407243.jpg\" id=\"img_80ee4bb0-acb8-11e7-8a08-1bdf9c92da84\" w=\"784\" h=\"450\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 3.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 3.\" rel=\"lightbox\" photoid=\"80ee4bb0-acb8-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-8-1507529407243.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>James và tôi quyết định thiết lập một chương trình mà không chỉ giúp tôi giảm cân và mà còn mong muốn có thể dạy cho những người khác và giúp họ giảm cân một cách tự nhiên nhất. James có niềm tin rằng tôi có thể làm điều đó, nhờ vậy đã thúc đẩy và hỗ trợ tôi tạo ra sự khác biệt.</p><p>Chúng tôi cũng nhận thấy rằng thuật ngữ \"thừa cân\" đã được sử dụng rất tiêu cực, và chúng tôi biết rằng thay đổi thuật ngữ này cũng là cách cơ bản để thay đổi mối quan hệ với thực phẩm.<br></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/pn-manh-mai-3-1507528289695.jpg\" id=\"img_e7dc4500-acb5-11e7-a89b-d53b14af2463\" w=\"600\" h=\"182\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 4.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 4.\" rel=\"lightbox\" photoid=\"e7dc4500-acb5-11e7-a89b-d53b14af2463\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-3-1507528289695.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Trong 9,5 tháng tiếp theo, chúng tôi bắt đầu sao chép những gì mà những người có vóc dáng mảnh mai. Tôi đồng thời cũng tự thay đổi bản thân để rồi nhận được kết quả thật mĩ mãn: Giảm tới 22kg và 4 size quần áo. Cho tới nay là 13 năm, tôi vẫn giữ được vóc dáng, cân nặng như vậy. Và bây giờ là lúc tôi chia sẻ những thói quen thay đổi cuộc sống của tôi.<br></p><p><i style=\"\">7 điều nữ phụ nữ có <a class=\"seo-suggest-link\" href=\"http://afamily.vn/than-hinh-manh-mai.html\" target=\"_blank\" title=\"thân hình mảnh mai\">thân hình mảnh mai</a> tự nhiên vẫn hay làm</i></p><p><b>1. Phụ nữ tự nhiên tự nhiên biết và hiểu rằng ăn kiêng không có tác dụng trong một thời gian dài</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-9-1507529687873.jpg\" id=\"img_28d69c10-acb9-11e7-8e2d-d3f798772c52\" w=\"724\" h=\"483\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 5.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 5.\" rel=\"lightbox\" photoid=\"28d69c10-acb9-11e7-8e2d-d3f798772c52\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-9-1507529687873.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Ngay cả những công ty giảm cân hàng đầu cũng thừa nhận rằng các nghiên cứu cho thấy khoảng 85% những người giảm cân theo chế độ ăn kiêng sẽ tăng cân trở lại một chút. <br></p><p>Đương nhiên những phụ nữ mảnh mai không ăn kiêng và họ tự cho phép mình loại bỏ bất cứ thứ gì gắn mác \"chế độ ăn kiêng giảm cân\". Nếu bạn muốn giảm cân và trở thành một người phụ nữ tự nhiên, bạn phải ngừng làm những gì mà người béo làm và bắt đầu làm những gì mà người béo phì, khỏe khoắn và khỏe mạnh làm được. Điều đó có nghĩa là \"không ăn kiêng\"!</p><p><b>2. Phụ nữ mảnh mai tự nhiên tin tưởng vào việc ăn theo trực giác</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-10-1507529877398.jpg\" id=\"img_992d4ea0-acb9-11e7-955a-b919c2eefcf8\" w=\"714\" h=\"489\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 6.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 6.\" rel=\"lightbox\" photoid=\"992d4ea0-acb9-11e7-955a-b919c2eefcf8\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-10-1507529877398.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Tất cả các chế độ ăn kiêng mà bạn đã làm trong quá khứ đã được \"lập trình\" mà bạn chỉ việc làm theo. Nhưng những phụ nữ bản năng biết phải ăn gì và ăn bao nhiêu, vì vậy, hãy thoát khỏi tất cả các cuốn sách ăn uống và tạp chí hướng dẫn ăn kiêng.</p><p>Kể từ ngày hôm nay, hãy bắt đầu chú ý những người khỏe mạnh tự nhiên, và cư xử giống như cách họ làm. </p><p><b>3. Phụ nữ có trọng lượng khỏe mạnh thường không dựa vào ý kiến của người khác về chuyện nên ăn gì, khi nào ăn...</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-11-1507530021520.jpg\" id=\"img_ef56f470-acb9-11e7-9564-854b3c797542\" w=\"724\" h=\"483\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 7.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 7.\" rel=\"lightbox\" photoid=\"ef56f470-acb9-11e7-9564-854b3c797542\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-11-1507530021520.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Ăn kiêng là một cách ăn uống tiêu cực và hạn chế khiến bạn cảm thấy không thể kiểm soát được mối quan hệ của mình với thực phẩm nếu không có sự giúp đỡ từ bên ngoài. Chính sự giúp đỡ bên ngoài này là thứ kiểm soát tâm trí và khiến bạn không thể giảm cân một cách tự nhiên.</p><p>Tôi có thể hứa với bạn điều này, không ai ngoài bạn thực sự biết bạn cần ăn bao nhiêu thực phẩm vào một thời điểm nào đó hoặc loại thực phẩm nào phù hợp với bạn.</p><p>Trước tiên, có thể bạn cảm thấy rất đáng sợ khi phải tự chịu trách nhiệm về quyết định ăn uống của mình. Nhưng tôi sẵn sàng đặt cược rằng bạn sẽ cảm thấy tuyệt vời sau đó và thực sự tự hào về bản thân mình.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/pn-manh-mai-4-1507528289691.jpg\" id=\"img_e7edf840-acb5-11e7-ad77-9f411bebcc1b\" w=\"600\" h=\"182\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 8.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 8.\" rel=\"lightbox\" photoid=\"e7edf840-acb5-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-4-1507528289691.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><b>4. Phụ nữ mảnh mai ăn cho đến khi họ không còn đói nữa</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-12-1507530181946.jpg\" id=\"img_4ea8f680-acba-11e7-81d9-d137edec0f5b\" w=\"724\" h=\"483\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 9.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 9.\" rel=\"lightbox\" photoid=\"4ea8f680-acba-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-12-1507530181946.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Tập trung vào việc giảm cân có thể dẫn tới việc luôn bị ám ảnh về thức ăn và kết quả là bạn chu kì tăng cân đã quay lại. Để bắt đầu thay đổi mối quan hệ của bạn với thực phẩm, bạn cần phải thay đổi trọng tâm từ \"giảm cân\" thành \"trở nên khỏe mạnh\".</p><p>Trước khi bạn ăn bất cứ thứ gì hãy tự hỏi: \"Ăn thức ăn đặc biệt này sẽ giúp tôi trở nên khỏe mạnh hơn không?\" Nếu có thể, hãy ăn thực phẩm đó miễn là bạn đói bụng. Còn nếu câu trả lời là \'không\" thì nên dừng lại.</p><p><b>5. Phụ nữ mảnh mai ăn những gì họ muốn khi thèm</b><br></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-13-1507530326405.jpg\" id=\"img_a473da80-acba-11e7-9564-854b3c797542\" w=\"825\" h=\"423\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 10.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 10.\" rel=\"lightbox\" photoid=\"a473da80-acba-11e7-9564-854b3c797542\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-13-1507530326405.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>\"Chế độ ăn kiêng\" dạy bạn về việc hạn chế lượng calo, hoặc lượng thức ăn. Họ cung cấp cho bạn danh sách thực phẩm \"tốt và xấu\" hoặc danh sách \"thực phẩm cấm\" và \"thực phẩm miễn phí\" - có nghĩa là bạn có thể ăn nhiều như bạn muốn. Điều này có thể dẫn đến sự thèm ăn một số loại thức ăn nhất định. Hậu quả là sẽ có lúc bạn để bản thân được thoải mái thưởng thức những món ăn đó.</p><p>Những hạn chế này có thể dẫn đến sự thiếu hụt chất dinh dưỡng và thiếu thốn cũng như có mối quan hệ không lành mạnh với thức ăn, do đó, hãy hứa với bản thân rằng đừng làm điều đó với bản thân mình nữa. Hãy loại bỏ nỗi sợ hãi rằng \"nếu không có chế độ ăn kiêng thì bạn sẽ không giảm cân\".</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/pn-manh-mai-5-1507528289687.jpg\" id=\"img_e877c1b0-acb5-11e7-ad77-9f411bebcc1b\" w=\"600\" h=\"182\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 11.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 11.\" rel=\"lightbox\" photoid=\"e877c1b0-acb5-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-5-1507528289687.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p><b>6. Phụ nữ có vóc dáng mảnh mai tự nhiên biết lắng nghe cơ thể mình</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-14-1507530482095.jpg\" id=\"img_0195b620-acbb-11e7-955a-b919c2eefcf8\" w=\"724\" h=\"483\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 12.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 12.\" rel=\"lightbox\" photoid=\"0195b620-acbb-11e7-955a-b919c2eefcf8\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-14-1507530482095.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Tự đặt những câu hỏi này nhanh chóng trước khi ăn sẽ giúp bạn cải thiện mối quan hệ với thực phẩm và trở thành một người phụ nữ mảnh mai tự nhiên:</p><p>-<i> Tôi có đói không?</i></p><p><i>- Đây có phải là thứ tôi thực sự muốn ăn không?</i></p><p><i>- Liệu thức ăn này có giúp tôi trở nên gầy và khỏe mạnh hơn?</i></p><p><i>- Tôi sẽ cảm thấy thế nào sau khi ăn xong? Ăn thức ăn này giúp tôi cảm thấy tràn đầy sức sống và thoải mái hay là cơ thể trở nên chậm chạp vì tăng cân?</i></p><p><i>- Tôi có cần ăn ngay bây giờ không?</i></p><p><b>7. Những phụ nữ khỏe mạnh ít khi đứng lên cân</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/pn-manh-mai-15-1507530711432.jpg\" id=\"img_8a256da0-acbb-11e7-ad77-9f411bebcc1b\" w=\"724\" h=\"483\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 13.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 13.\" rel=\"lightbox\" photoid=\"8a256da0-acbb-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-15-1507530711432.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Họ không ám ảnh về cân nặng nên họ không làm điều này. Chỉ cần quần áo của họ phù hợp là họ cảm thấy tốt, và đó là những gì quan trọng nhất.</p><p>Thường xuyên đứng lên cân có thể có tác động tiêu cực do trọng lượng của bạn có thể dao động vì nhiều lý do khác nhau. Mất cân và mất mỡ không giống nhau. Có thể có nhiều lý do tại sao các con số trên bàn cân đi lên và xuống. Nó có thể là do sự lưu giữ chất lỏng trong cơ thể, tạo ra nhiều cơ hay chỉ là do \"đến tháng\"...</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/pn-manh-mai-6-1507528289683.jpg\" id=\"img_e88c0d00-acb5-11e7-81d9-d137edec0f5b\" w=\"600\" h=\"182\" alt=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 14.\" title=\"Để giảm cân, không tăng cân hay giữ dáng, hãy học 7 điều này từ những phụ nữ có thân hình mảnh mai - Ảnh 14.\" rel=\"lightbox\" photoid=\"e88c0d00-acb5-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/pn-manh-mai-6-1507528289683.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Các nghiên cứu cho thấy kiểm soát cân nặng của bạn có thể có ảnh hưởng sâu sắc đến cảm giác, dẫn đến tự ti, xấu hổ. Vì vậy, ngừng làm việc này sẽ tốt hơn, thay vào đó là chăm sóc cho chính mình. Phụ nữ tự nhiên tự chăm sóc bản thân bằng cách ăn uống chất lượng tốt, thực phẩm lành mạnh khi họ đói. Họ chỉ ăn những thực phẩm bị coi là ít lành mạnh trong những dịp đặc biệt nhưng cũng rất hạn chế.</p><div class=\"VCSortableInPreviewMode\" data-back=\"#FFFEC7\" data-border=\"#FF7D00\" style=\"background-color:#FFFBF1;\" id=\"ObjectBoxContent_1507527499373\" type=\"content\"><div placeholder=\"[nhập nội dung]\"><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">Sue peckham là một chuyên gia hàng đầu của Anh về khả năng khai thác sức mạnh của tâm trí để giảm cân. Cô đã giảm hơn 22kg vào năm 2003 bằng cách sử dụng chương trình giảm cân mà cô và đối tác kinh doanh James Holmes tạo ra. Cô cũng giữ được trọng lượng khỏe mạnh cho tới bây giờ. Cô đã giúp hơn nửa triệu người trên toàn cầu vượt qua các vấn đề trọng lượng cơ thể thông qua một loạt các ứng dụng và chương trình của mình.</p><p><!--StartFragment--><!--EndFragment--></p><p style=\"margin: 0px 0px 15px; outline: none; box-sizing: border-box; min-height: 30px; position: relative; color: rgb(60, 60, 60); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">Chương trình giảm cân của Sue và James \'Virtual Gastric Band\' đã được đưa lên chương trình \"How to Lose Weight Wel\" )Làm thế nào để giảm cân) của kênh Channel 4 và câu chuyện thành công về giảm cân của khách hàng thường xuyên được đăng trong các báo và tạp chí quốc gia.</p></div></div><p style=\"text-align: right;\"><i>Tổng hợp Health/12WTW</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Từ năm 2004, Chương tr&#236;nh &quot;12 Weeks to Wow’ Weight Loss Programmes&quot; đ&#227; được tạo ra để gi&#250;p mọi người tr&#234;n to&#224;n thế giới trở n&#234;n thon thả hơn một c&#225;ch tự nhi&#234;n. ', 'publish', 16, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(17, ' 8 “nguyên tắc vàng” mà cựu Tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh ', '8-nguyen-tac-vang-ma-cuu-tong-thong-bill-clinton-da-ap-dung-de-kiem-soat-benh-tim-va-khoe-manh', 'http://afamilycdn.com/zoom/100_62/2017/sai-lam-trong-an-uong-7-1507516418779-0-0-462-740-crop-1507516428986.jpg', '8 “nguyên tắc vàng” mà cựu Tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh          <div data-check-position=\"af_detail_body_start\"></div>          <p style=\"text-align: justify;\">Được biết đến là một chính trị gia nổi tiếng của Hoa Kỳ, vào khoảng những năm 2004-2005, cựu tổng thống Mỹ Bill Clinton lâm vào tình trạng không kiểm soát được cân nặng bởi thói quen ăn vặt rất nhiều trong phòng làm việc. Cơ thể ông từ đó bắt đầu quá khổ, những căn bệnh tim mạch cũng kéo đến thường xuyên. Đỉnh điểm là việc ông phải thực hiện cuộc phẫu thuật tim khẩn cấp để bảo toàn tính mạng vào các năm 2004 và 2010.<br></p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/1-1507127498510.jpg\" id=\"img_12ab16b0-a911-11e7-a065-7937920d296b\" w=\"920\" h=\"537\" alt=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 1.\" title=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 1.\" rel=\"lightbox\" photoid=\"12ab16b0-a911-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/1-1507127498510.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Cựu Tổng thống Mỹ Bill Clinton. (Ảnh: Internet)</p></div></div><p style=\"text-align: justify;\"><b>Giảm cân thần tốc nhờ phương pháp đặc biệt  </b></p><p style=\"text-align: justify;\">Kể từ đó, Bill Clinton bắt đầu thay đổi cách ăn uống nghiêm túc. Ông nói \"không\" với thịt cá, kể cả các sản phẩm từ sữa. Những món ăn \"gối đầu giường\" của ông như hamberger, pizza hay thịt nguội cũng được loại bỏ dần dần, thay thế bằng rau củ, ngũ cốc và trái cây.  </p><p style=\"text-align: justify;\">Clinton cũng chăm rèn luyện thể lực và sức khỏe hơn. Mỗi ngày ông đi bộ khoảng 3-5km. Bất cứ nơi nào có thể, ông đều sải bước để cơ thể đổ mồ hôi. Ông cũng tập tạ và cân bằng cơ thể bằng bóng, song song với chơi golf, môn thể thao yêu thích của ông.  </p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/photo-1-1507127872721.jpg\" id=\"img_9cb0a550-a911-11e7-aaaa-51ac0faef9da\" w=\"660\" h=\"495\" alt=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 2.\" title=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 2.\" rel=\"lightbox\" photoid=\"9cb0a550-a911-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/photo-1-1507127872721.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Nhờ chế độ ăn giàu rau củ, ngũ cốc, trái cây và chăm chỉ tập luyện thể lực, Bill Clinton đã cải thiện đáng kể sức khoẻ của mình.</p></div></div><p style=\"text-align: justify;\">Mỗi ngày, cựu Tổng thống đều đặt chỉ tiêu phải uống một ly nước ép trái cây hỗn hợp không kèm sữa hay hạnh nhân, để vừa đủ protein hoạt động nhưng vẫn không khiến cơ thể thừa chất béo. Thói quen ăn fastfood đã nhanh chóng được loại bỏ hoàn toàn bằng thực đơn chay. Mọi cố gắng của ông được tiếp thêm động lực bằng \"lệnh\" của con gái cưng Chelsea - phải giảm 6.8kg trước khi có mặt trong ngày cưới của cô.<br></p><p style=\"text-align: justify;\">Nhờ áp dụng phương pháp ăn chay kiểu Trung Hoa (The China study) này, chỉ trong một thời gian ngắn, Cựu tổng thống Clinton đã đạt được những kết quả đáng kinh ngạc. Cơ thể ông dần trở lại thon thả, và cho đến ngày cưới của con gái, ông đã giảm đến hơn 11kg, như cân nặng của ông hồi là sinh viên đại học.  </p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/photo-1-1507127927405.jpg\" id=\"img_bd4b5b20-a911-11e7-8a08-1bdf9c92da84\" w=\"660\" h=\"440\" alt=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 3.\" title=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 3.\" rel=\"lightbox\" photoid=\"bd4b5b20-a911-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/photo-1-1507127927405.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Đông đảo người dân học tập phương pháp rèn luyện sức khoẻ từ cuốn sách của T.Colin Campbell và cộng sự.</p></div></div><p style=\"text-align: justify;\">Quý giá hơn khi Clinton cũng cảm thấy trái tim mình khỏe mạnh hơn rất nhiều. Ông khẳng định thậm chí mình đã <a class=\"seo-suggest-link\" href=\"http://afamily.vn/kiem-soat-benh-tim.html\" target=\"_blank\" title=\"kiểm soát bệnh tim\">kiểm soát bệnh tim</a> mạch vành tốt hơn. Ở vào tuổi ngoài 70, độ minh mẫn và tốc độ làm việc của người đàn ông ấy khiến cho những người trẻ phải nể phục.<br></p><p style=\"text-align: justify;\">\"<i>Tôi áp dụng </i><a data-cke-saved-href=\"http://afamily.vn/7-bieu-hien-cho-thay-che-do-an-giam-can-khong-phu-hop-voi-ban-20171001090050914.chn\" href=\"http://afamily.vn/7-bieu-hien-cho-thay-che-do-an-giam-can-khong-phu-hop-voi-ban-20171001090050914.chn\" title=\"chế độ ăn\" target=\"_blank\" style=\"text-align: left;\">chế độ ăn</a><i> này bởi sau khi đưa các thiết bị nong mạch vành vào cơ thể, tôi nhận ra rằng khi mạch máu bị tắc nghẽn thì việc nong rộng mạch máu không làm cho cholesterol ngừng tích tụ. Và tôi không muốn điều đó cứ tiếp diễn</i>\" – Clinton tâm sự.</p><p style=\"text-align: justify;\"><b>8 nguyên tắc về thực phẩm và sức khỏe theo phương pháp rèn luyện sức khỏe của Tiến sĩ T.Colin Campbell và cộng sự</b></p><p style=\"text-align: justify;\">Chế độ ăn uống dựa trên thực vật mà cựu Tổng thống Clinton áp dụng được khởi xướng bởi Tiến sĩ T.Colin Campbell. Sau hơn 20 năm nghiên cứu, ông cùng cộng sự đã cho ra đời cuốn sách Bí mật dinh dưỡng cho sức khỏe toàn diện (The China Study) – được coi như công trình thế kỷ về sức khỏe.  </p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/photo-1-1507127889062.jpg\" id=\"img_a6a1f6e0-a911-11e7-a89b-d53b14af2463\" w=\"660\" h=\"440\" alt=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 4.\" title=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 4.\" rel=\"lightbox\" photoid=\"a6a1f6e0-a911-11e7-a89b-d53b14af2463\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/photo-1-1507127889062.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">The China Study được xem như một nghiên cứu thế kỷ về sức khoẻ.</p></div></div><p style=\"text-align: justify;\">Sự thành công của cựu Tổng thống Clinton nói riêng và 82% số người áp dụng phương pháp này kể từ năm 1986 nói chung đang dần khẳng định tính đúng đắn, hợp lý về mặt dinh dưỡng và sức khỏe mà công trình mang lại.<br></p><p style=\"text-align: justify;\">Một lối sống lành mạnh luôn mang đến nhiều lợi ích, chẳng hạn như: Tăng tuổi thọ, trẻ trung, giảm cân, có thêm nhiều năng lượng, giữ cho xương chắc khỏe, giảm thiểu và ngăn ngừa nhiều bệnh tật (như tim mạnh, đột quỵ, sỏi thận, ung thư…). Những lợi ích này, bạn hoàn toàn có thể đạt được, bằng cách thay đổi chế độ ăn hằng ngày.  </p><p style=\"text-align: justify;\">Trên hành trình đạt đến một thể trạng khỏe mạnh như cựu Tổng thống Clinton, bạn cần ghi nhớ 8 nguyên tắc quan trọng. Chúng sẽ cho các bạn biết cách ứng dụng khoa học, cách chữa bệnh, cách ăn uống sinh hoạt, cách quan tâm đúng mực đến sức khỏe cũng như cách nhìn nhận thế giới.<br></p><p style=\"text-align: justify;\"> <i style=\"\">Dinh dưỡng là đại diện cho sự kết hợp của vô số chất trong thức ăn. Vì thế, ăn toàn phần thì tốt hơn tất cả  </i></p><p style=\"text-align: justify;\">Cơ thể sẽ thu nạp các chất hoá học từ những thực phẩm mà bạn ăn vào để tạo ra sức khỏe tốt cho chính bạn. Thông qua sự kiểm soát vô cùng phức tạp tại các tế bào ở khắp cơ thể, chúng sẽ được chỉ định đi đâu, với lượng bao nhiêu và khi nào tham gia. Đây là sự vận hành tự nhiên của hệ tiêu hoá nói riêng và mọi cơ quan trong cơ thể nói chung.  </p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/sai-lam-trong-an-uong-4-1507516568132.jpg\" id=\"img_9ba4cf60-ac9a-11e7-8a08-1bdf9c92da84\" w=\"660\" h=\"434\" alt=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 5.\" title=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 5.\" rel=\"lightbox\" photoid=\"9ba4cf60-ac9a-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/sai-lam-trong-an-uong-4-1507516568132.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\">Cơ thể sẽ thu nạp các chất hoá học từ những thực phẩm mà bạn ăn vào để tạo ra sức khỏe tốt cho chính bạn.</p></div></div><p style=\"text-align: justify;\"> <i>Thực phẩm chức năng không phải thần dược chữa bách bệnh  </i></p><p style=\"text-align: justify;\">Thực phẩm chức năng có thể chứa vitamin, khoáng chất, cũng có thể là thảo dược hoặc một số loại thực vật khác, cũng có thể là các axit amin hoặc các chất cô đặc, các chất chuyển hoá, chiết xuất…  </p><p style=\"text-align: justify;\">Không nói rằng những chất dinh dưỡng này là không cân thiết, tuy nhiên trong cơ thể con người, các dưỡng chất hoạt động như một hệ thống sinh hoá vô cùng phức tạp. Việc lấy ra chỉ một vài chất dinh dưỡng đơn lẻ để thay thế cho thực phẩm toàn phần hầu như chẳng có ý nghĩa chút nào. Thực phẩm chức năng không mang đến sức khỏe lâu dài, chưa thể hoàn toàn có thể gây ra các phản ứng phụ. Do đó, không thể xem thự phẩm chức năng như thần dược giúp xó bỏ mọi nguy cơ gây ra do chế độ dinh dưỡng không lành mạnh.  </p><p style=\"text-align: justify;\"> <i>Không có chuyện chất dinh dưỡng có trong thực phẩm nguồn gốc động vật thì tốt hơn thực vật  </i></p><p style=\"text-align: justify;\">Thực phẩm từ thực vật có hàm lượng chất chống oxy hoá, chất xơ, vitamin và khoáng chất nhiều hơn rất nhiều so với thực phẩm từ động vật. Bù lại, thực phẩm từ động vật có nhiều cholesterol, chất béo, protein hơn so với thực phẩm từ thực vật.  </p><p style=\"text-align: justify;\">Tuy nhiên, chất béo và protein trong các loại hạt (thực vật) lại tốt hơn trong động vật.  </p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/photo-1-1507127859229.jpg\" id=\"img_969e1490-a911-11e7-81d9-d137edec0f5b\" w=\"660\" h=\"440\" alt=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 6.\" title=\"8 “nguyên tắc vàng” mà cựu tổng thống Bill Clinton đã áp dụng để kiểm soát bệnh tim và khỏe mạnh - Ảnh 6.\" rel=\"lightbox\" photoid=\"969e1490-a911-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/photo-1-1507127859229.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Thực phẩm nguồn gốc động vật không tốt hơn so với thực vật.</p></div></div><p style=\"text-align: justify;\"><i style=\"\">Gen không chịu trách nhiệm cho bệnh tật</i><br></p><p style=\"text-align: justify;\">Gen chỉ thể hiện tính trạng trội khi nhận định được kích hoạt, và dinh dưỡng mới là yếu tố quan trọng giúp xác định xem gen nào tốt, xấu, trội hay lặn.  </p><p style=\"text-align: justify;\"> <i style=\"\">Dinh dưỡng có thể kiểm soát đáng kể các tác hại của hoá chất độc hại  </i></p><p style=\"text-align: justify;\">Dinh dưỡng là tác nhân chính quyết định xem bệnh tật có thể làm tổn hại đến bạn không. Lấy ví dụ, bạn không thể kết luận cơ thể khỏe mạnh lên bằng cách ăn thịt bò hữu cơ hay bơm đầy hoá chất. Bởi cả hai loại thịt bò đều có cơ cấu thành phần dinh dưỡng tương tự nhau.  </p><p style=\"text-align: justify;\"> <i>Cùng một dưỡng chất giúp ngăn ngừa bệnh ở giai đoạn đầu cũng có thể giúp ngăn chặn hoặc đẩy lùi căn bệnh ở giai đoạn sau  </i></p><p style=\"text-align: justify;\">Dinh dưỡng đúng cách có thể làm suy yếu hay làm chậm phát triển một vài bệnh tự miễn. Suy cho cùng, phòng bệnh hơn chữa bệnh. Càng sớm ăn thực phẩm tốt càng sớm có sức khỏe tốt.  </p><p style=\"text-align: justify;\"> <i>Chế độ dinh dưỡng có ích cho người mắc bệnh mạn tính là chế độ giúp hỗ trợ sức khỏe toàn diện  </i></p><p style=\"text-align: justify;\">Một chất dinh dưỡng tốt sẽ giúp tăng cuờng sức khỏe, ngăn ngừa và giải quyết toàn diện các vấn đề bệnh tật. Bạn có thể tăng cường đến mức tối đa sức khỏe toàn diện của bản thân trong công cuộc phòng ngừa và chống lại bất kỳ căn bệnh nào, tất cả chỉ bằng chế độ ăn đơn giản.  </p><p style=\"text-align: justify;\"> <i style=\"\">Dưỡng chất tốt tạo ra sức khỏe cho mọi hoạt động. Tất cả hoạt động đều được kết nối với nhau  </i></p><p style=\"text-align: justify;\">Việc chúng ta lựa chọn thực phẩm có những tác động kinh ngạc đến sự trao đổi chất của cơ thể trong nhiều phương diện như năng lượng, hoạt động thể chất, tình cảm và tinh thần thoải mái, với cả môi trường sống trên toàn thế giới, thậm chí là quá trình kìm hãm và đẩy lùi bệnh tật.  </p><p style=\"text-align: justify;\">Tất cả phương diện trên tưởng là tách biệt, song thực chất chúng luôn kết nối và liên quan chặt chẽ lẫn nhau.</p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Cựu Tổng thống Mỹ tiết lộ nhờ &#225;p dụng những nguy&#234;n tắc n&#224;y c&#249;ng với chế độ ăn hợp l&#253;, &#244;ng đ&#227; giảm đến 11kg trong v&#224;i th&#225;ng v&#224; kiểm so&#225;t bệnh tim mạch v&#224;nh của m&#236;nh tốt hơn. ', 'publish', 17, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(18, ' Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ ', 'gio-mua-sap-ve-roi-hay-trang-bi-ngay-cach-lam-nhung-loai-do-uong-chua-benh-cam-lanh-ngay-tu-bay-gio', 'http://afamilycdn.com/zoom/100_62/2017/do-uong-chua-benh-cam-lanh2-1507199620650-68-0-442-600-crop-1507199741755.jpg', 'Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ          <div data-check-position=\"af_detail_body_start\"></div>          <p>Khi những cơn gió mùa ghé thăm cũng là thời điểm bạn dễ dàng bị cảm lạnh nhất. Bệnh cảm cúm thông thường nghe có vẻ không là gì nhưng thực tế có thể tấn công nhiều người hơn bất cứ căn bệnh nào khác trên thế giới.<br></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/do-uong-chua-benh-cam-lanh1-1507199620648.jpg\" id=\"img_a7774e60-a9b8-11e7-a065-7937920d296b\" w=\"810\" h=\"540\" alt=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 1.\" title=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 1.\" rel=\"lightbox\" photoid=\"a7774e60-a9b8-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh1-1507199620648.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Khi những cơn gió mùa ghé thăm cũng là thời điểm bạn dễ dàng bị cảm lạnh nhất.</p></div></div><p>Vì vậy nếu bạn đang lo lắng mình cũng như các thành viên trong gia đình bị cảm lạnh, cảm cúm do thay đổi thời tiết, hãy trang bị ngay 5 biện pháp khắc phục tự nhiên được bào chế thành những loại <a class=\"seo-suggest-link\" href=\"http://afamily.vn/do-uong-chua-benh.html\" target=\"_blank\" title=\"đồ uống chữa bệnh\">đồ uống chữa bệnh</a> sau. Đặc biệt nếu bạn không muốn mình rơi vào trường hợp lạm dụng kháng sinh, \"đổ thêm dầu vào lửa\" vào tình trạng kháng kháng sinh như hiện nay thì đừng lười biếng nữa! Chỉ cần vài phút với những nguyên liệu sẵn có trong nhà bếp, bạn đã pha chế được <a class=\"seo-suggest-link\" href=\"http://afamily.vn/do-uong-chua-benh-cam-lanh.html\" target=\"_blank\" title=\"đồ uống chữa bệnh cảm lạnh\">đồ uống chữa bệnh cảm lạnh</a>, cảm cúm siêu dễ mà cực hiệu quả rồi:</p><h2>Trà gừng nóng</h2><p>Một tách trà gừng nóng nhâm nhi cùng bánh munchies chiên cho ngày mưa được coi là sự kết hợp quá ư hoàn hảo. Gừng – gia vị quen thuộc có ngay trong bếp nhà bạn sẽ phát huy hiệu quả ngăn chặn những cơn cảm lạnh hơn bất cứ liều thuốc kháng sinh nào. Bí mật nằm ở khả năng chống viêm của gừng, nhờ đó chúng sẽ loại bỏ chất lỏng dư thừa khỏi cổ họng do bị nhiễm lạnh. Thêm nữa, một tách trà nóng rất tốt để làm dịu cơn đau nhức.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh2-1507199620650.jpg\" id=\"img_a785cd50-a9b8-11e7-a047-73f8081241a6\" w=\"600\" h=\"450\" alt=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 2.\" title=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 2.\" rel=\"lightbox\" photoid=\"a785cd50-a9b8-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh2-1507199620650.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Gừng – gia vị quen thuộc có ngay trong bếp nhà bạn sẽ phát huy hiệu quả ngăn chặn những cơn cảm lạnh hơn bất cứ liều thuốc kháng sinh nào.</p></div></div><p>Theo <a class=\"seo-suggest-link\" href=\"http://afamily.vn/luong-y-bui-hong-minh.html\" target=\"_blank\" title=\"lương y bùi hồng minh\">lương y Bùi Hồng Minh</a> (Phó Chủ tịch Hội Đông y Ba Đình, Hà Nội), trong Đông y, gừng có vị cay, tính ấm, thơm, gừng khô có vị cay, tính nóng, thơm hắc, có thể chữa cảm mạo, phong hàn, ngạt mũi, nhức đầu, đau đầu, nôn mửa, kích thích tiêu hóa, đầy bụng, trướng bụng, giải độc từ cua cá, chim thú nếu có độc, các loại thịt (trừ thịt lợn vì gừng kỵ thịt lợn) khi ăn vào cơ thể.</p><p>Chỉ cần thêm 2 muỗng cà phê gừng và pha nước nóng vào, đợi khoảng vài phút là bạn đã sẵn sàng có thức uống ấm nóng, có tác dụng chữa bệnh cảm lạnh hiệu quả.</p><p><b>Ly nước ấm mật ong nguyên chất</b></p><p>Không phải ngẫu nhiên mà mật ong cũng được gọi là vàng lỏng. Với tính chất kháng khuẩn, kháng virus và giàu chất chống oxy hóa, mật ong nguyên chất là phương thuốc tự nhiên chữa được nhiều bệnh vô cùng hiệu quả. Và chắc chắn, mật ong nguyên chất có thể chế biến thành đồ uống chữa bệnh cảm lạnh thông thường cực tốt. Tất cả những gì bạn cần là một thìa mật ong nguyên chất pha trong 1 ly nước ấm hoặc sữa ấm. Sau đó, bạn uống thức uống thơm ngon này, hoặc ngậm thêm trong họng một chút. Điều này sẽ giúp giảm đau họng, làm dịu mát cổ họng cực tốt.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh3-1507199620651.jpg\" id=\"img_a7966f20-a9b8-11e7-a065-7937920d296b\" w=\"600\" h=\"450\" alt=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 3.\" title=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 3.\" rel=\"lightbox\" photoid=\"a7966f20-a9b8-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh3-1507199620651.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Với tính chất kháng khuẩn, kháng virus và giàu chất chống oxy hóa, mật ong nguyên chất là phương thuốc tự nhiên chữa được nhiều bệnh vô cùng hiệu quả.</p></div></div><p>Theo lương y Bùi Hồng Minh, mật ong chứa 60-70% là glucose, ngoài ra còn có sacharose, muối vô cơ, axit hữu cơ, men tiêu hóa, chất béo… Mật ong vị ngọt, tính bình, vào 5 kinh: tâm, tì, phế, vị, đại tràng. Đây là một loại thuốc bổ, giảm mật độ axit của dịch vị, làm axit dạ dày hoạt động bình thường. \"Nó có thể chữa được nhiều bệnh như viêm loét dạ dày, hành tá tràng, viêm gan, viêm túi mật, diệt vi trùng, rất tốt cho người gặp các triệu chứng của cảm lạnh thông thường\", lương y Bùi Hồng Minh nói.</p><p><b><a class=\"seo-suggest-link\" href=\"http://afamily.vn/nuoc-muoi.html\" target=\"_blank\" title=\"nước muối\">Nước muối</a></b><br></p><p>Muối là một chất chống viêm mạnh mẽ, do đó, đây chính là phương pháp điều trị hoàn hảo chứng đau họng. Chỉ cần trộn 1-2 thìa cà phê muối vào nước và nhẹ nhàng súc miệng bằng nước này ít nhất 30 giây mỗi lần. Lặp lại 3 - 4 lần mỗi ngày và bạn sẽ nhận thấy sự cải thiện đáng kể.</p><p>Theo lương y Bùi Hồng Minh, trong Đông y, muối biển thô hay còn gọi là muối chưa qua tinh chế rất giàu khoáng chất và có những đặc tính chữa bệnh nhất định. Trong đó, nước muối được đánh giá có tác dụng sát trùng rất tốt, vì vậy nó là bài thuốc chữa viêm họng hiệu quả.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh5-1507199620653.jpg\" id=\"img_a7b2d0c0-a9b8-11e7-a047-73f8081241a6\" w=\"600\" h=\"450\" alt=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 5.\" title=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 5.\" rel=\"lightbox\" photoid=\"a7b2d0c0-a9b8-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh5-1507199620653.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Muối là một chất chống viêm mạnh mẽ, do đó, đây chính là phương pháp điều trị hoàn hảo chứng đau họng.</p></div></div><p><b>Sữa nghệ</b></p><p>Sữa nghệ là một phương thuốc chữa bệnh cổ xưa được biết đến rộng rãi như là thuốc chữa bệnh cho mọi bệnh trên thế giới, được dân gian tin tưởng. Uống một ly sữa ấm, pha chút bột nghệ sẽ làm bạn cảm thấy cơ thể mình chống lạnh tốt hơn.</p><p>Lương y Bùi Hồng Minh đặc biệt nhấn mạnh, nghệ là loại củ được dùng ở nhiều dạng khác nhau có tác dụng phòng chống, chữa trị nhiều bệnh. Theo Đông y, uất kim (những củ nghệ mọc ra xung quanh 1 củ  chính) có vị cay, đắng, hơi ngọt, tính mát, trong khi khương hoàng (củ nghệ to) có vị cay, đắng, hơi ngọt nhưng tính nóng. Uất kim vào gan, kinh tâm, kinh phế có tác dụng hành khí, giải uất; trong khi khương hoàng hành phế, phá huyết, thông kinh.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh6-1507199620654.jpg\" id=\"img_a7c3c0b0-a9b8-11e7-80e3-897aa0f332fe\" w=\"600\" h=\"450\" alt=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 6.\" title=\"Gió mùa sắp về rồi, hãy trang bị ngay cách làm những loại đồ uống chữa bệnh cảm lạnh ngay từ bây giờ - Ảnh 6.\" rel=\"lightbox\" photoid=\"a7c3c0b0-a9b8-11e7-80e3-897aa0f332fe\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/do-uong-chua-benh-cam-lanh6-1507199620654.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Uống một ly sữa ấm, pha chút bột nghệ sẽ làm bạn cảm thấy cơ thể mình chống lạnh tốt hơn.</p></div></div><p>\"Cụ thể, khi khí huyết uất trệ, bụng sườn đau, chảy máu cam, đái ra máu, hôn mê thì ta dùng uất kim. Có thể giã đắp vết thương nếu vết thương chậm, lâu lành. Khương hoàng chữa huyết ứ, bụng đầy, khó thở, cánh tay tê đau, phụ nữ bế kinh, có hòn cục trong bụng (bệnh bôn đồn), sau khi sinh huyết xấu, vết thương do vấp, ngã\", lương y cho biết. Khả năng kháng viêm của nghệ cực mạnh, do đó, không thể thiếu nếu bạn muốn ngăn chặn cảm lạnh.</p><p>Sữa là một loại thuốc bổ, bồi dưỡng cơ thể nhờ những hoạt chất có lợi cho cơ thể như tăng cường năng lượng và trao đổi chất. Do đó, sữa kết hợp nghệ sẽ đem lại đồ uống chữa bệnh cảm lạnh cực hữu hiệu.</p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Điều tuyệt vời hơn nữa l&#224; những loại đồ uống chữa bệnh cảm lạnh, cảm c&#250;m đều l&#224; những nguy&#234;n liệu c&#243; sẵn trong nh&#224; bếp của bạn rồi nh&#233;! ', 'publish', 18, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `description`, `status`, `pos`, `seo_title`, `seo_keyword`, `seo_description`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(19, ' Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này ', 'neu-chua-biet-lam-sao-de-bat-dau-mot-che-do-an-uong-lanh-manh-thi-hay-ap-dung-4-cach-nay', 'http://afamilycdn.com/zoom/100_62/2017/a1-1507349636698-18-0-396-606-crop-1507349760069.jpg', 'Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này          <div data-check-position=\"af_detail_body_start\"></div>          <p>Là một chuyên gia dinh dưỡng, mọi người thường hỏi tôi ăn như thế nào để luôn được khỏe mạnh. Từ khi tôi bắt đầu công việc của mình cách đây 20 năm, tôi đã nhìn thấy rất nhiều xu hướng và mô hình <a class=\"seo-suggest-link\" href=\"http://afamily.vn/an-kieng.html\" target=\"_blank\" title=\"ăn kiêng\">ăn kiêng</a>, một số thực sự rất tốt, nhưng cũng không thiếu những cách vô cùng tồi tệ mà mọi người vẫn thực hiện như một thói quen.<br></p><p>Vì vậy, khi tôi cộng tác với Women’s Health để viết cuốn sách  The Women\'s Health Big Book Of Smoothies &amp; Soups, chúng tôi quyết định tham gia vào các chiến dịch và đưa ra lời khuyên tốt nhất, giúp các bạn có được <a class=\"seo-suggest-link\" href=\"http://afamily.vn/che-do-an-uong-dung-cach.html\" target=\"_blank\" title=\"chế độ ăn uống đúng cách\">chế độ ăn uống đúng cách</a>. </p><p>Dưới đây là 4 lời khuyên hàng đầu của chúng tôi – tất cả được hỗ trợ bởi nghiên cứu thực tế để tối ưu hóa sức khỏe bằng chế độ ăn uống lành mạnh.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/a1-1507349636698.jpg\" id=\"img_f0b4c090-ab15-11e7-81d9-d137edec0f5b\" w=\"606\" h=\"404\" alt=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 1.\" title=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 1.\" rel=\"lightbox\" photoid=\"f0b4c090-ab15-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/a1-1507349636698.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Gần như tất cả các chuyên gia đều nhấn mạnh tầm quan trọng của việc ăn uống thường xuyên để duy trì sự trao đổi chất và năng lượng.</p></div></div><p><b>Ăn thường xuyên</b></p><p>Gần như tất cả các chuyên gia đều nhấn mạnh tầm quan trọng của việc ăn uống thường xuyên để duy trì sự trao đổi chất và năng lượng, tránh việc ăn kiêng trong ngày rồi lại tập trung ăn vào một bữa. </p><p>Cách tiếp cận \"Ba bữa ăn lớn cộng với hai bữa phụ mỗi ngày\" dường như là phương pháp tốt nhất để <a class=\"seo-suggest-link\" href=\"http://afamily.vn/giam-can.html\" target=\"_blank\" title=\"giảm cân\">giảm cân</a> và duy trì cân nặng. Trong một nghiên cứu trên Tạp chí Academy of Nutrition and Dietetics, các nhà nghiên cứu phát hiện ra những người thường xuyên ăn thêm hai bữa nhẹ mỗi ngày thường dễ có được số cân nặng lý tưởng hơn.</p><p>Lợi ích lớn nhất của việc ăn uống thường xuyên đến từ ảnh hưởng của nó đối với mức đường trong máu (glucose) và do đó sản xuất insulin. Khi glucose và insulin cân bằng sẽ làm giảm sự thèm ăn của bạn. Điều này không chỉ giúp bạn giảm đói mà đơn giản giúp bạn cảm thấy thoải mái hơn là phải ăn kiêng dè dặt. </p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/a2-1507349636700.jpg\" id=\"img_f0cfc2a0-ab15-11e7-8dbb-355ae774403f\" w=\"700\" h=\"469\" alt=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 2.\" title=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 2.\" rel=\"lightbox\" photoid=\"f0cfc2a0-ab15-11e7-8dbb-355ae774403f\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/a2-1507349636700.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Lợi ích lớn nhất của việc ăn uống thường xuyên đến từ ảnh hưởng của nó đối với mức đường trong máu (glucose) và do đó sản xuất insulin.</p></div></div><p>Theo kinh nghiệm cá nhân tôi, việc ăn thêm bữa ăn nhỏ trong ngày (thay vì chỉ ăn 3 bữa lớn) giúp tôi có được nguồn năng lượng dồi dào để hoạt động. Một số chuyên gia cho rằng ăn uống với khoảng cách thời gian đều đặn sẽ giúp lưu trữ ít chất béo trong cơ thể hơn, vì cơ thể bạn học được cách thừa nhận rằng năng lượng sẽ được cung cấp thông qua thức ăn trong tương lai gần. </p><p>Và về mặt tâm lý, việc ăn thường xuyên có lẽ là điều mà nhiều người mong muốn bởi nó giúp họ tránh nỗi sợ hãi to lớn - sợ đói.</p><p><b>Không bao giờ bỏ bữa sáng</b></p><p>Bỏ qua bữa sáng, đồng nghĩa với việc bạn sẽ nạp nhiều calo no hơn vào 2 buổi ăn còn lại chỉ đơn giản vì bạn đang đói. Hãy suy nghĩ: Nếu bạn kết thúc bữa ăn tối lúc 7h tối và không ăn một lần nào nữa cho đến trưa ngày hôm sau, nghĩa là bạn đang bắt cơ thể nhịn đói suốt 17 giờ. Bạn nghĩ bạn đang tự giúp mình giảm cân vì cắt giảm hẳn lượng calo của một bữa ăn sáng? </p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/a5-1507349636703.jpg\" id=\"img_f1247250-ab15-11e7-a047-73f8081241a6\" w=\"600\" h=\"400\" alt=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 3.\" title=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 3.\" rel=\"lightbox\" photoid=\"f1247250-ab15-11e7-a047-73f8081241a6\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/a5-1507349636703.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Bỏ qua bữa sáng, đồng nghĩa với việc bạn sẽ nạp nhiều calo no hơn vào 2 buổi ăn còn lại chỉ đơn giản vì bạn đang đói.</p></div></div><p>Thực ra, bạn đang khiến cơ thể lưu trữ nhiều chất béo hơn, bởi cơ thể không biết được khi nào sẽ có dòng năng lượng tiếp theo được nạp vào. Ngoài ra, ăn sáng cũng có liên quan đến lượng đường trong máu và cholesterol thấp hơn, và nếu bỏ qua nó, bạn dễ mắc các bệnh táo bón hay đau kinh.</p><p>Có rất nhiều nghiên cứu về việc nên ăn gì cho buổi sáng, nhưng cuối cùng không ai có thể kết luận được, vì vậy lời khuyên của chúng tôi là hãy ăn một thứ gì đó lành mạnh cho buổi sáng.</p><p><b>Tập trung vào bữa ăn</b></p><p>Bạn không chỉ phải ăn thường xuyên, ăn bữa sáng, bạn còn phải ăn có tập trung. Nếu bạn vừa ăn vừa xem TV, vừa trả lời e-mail, chơi điện tử,… điều này làm bạn ít chú ý đến những gì bạn đang nạp vào cơ thể. </p><p>Trong một nghiên cứu trên Tạp chí Dinh dưỡng Lâm sàng Hoa Kỳ, các nhà nghiên cứu đã phân loại hai nhóm, một nhóm người tham gia ăn một bữa ăn kết hợp chơi điện tử trên máy tính, một nhóm khác ăn mà không có bất cứ phiền nhiễu nào. </p><p>Kết quả, nhóm người chơi điện tử khó có thể nhớ được những gì họ ăn, và họ cảm thấy bữa ăn không đầy đủ dinh dưỡng cho cơ thể mình, và sau đó họ cần ăn thêm bánh cookie nửa giờ sau đó.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/a6-1507349636704.jpg\" id=\"img_f13cdc50-ab15-11e7-84cf-011ebaba2567\" w=\"600\" h=\"409\" alt=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 4.\" title=\"Nếu chưa biết làm sao để bắt đầu một chế độ ăn uống lành mạnh thì hãy áp dụng 4 cách này - Ảnh 4.\" rel=\"lightbox\" photoid=\"f13cdc50-ab15-11e7-84cf-011ebaba2567\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/a6-1507349636704.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Dù bận rộn đến đâu, hãy dành ra 15 phút để ngồi lại, tập trung vào bữa ăn của mình.</p></div></div><p>Dù bận rộn đến đâu, hãy dành ra 15 phút để ngồi lại, tập trung vào bữa ăn của mình. Điều này giúp bạn cảm nhận được hết hương vị của bữa ăn. Các nhà nghiên cứu Nhật Bản cũng phát hiện ra rằng những người ăn nhanh có nguy cơ bị béo phì gấp 3 lần so với những người ăn từ tốn.</p><p><b>Có một ngày không ăn thịt trong tuần</b></p><p>Dành một ngày trong tuần không ăn thịt, ví dụ như thứ Hai không ăn thịt, ăn chay, thuần chay suốt một ngày. Cách tốt nhất để bạn thực hiện là tự coi mình là người ăn thuần chay hầu hết thời gian.</p><p style=\"text-align: right;\"><i>(Nguồn: Womenshealth)</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'Liza Defazio (đến từ Mỹ) l&#224; một chuy&#234;n gia dinh dưỡng với 20 năm kinh nghiệm nghi&#234;n cứu ăn ki&#234;ng sẽ chỉ r&#245; cho bạn chế độ ăn uống l&#224;nh mạnh. ', 'publish', 19, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(20, ' 7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất ', '7-vi-tri-tren-co-the-de-bi-benh-vay-nen-tan-cong-nhat', 'http://afamilycdn.com/zoom/100_62/2017/benh-vay-nen-1-1507437321912-40-0-510-752-crop-1507437645687.jpg', '7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất          <div data-check-position=\"af_detail_body_start\"></div>          <p>Có thể bạn nghĩ rất đơn giản về <a class=\"seo-suggest-link\" href=\"http://afamily.vn/benh-vay-nen.html\" target=\"_blank\" title=\"bệnh vảy nến\"><b>bệnh vảy nến</b></a> và biết mình mắc bệnh khi nhìn thấy biểu hiện của nó. Nhưng bệnh vảy nến không phải lúc nào cũng biểu hiện theo cùng một cách hay tại cùng một vị trí nên có thể khiến bạn không ngờ khi thấy nó xuất hiện.<br></p><p>Khoảng 7,5 triệu người Mỹ bị <i>bệnh vảy nến</i>, theo số liệu thống kê của Viện Da liễu Hoa Kỳ (American Academy of Dermatology). Bệnh nhân phải chịu đựng các đợt bùng phát có thể gây các mảng da tróc vảy, màu đỏ và ngứa ngáy.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/benh-vay-nen-1-1507437321912.jpg\" id=\"img_1b81f5f0-abe2-11e7-b061-cd0b99b6bd76\" w=\"752\" h=\"564\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 1.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 1.\" rel=\"lightbox\" photoid=\"1b81f5f0-abe2-11e7-b061-cd0b99b6bd76\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-1-1507437321912.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">Bệnh tự miễn có thể xuất hiện tại bất kì vị trí nào trên cơ thể...</p></div></div><p>Tiến sĩ Michael Siegel, phó chủ tịch chương trình nghiên cứu tại Quỹ Bệnh vảy nến Quốc gia, cho biết: \"Vảy nến có thể xuất hiện ở bất cứ đâu - trên mi mắt, tai, miệng, môi, các nếp gấp da, bàn tay, bàn chân và móng. Da ở mỗi vùng trên không giống nhau và do đó, bạn cần biện pháp điều trị khác nhau\".</p><p>Bác sĩ Cynthia Bailey, chủ tịch kiêm giám đốc điều hành Advanced Skin Care and Dermatology, cho biết thêm: \"Ảnh hưởng của <i>bệnh vảy nến</i> thể hiện từ chỗ gây khó chịu đơn thuần tới mức thay đổi cả cuộc sống bệnh nhân, tuỳ thuộc vào vị trí và phạm vi bệnh\". Còn bác sĩ Doris Day ở New York kể: \"Tôi biết những cô gái trẻ chỉ bị vài mảng da tróc trên chân nhưng sẽ không mặc quần soóc hoặc váy ngắn vì cảm giác xấu hổ. Họ nói người ta sẽ nghĩ họ đang mang một căn bệnh dễ lây\".</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/thumb_w/660/2017/benh-vay-nen-8-1507437321901.jpg\" id=\"img_1cf5f850-abe2-11e7-81d9-d137edec0f5b\" w=\"722\" h=\"406\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 2.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 2.\" rel=\"lightbox\" photoid=\"1cf5f850-abe2-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-8-1507437321901.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"\">... nhưng có những vị trí dễ xuất hiện bệnh hơn cả.</p></div></div><p>Chính xác thì <b>bệnh vảy nến</b> không lây. Nó do sự kết hợp giữa các yếu tố gen và môi trường như stress, cháy nắng, dị ứng, chế độ ăn, một số loại thuốc nhất định (bao gồm lithium và thuốc tim quinidine), thậm chí cả thời tiết, gây ra.</p><p>Có nhiều dạng vảy nến khác nhau nhưng bệnh thường xuất hiện nhiều nhất ở 7 vị trí sau:</p><p><b>1. Khuỷu tay và đầu gối</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-2-1507437321913.jpg\" id=\"img_1b9e3080-abe2-11e7-aaaa-51ac0faef9da\" w=\"640\" h=\"479\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 3.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 3.\" rel=\"lightbox\" photoid=\"1b9e3080-abe2-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-2-1507437321913.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Vị trí dễ bị vảy nến tấn công nhất là khuỷu tay và đầu gối. Đó thường là vảy nến mảng - một dạng bệnh làm xuất hiện những mảng da đỏ, hơi phồng, được bao phủ bằng lớp tế bào da chết màu trắng bạc. Bác sĩ Siegel cho biết, chúng thường gây đau và ngứa, đồng thời có thể bị nứt vỡ ra và chảy máu.</p><p><b>2. Da đầu</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-3-1507437321910.jpg\" id=\"img_1bc1be10-abe2-11e7-ad77-9f411bebcc1b\" w=\"640\" h=\"480\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 4.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 4.\" rel=\"lightbox\" photoid=\"1bc1be10-abe2-11e7-ad77-9f411bebcc1b\" type=\"photo\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-3-1507437321910.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Vảy nến mảng cũng thường tấn công vị trí này trên cơ thể. Nó có thể biểu hiện ở nhiều cấp độ như tình trạng da tróc vảy nhẹ tới mức các mảng da dày, cứng che phủ toàn bộ da đầu. Bệnh có thể còn vượt qua vùng tóc để lan xuống trán, lưng, cổ và xung quanh tai.</p><p><b>3. Trên mặt</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-4-1507437321908.jpg\" id=\"img_1be069a0-abe2-11e7-81d9-d137edec0f5b\" w=\"642\" h=\"361\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 5.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 5.\" rel=\"lightbox\" photoid=\"1be069a0-abe2-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-4-1507437321908.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Việc bệnh vảy nến xuất hiện ở mặt không thường gặp nhưng nó vẫn có thể xảy ra. Dạng vảy nến này thường tác động tới lông mày người mắc, vùng da giữa mũi và môi trên, trán và vùng chân tóc nối giữa trán và da đầu. Do những vị trí này, làn da rất nhạy cảm nên người bệnh cần được điều trị thật cẩn thận.</p><p><b>4. Bàn tay và bàn chân</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-5-1507437321909.jpg\" id=\"img_1c02beb0-abe2-11e7-81d9-d137edec0f5b\" w=\"564\" h=\"423\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 6.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 6.\" rel=\"lightbox\" photoid=\"1c02beb0-abe2-11e7-81d9-d137edec0f5b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-5-1507437321909.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Lòng bàn tay và lòng bàn chân có thể bị vảy nến dạng palmar-plantar. Tại đây, da bị đóng vảy, nứt nẻ và đôi khi bị phồng giộp hoặc nổi màu đỏ. Người bệnh sẽ phải chịu đựng cảm giác đau đớn.</p><p><b>5. Móng tay</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-6-1507437321907.jpg\" id=\"img_1c28bd40-abe2-11e7-a065-7937920d296b\" w=\"493\" h=\"335\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 7.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 7.\" rel=\"lightbox\" photoid=\"1c28bd40-abe2-11e7-a065-7937920d296b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-6-1507437321907.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Vảy nến có thể ảnh hưởng tới móng, nơi nó gây ra tình trạng đổi màu móng sang sắc hơi vàng. Nó thậm chí có thể làm rời phần móng ra khỏi nền móng (lớp mô ở đáy móng tay, móng chân). </p><p>Bác sĩ Doris Day tiết lộ: \"Với những người không phải bác sĩ da liễu, trông nó có vẻ như bệnh nhiễm trùng nấm, nhưng vảy nến móng tay hơi khác\". Sự thay đổi ở móng xuất hiện ở khoảng 50% số người mắc vảy nến.</p><p><b>6. Bộ phận sinh dục</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-7-1507437321904.jpg\" id=\"img_1ccbdb10-abe2-11e7-aaaa-51ac0faef9da\" w=\"642\" h=\"361\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 8.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 8.\" rel=\"lightbox\" photoid=\"1ccbdb10-abe2-11e7-aaaa-51ac0faef9da\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-7-1507437321904.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Thật không may, vị trí nhạy cảm này cũng có thể trở thành mục tiêu tấn công của vảy nến. Bác sĩ Doris cho biết: \"Tôi từng có những bệnh nhân nam bị vảy nến trên dương vật và bệnh nhân nữ bị vảy nến ở âm đạo\".</p><p>Ban đầu, người ta có thể nhầm lẫn nó với một <a class=\"seo-suggest-link\" href=\"http://afamily.vn/benh-lay-nhiem-qua-duong-tinh-duc.html\" target=\"_blank\" title=\"bệnh lây nhiễm qua đường tình dục\">bệnh lây nhiễm qua đường tình dục</a> nào đó, nhưng không phải như vậy. Vảy nến ở bộ phận sinh dục giống vảy nến ở các vị trí khác trên cơ thể nhưng trường hợp này, khi xuất hiện ở các nếp gấp da (như háng), nó thường trơn và sáng với vẻ ngoài giống sáp, thường không tạo vảy như vảy nến dạng mảng.</p><p><b>7. Ở các nếp gấp da, như mông, nách hoặc bên dưới vú</b></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-10-1507437609782.jpg\" id=\"img_c46c4d00-abe2-11e7-8a08-1bdf9c92da84\" w=\"468\" h=\"392\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 9.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 9.\" rel=\"lightbox\" photoid=\"c46c4d00-abe2-11e7-8a08-1bdf9c92da84\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-10-1507437609782.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><p>Dạng vảy nến này được biết đến với tên gọi vảy nến ngược và thường xuất hiện dưới dạng những thương tổn rất đỏ bên dưới đầu gối, ở nách hay thậm chí trên mông. Nó cũng có thể tấn công vùng bên dưới vú và gây khó chịu do tình trạng ra mồ hôi cũng như cọ xát giữa các vùng da với nhau.</p><h2><b>Cách điều trị bệnh vảy nến</b></h2><div class=\"VCSortableInPreviewMode active noCaption\" type=\"Photo\" style=\"\"><div><img src=\"http://afamilycdn.com/2017/benh-vay-nen-9-1507437321899.jpg\" id=\"img_1d0f9ad0-abe2-11e7-ad77-9f411bebcc1b\" w=\"400\" h=\"225\" alt=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 10.\" title=\"7 vị trí trên cơ thể dễ bị bệnh vảy nến tấn công nhất - Ảnh 10.\" rel=\"lightbox\" photoid=\"1d0f9ad0-abe2-11e7-ad77-9f411bebcc1b\" type=\"photo\" style=\"max-width:100%;\" data-original=\"http://afamilycdn.com/2017/benh-vay-nen-9-1507437321899.jpg\" width=\"\" height=\"\"></div><div class=\"PhotoCMS_Caption\"><p placeholder=\"[nhập chú thích]\"></p></div></div><p>Bệnh vảy nến có thể kéo dài cả đời nhưng vẫn có nhiều cách điều trị khác nhau, bao gồm sử dụng kem bôi, thuốc rửa với bệnh dạng nhẹ. Trong trường hợp nghiêm trọng hơn, bác sĩ sẽ kê đơn thuốc biến đổi miễn dịch sinh học có tác dụng biến đổi phản ứng của hệ miễn dịch dẫn tới bệnh vảy nến và có thể giảm tác động của các chất trong cơ thể gây ra tình trạng viêm.</p><p>Bác sĩ Siegel nhấn mạnh: \"Bất kể vị trí xuất hiện của vảy nến hay mức độ bệnh, nó cần được điều trị một cách phù hợp. Điều trị tất cả các mức độ bệnh vảy nến có thể cải thiện đáng kể chất lượng cuộc sống của bệnh nhân\".</p><p style=\"text-align: right;\"><i>Theo Shape</i></p>           <div data-check-position=\"af_detail_body_end\"></div>      ', 'D&#249; thực tế l&#224; căn bệnh vảy nến c&#243; thể xuất hiện ở bất cứ nơi đ&#226;u tr&#234;n cơ thể bạn th&#236; cũng c&#243; những vị tr&#237; tr&#234;n cơ thể dễ xuất hiện bệnh n&#224;y hơn cả. ', 'publish', 20, NULL, NULL, NULL, 6, 1, '2017-10-13 14:44:33', '2017-10-13 14:44:33', NULL),
(21, 'Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh', 'me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh', 'http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892808-150789138186152-thumbnail.jpg', 'Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh  		Nhà thiết kế thời trang lừng danh Tommy Hilfiger đang đăng bán biệt thự nghỉ dưỡng ven biển tại Miami với giá 27,5 triệu USD (tương đương 627 tỷ VNĐ).  		<script type=\"text/javascript\">window.onload = function () {resizeNewsImage(\"news-image\", 699);}</script> <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 1\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-1-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Căn hộ rộng 1.300m2, nằm tại bờ biển Golden Beach, với thiết kế đa sắc màu độc nhất vô nhị của Tommy Hilfiger khiến nhiều người ngưỡng mộ. Bể bơi ở sân sau được thiết kế rộng rãi, thoáng, tạo điều kiện cho chủ nhân tổ chức các bữa tiệc sang chảnh ngay tại nhà.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 2\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-2-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Hilfiger và vợ anh, Dee, đã mua ngôi nhà vào năm 2013 với giá 17,25 triệu USD (tương đương 393 tỷ). Họ mạnh tay chi trả số tiền lên tới hàng triệu USD để tân trang, biến căn nhà trở nên thật độc đáo với những nét chấm phá về màu sắc.</p><p><script language=\'javascript\' type=\'text/javascript\'>//<![CDATA[   try {if(ADS_159_15s!=undefined){document.write( ADS_159_15s);ADS_159_15s.start();}}catch(e){}   //]]></script></p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 3\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-3-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Mỗi căn phòng đều được trang trí bởi những tác phẩm nghệ thuật do Hilfiger kỳ công sưu tầm. Thậm chí, có những món đồ hiếm được anh tậu với mức giá 10 triệu USD (tương đương 228 tỷ VNĐ).</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 4\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-4-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Phòng chiếu phim được phủ bởi sắc đỏ rực rỡ.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 5\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-5-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Quầy bar là nơi hiếm hoi trong nhà không có nhiều mảng đan xen nhưng lại được trang bị một quả cầu disco lấp lánh.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 6\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-6-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Những bức tường tại phòng ngủ của khách cũng được chủ nhân trang trí cầu kỳ, độc đáo.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 7\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-7-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Tường phòng tắm thậm chí còn độc đáo hơn họa tiết chuối bao phủ tứ phía.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 8\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892809-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-8-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Trong khi đó, phòng làm việc của Hilfiger được trang trí bằng vải màu xanh cổ vịt, đỏ, thể hiện phần nào tính chất công việc của chủ nhân.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 9\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892810-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-9-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Nội thất trong phòng ngủ chủ yếu thiên về sắc trắng mộng mơ, với một chiếc TV được giấu kín dưới chiếc giường, giúp tiết kiệm diện tích.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Mê mẩn biệt thự hướng biển trị giá 627 tỷ của nhà thiết kế lừng danh - 10\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892810-me-man-biet-thu-huong-bien-tri-gia-627-ty-cua-nha-thiet-ke-lung-danh-nha-cua-sao-10-1507891541-width600height338.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Hồ bơi được thiết kế ở khu vực sân sau, hướng thẳng ra phía đại dương, tạo cảm giác mênh mông, vô tận.</p>	  	', 'Nhà thiết kế thời trang lừng danh Tommy Hilfiger đang đăng bán biệt thự nghỉ dưỡng ven biển tại Miami với giá 27,5 triệu USD (tương đương 627 tỷ VNĐ).', 'publish', 21, NULL, NULL, NULL, 5, 1, '2017-10-13 14:47:20', '2017-10-13 14:47:20', NULL),
(22, 'Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền', 'chuyen-lam-giau-dang-kham-phuc-cua-ty-phu-tung-phai-ban-mau-kiem-tien', 'http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892794-150789276644505-thumbnail.jpg', 'Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền  		Khởi nghiệp bị từ chối 217 lần, phải bán máu kiếm sống, giờ tỷ phú này đã trở thành ông trùm đế chế cà phê thế giới. Chuỗi cửa hàng cà phê của ông hiện cũng là cái tên quen thuộc đối với giới trẻ Việt Nam.  		<script type=\"text/javascript\">window.onload = function () {resizeNewsImage(\"news-image\", 699);}</script> <p>Không phải ai xa lạ, vị tỷ phú này chính là Howard Schultz – chủ tịch kiêm lãnh đạo cấp cao của công ty cà phê Starbucks. Theo bảng xếp hạng tỷ phú trên tạp chí Forbes cuối năm 2016, Howard xếp ở vị trí thứ 232 với tổng tài sản là 3,1 tỷ USD.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền - 1\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892795-chuyen-khoi-nghiep-dang-kham-phuc-cua-ty-phu-tung-phai-ban-mau-kiem-tien-1-1507890946-width814height1085.jpg\" width=\"660\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Chân dung Howard Schultz trên tạp chí Time</p>    <p>Howard Schultz sinh năm 1953 tại Brooklyn, New York. Tuổi thơ của ông là những tháng ngày nghèo khó trong căn hộ giá rẻ được chính phủ trợ cấp, người cha bợm rượu thường xuyên bạo hành Howard và các anh chị em. Chính tuổi thơ đầy khó khăn đã giúp tôi luyện tính cách kiên cường cho Howard. Khi học đại học, Howard đi làm thêm liên tục, thậm chí nhiều lúc còn phải đi bán máu để trang trải phí sinh hoạt.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền - 2\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892795-chuyen-khoi-nghiep-dang-kham-phuc-cua-ty-phu-tung-phai-ban-mau-kiem-tien-2-1507890946-width1024height576.jpg\" width=\"660\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Ông chủ và thương hiệu Starbucks nổi tiếng toàn cầu</p>    <p>Sau khi tốt nghiệp, Howard vào làm cho một công ty Xerox của Thụy Điển. Năm 1979, ông lọt vào mắt xanh của Hammarplast – nhà kinh doanh máy pha cà phê của Thụy Điển. Cũng nhờ cơ hội này mà Howard Schultz đã biết tới Starbucks và được gặp gỡ ba nhà sáng lập của công ty này: Giáo sư Anh ngữ Jerry Baldwin, Giáo sư lịch sử Zev Siegl và nhà văn Gordon Bowker. Năm 29 tuổi, Howard Schultz đã thuyết phục Baldwin đưa ông vào vị trí nghiệp vụ bán lẻ và giám đốc chiến lược. Trong thời gian này, ông cũng từng khuyên Baldwin mở thử một cửa hàng Starbucks ở Ý. Tuy nhiên, ý tưởng này đã bị từ chối bởi Balwin mở quán cà phê chỉ để tìm nơi thư giãn, tụ tập bạn bè chứ hoàn toàn không có ý định bước chân vào ngành ẩm thực.</p><p><script language=\'javascript\' type=\'text/javascript\'>//<![CDATA[   try {if(ADS_159_15s!=undefined){document.write( ADS_159_15s);ADS_159_15s.start();}}catch(e){}   //]]></script></p>    <p align=\"center\"><img class=\"news-image\" alt=\"Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền - 3\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892795-chuyen-khoi-nghiep-dang-kham-phuc-cua-ty-phu-tung-phai-ban-mau-kiem-tien-3-1507890946-width468height439.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Xếp hạng của Howard Schultz trên tạp chí Forbes</p>    <p>Thời gian sau đó, Howard Schultz vẫn không nhụt chí. Ông vẫn miệt mài tìm cách kinh doanh quán cà phê. Có lẽ động lực lớn nhất khiến Howard kiên trì đến vậy là nhờ di nguyện lúc cuối đời của cha ông. Trong bức thư gửi Howard, cha ông đã bày tỏ tâm nguyện “có một quán cà phê với những bộ trang phục sạch sẽ”.</p>    <p>Tuy nhiên, con đường khởi nghiệp của Howard không hề dễ dàng như ông nghĩ. Để tự mở quán cà phê đầu tiên có tên II Giornale, Howard Schultz đã phải tìm đủ mọi cách để huy động nguồn vốn. Trong vòng một năm, ông đã gặp 242 người để xin tài trợ nhưng bị từ chối tới 217 lần. Thời điểm đó, Howard thậm chí còn không có đủ tiền để hỗ trợ người vợ đang mang thai.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền - 4\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892795-chuyen-khoi-nghiep-dang-kham-phuc-cua-ty-phu-tung-phai-ban-mau-kiem-tien-4-1507890946-width399height440.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Howard Schultz hồi nhỏ</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền - 5\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892795-chuyen-khoi-nghiep-dang-kham-phuc-cua-ty-phu-tung-phai-ban-mau-kiem-tien-5-1507890946-width500height313.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">3 nhà sáng lập của Starbucks Jerry Baldwin, Gordon Bowker, Zev Siegl năm 1971</p>    <p>May mắn thay, Jerry Baldwin lại ra tay giúp ông lần nữa. Hai năm sau đó, công ty Starbucks quyết định mua lại Giornale với giá 3,8 triệu USD và mời Howard Schultz làm CEO. Starbucks nhanh chóng trở thành thức uống yêu thích của người dân Mỹ. Năm 1992, Starbucks chính thức lên sàn giao dịch tại thị trường <a class=\'TextlinkBaiviet\' href=\'http://www.24h.com.vn/tin-chung-khoan-c161e3864.html\' title=\'chứng khoán\'>chứng khoán</a> New Yorl, thu nhập của công ty với 165 chuỗi cửa hàng lúc đó là 93 triệu USD.</p>    <p>Thương hiệu cà phê Starbucks hiện đã có 22.000 cửa hàng tại hơn 60 quốc gia trên thế giới, giá trị thương hiệu lên đến gần 100 tỷ USD. Đặc biệt, toàn thể nhân viên của Starbucks đều được đối xử như những “nhà hợp tác”. Tuy mô hình kinh doanh của Starbucks không hẳn là tốt nhất nhưng thái độ tôn trọng dành cho nhân viên và sức mạnh đoàn kết họ có được trên toàn cầu là một giá trị không gì có thể thay thế. 26 năm trước, Howard Schultz đã đề nghị hội đồng chủ tịch mở rộng phạm vi bảo hiểm y tế cho nhân viên. Kết quả là mỗi nhân viên đều được Starbucks chi trả 75% bảo hiểm y tế. Họ còn được mua cổ phiếu công ty, điều này khiến giá cổ phiếu của Starbucks đã tăng gấp 50 lần so với hơn 20 năm trước.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Chuyện làm giàu đáng khâm phục của tỷ phú từng phải bán máu kiếm tiền - 6\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507892796-chuyen-khoi-nghiep-dang-kham-phuc-cua-ty-phu-tung-phai-ban-mau-kiem-tien-6-1507890946-width620height348.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Howard Schultz chụp ảnh cùng nhân viên</p>    <p>Năm 2016, vị lãnh đạo có tâm và có tầm này đã xin rút khỏi vị trí CEO. Howard cho hay, tuy từ chức nhưng ông sẽ không rời xa công ty, ông vẫn luôn ở đó, tìm cách phát triển công ty và tạo cảm hứng cho hàng nghìn nhân viên mỗi ngày.</p>	  	', 'Khởi nghiệp bị từ chối 217 lần, phải bán máu kiếm sống, giờ tỷ phú này đã trở thành ông trùm đế chế cà phê thế giới. Chuỗi cửa hàng cà phê của ông hiện cũng là cái tên quen thuộc đối với giới trẻ Việt Nam.', 'publish', 22, NULL, NULL, NULL, 5, 1, '2017-10-13 14:47:20', '2017-10-13 14:47:20', NULL),
(23, 'Tranh cãi nảy lửa về phát triển công nghiệp ô tô', 'tranh-cai-nay-lua-ve-phat-trien-cong-nghiep-o-to', 'http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507890512-thumbnail.jpg', 'Tranh cãi nảy lửa về phát triển công nghiệp ô tô  		Theo Bộ Tài chính giảm thuế mà không gắn với yêu cầu về sản lượng và tỉ lệ nội địa hóa thì khó duy trì được ngành công nghiệp ô tô.  		<script type=\"text/javascript\">window.onload = function () {resizeNewsImage(\"news-image\", 699);}</script> <p>Tại một hội thảo – triển lãm diễn ra ngày 12-10 ở Hà Nội, khi bàn đến giải pháp để biến cơ hội phát triển ngành công nghiệp ô tô, nhờ vào lực kéo của thị trường, lực đẩy của công nghệ và lực nâng của chính sách thành hiện thực thì giữa các doanh nghiệp (DN), cơ quan nghiên cứu và nhà hoạch định chính sách lại chưa tìm được tiếng nói chung.</p>    <p>Là một tiếng nói có trọng lượng trong ngành, ông Toru Kinoshita, Chủ tịch Hiệp hội các nhà sản xuất ô tô Việt Nam (VAMA), cho biết rất ủng hộ chủ trương của Chính phủ thúc đẩy sản xuất xe và linh kiện ô tô tại Việt Nam nhưng chỉ có thể phát triển được khi thị trường đủ lớn, DN duy trì được sản lượng. VAMA để xuất trong ngắn hạn, cần giảm ngay hoặc bãi bỏ thuế nhập khẩu linh kiện về 0%, không gắn với điều kiện về sản lượng, nội địa hóa vì từ năm 2018, thuế nhập khẩu xe nguyên chiếc từ ASEAN bằng 0%, nếu tiếp tục duy trì chính sách thuế nhập khẩu linh kiện như hiện nay sẽ trở thành chính sách thuế hỗ trợ nhập khẩu. Đặc biệt là chính sách thuế phải nhất quán, không được \"lúc nóng, lúc lạnh\".</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Tranh cãi nảy lửa về phát triển công nghiệp ô tô - 1\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/Tranh-cai-nay-lua-ve-phat-trien-cong-nghiep-o-to-1-1507890258-width540height360.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Doanh nghiệp đề xuất giảm thuế để hỗ trợ ngành công nghiệp ô tô phát triển, trong khi Bộ <a class=\'TextlinkBaiviet\' href=\'http://www.24h.com.vn/tai-chinh-bat-dong-san-c161.html\' title=\'Tài chính\'>Tài chính</a> cho rằng giảm thuế mà không gắn với yêu cầu về sản lượng và tỉ lệ nội địa hóa thì khó duy trì được ngành công nghiệp ô tô. Ảnh: Hoàng Triều</p>    <p>Ông Hồ Mạnh Tuấn, Phó tổng giám đốc Tổng Công ty Máy Động lực và Máy nông nghiệp Việt Nam (VEAM), đề xuất cần khuyến khích DN nâng cao tỉ lệ nội địa hóa bằng cách giảm thuế thu nhập DN theo tỉ lệ nội địa hoá; thuế tiêu thụ đặc biệt tính trên giá trị xe trừ đi giá trị nội địa hóa dùng trên loại xe đó.</p><p><script language=\'javascript\' type=\'text/javascript\'>//<![CDATA[   try {if(ADS_159_15s!=undefined){document.write( ADS_159_15s);ADS_159_15s.start();}}catch(e){}   //]]></script></p>    <p>Bà Nguyễn Thị Thanh Hằng, Phó vụ trưởng vụ Chính sách thuế (Bộ Tài chính), bức xúc: 20 năm qua Việt Nam đã có chính sách bảo hộ rất cao đối với DN ô tô, thuế nhập khẩu linh kiện với nhập khẩu xe nguyên chiếc luôn duy trì chênh lệch 50%-60%, chỉ đến gần thời điểm năm 2017-2018 mới giảm theo cam kết hội nhập. \"Tôi rất thất vọng vì tại hội thảo này, ngoại trừ thông điệp đáng mừng của một số DN là quyết tâm xây dựng công nghiệp ô tô, các DN không đưa ra được giải pháp đột phá gì mà lại chỉ tập trung vào thuế, đề xuất giảm thuế. Như thế sẽ không giải quyết được gì vì chính sách thuế đã có hết rồi. DN nên tập trung vào giải pháp khác để phát triển công nghiệp hỗ trợ (CNHT). Chúng tôi đã rất vất vả để đưa ra giải pháp về thuế với mục tiêu hỗ trợ ngành công nghiệp ô tô nhưng VAMA vẫn không ủng hộ. Giảm thuế nhập khẩu linh kiện phải gắn với sản lượng và tỉ lệ nội địa hóa vì như thế mới đảm bảo duy trì sản xuất lắp ráp, từ đó mới có CNHT\" - bà Hằng giải thích.</p>    <table align=\"right\" cellpadding=\"3\" cellspacing=\"0\" style=\"border:1px solid #bbb;background-color:#DEF1C8;margin:5px;\" width=\"210\">  	<tbody>  		<tr>  			<td style=\"text-align:justify\" valign=\"top\">  			<p>Mới đây, Bộ Tài chính trình Chính phủ đề xuất điều chỉnh thuế suất nhập khẩu linh kiện ô tô về 0%, nhằm bảo hộ ngành sản xuất ô tô trong nước, giúp các DN giảm chi phí sản xuất, từ đó giảm giá xe, tăng lợi thế cạnh tranh với xe nhập khẩu khi thời điểm 2018 thuế nhập khẩu nguyên chiếc từ ASEAN về 0% đang tới gần. Điều kiện được hưởng thuế suất 0% là linh kiện không sản xuất được ở Việt Nam. Bên cạnh đó còn có các điều kiện về tỷ lệ tăng trưởng; sản lượng tối thiểu là từ 34.000 xe/năm. Theo điều kiện này, chỉ có 3 DN đáp ứng được.</p>  			</td>  		</tr>  	</tbody>  </table>    <p>Theo bà Hằng, giảm thuế mà không gắn với yêu cầu về sản lượng và tỉ lệ nội địa hóa thì khó duy trì được ngành công nghiệp ô tô. Năm 2004, Chính phủ đã chuyển cách tính thuế nhập khẩu CKD theo linh kiện rời thì thay vì nhập nguyên chiếc, một số DN FDI lại tháo rời ra để hưởng thuế thấp cho linh kiện. Từ đó đến nay tại sao nội địa hóa vẫn thấp như thế. Vậy DN có thực sự muốn nâng cao nội địa hóa không? Chúng tôi rất trăn trở vì biết rằng nội địa hóa phải gắn với cam kết gia nhập WTO nhưng đã mạnh dạn đưa ra kiến nghị nhưng vẫn bị phản đối. Nếu giảm hết thuế nhập khẩu linh kiện làm sao phát triển công nghiệp phụ trợ được. Mục tiêu của chúng tôi là phải giảm thuế có điều kiện. Mong các DN ủng hộ, không nên thời điểm này còn đưa ra quan điểm khác hẳn như thế.</p>    <p>Không đồng tình với quan điểm của đại diện Bộ Tài chính, ông Phan Đăng Tuất, Phó chủ tịch CNHT Việt Nam, lại cho rằng phải đặt vấn đề ta xây dựng một \"nền công nghiệp ô tô\" để có chính sách vượt lên thì mới tháo gỡ được, nếu chỉ nhìn đây là một ngành công nghiệp và loay hoay với thuế 0% hay cao hơn thì không thể thành công. \"Nếu chị Hằng muốn tham khảo, tôi sẽ cung cấp tài liệu để có cái nhìn vượt qua những ý kiến như trên. Từ năm 1958, Nhật Bản đã có sự đấu tranh trong nội các để đi đến chủ trương là toàn bộ tiền thu được từ sản xuất ô tô không được dùng vào việc gì khác, chỉ để đầu tư làm đường giao thông vì họ nhìn thấy rõ không đầu tư cho giao thông thì không có đường đi, không phát triển được công nghiệp ô tô. Đó mới là đột phá\" - ông Tuất nói.</p>    <p>Ông Tuất kể thêm: \" Một hội viên của chúng tôi vừa có đơn đặt hàng 200 chi tiết nhưng không làm được vì số lượng phải gấp nhiều lần như thế mới đủ tiền làm khuôn. Để sản xuất cánh cửa xe, chi phí làm khuôn đã mất 2 triệu USD. Nên quy định số lượng là rất khổ cho DN. Không bao giờ ở lần đặt hàng đầu tiên, đối tác đã đặt 10 vạn chiếc như họ cần mà còn \"mồi\" chán. Họ chỉ đặt 100 hoặc 1.000 cái để xem có đáp ứng được không, quá trình thử nghiệm này không dưới 2 năm, sau đó mới đặt hàng chính thức. Tôi thỉnh cầu đừng để xuất mức thuế gắn với tỉ lệ và số lượng. Muốn làm chính sách hãy đến DN tìm hiểu thực tiễn đi. Đáng lẽ với cách hiểu là \"nền công nghiệp ô tô\" thì phải dung dưỡng, hãy đọc những cuốn sách của Hàn Quốc, Nhật Bản để thấy họ coi đó là một nền công nghiệp. Còn coi đó là đối tượng quản lý thì không được\".</p>    <table align=\"center\" cellpadding=\"3\" cellspacing=\"0\" style=\"border:1px solid #bbb;background-color:#DEF1C8;margin:5px;margin-left:auto;margin-right:auto\" width=\"500\">  	<tbody>  		<tr>  			<td style=\"text-align:justify\" valign=\"top\">  			<p><strong>Trông đợi vào nhân tố mới</strong></p>    			<p>Đã hơn 20 năm kể từ khi Việt Nam đặt mục tiêu phát triển ngành công nghiệp ô tô như một ngành <a class=\'TextlinkBaiviet\' href=\'http://www.24h.com.vn/tai-chinh-bat-dong-san-c161.html\' title=\'kinh tế\'>kinh tế</a> mũi nhọn, đến nay Việt Nam vẫn chưa đạt được tiêu chí của ngành sản xuất ô tô mà thực chất mới chỉ ở mức độ lắp ráp đơn giản. Giá bán xe vẫn ở mức cao so với khu vực, tỉ lệ nội địa hóa xe con đạt bình quân 7%-10%, tỉ lệ sở hữu ô tô chỉ đạt 23 xe/1.000 dân. Xe sản xuất, lắp ráp ở Việt Nam vẫn có giá thành cao hơn khoảng 20% so với xe nhập khẩu từ khu vực.</p>    			<p>Các DN cho rằng chỉ có một cách để cắt giảm chi phí là tăng tỉ lệ nội địa hoá. Một chiếc ô tô có khoảng 30.000 linh kiện, trong đó 70% phải nhập khẩu, riêng chi phí đóng gói, vận chuyển đã rất cao. Kinh nghiệm phát triển ô tô các nước ASEAN cho thấy phải kiên trì thúc đẩy phát triển thị trường và gia tăng sản lượng các dòng xe, tăng cường nội địa hóa giúp tiết giảm chi phí sản xuất. Tuy nhiên, Việt Nam chưa có những hướng dẫn cụ thể cho giải pháp này.</p>    			<p>Ông Võ Quang Huệ, phó tổng giám đốc tổ hợp sản xuất ô-tô Vinfast, đưa ra những tín hiệu tích cực từ một DN mới tham gia thị trường khi cho biết đã ký kết với VAMA, Toyota và trực tiếp gặp gỡ lãnh đạo các hãng ô tô nước ngoài ở Việt Nam để kêu gọi hợp tác đầu tư theo chuỗi. Chiến lược của Vinfast là dành 30% diện tích của KCN Đình Vũ (Hải Phòng) cho các DN công nghiệp phụ trợ, trong đó có các DN Việt Nam. \"Tinh thần của chúng tôi là kêu gọi nhiều nhà đầu tư mới vào Việt Nam cùng tham gia chuỗi cung ứng của Việt Nam để đạt tỉ lệ nội địa hóa 40%-60%, phải đạt tiêu chí chất lượng ô tô, tiếp theo mới là giá cả. Thành công của nhà sản xuất xe hơi không chỉ dừng lại ở năng lực nội tại mà ở mạng lưới công nghiệp phụ trợ hoàn hảo nên Vinfast đã phối hợp với các đối tác để giảm chi phí\" - ông Huệ nói.</p>    			<p>Theo chiến lược của Vinfast, DN này sẽ cung cấp ra thị trường 100.000 chiếc xe con đầu tiên vào quý III-2018 và nâng lên 500.000 chiếc vào năm 2025 để hiện thực hóa giấc mơ sở hữu ô tô của người Việt Nam.</p>  			</td>  		</tr>  	</tbody>  </table>    <p></p>	  	', 'Theo Bộ Tài chính giảm thuế mà không gắn với yêu cầu về sản lượng và tỉ lệ nội địa hóa thì khó duy trì được ngành công nghiệp ô tô.', 'publish', 23, NULL, NULL, NULL, 5, 1, '2017-10-13 14:47:21', '2017-10-13 14:47:21', NULL),
(24, 'Công bố thanh tra cổ phần hóa Hãng phim truyện Việt Nam', 'cong-bo-thanh-tra-co-phan-hoa-hang-phim-truyen-viet-nam', 'http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507887351-thumbnail.jpg', 'Công bố thanh tra cổ phần hóa Hãng phim truyện Việt Nam  		Sáng nay (13/10), Thanh tra Chính phủ đã chính thức công bố quyết định thanh tra việc cổ phần hóa Hãng phim truyện Việt Nam.  		<script type=\"text/javascript\">window.onload = function () {resizeNewsImage(\"news-image\", 699);}</script> <p>Thanh tra Chính phủ chính thức công bố quyết định thanh tra việc cổ phần hóa Hãng Phim truyện Việt Nam (VFS) tại Bộ Văn hóa - Thể thao và Du lịch, cơ quan chủ quản của Hãng.</p>    <p>Trong 30 ngày làm việc tới đây, Thanh tra Chính phủ sẽ thanh tra từ khi bắt đầu công tác cổ phần hóa Hãng Phim truyện Việt Nam (năm 2014) đến khi thành lập Công ty Cổ phần Đầu tư và Phát triển Hãng Phim truyện Việt Nam (6/2017). Theo báo Thanh tra, trong trường hợp cần thiết có thể xem xét trước hoặc sau thời gian trên.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Công bố thanh tra cổ phần hóa Hãng phim truyện Việt Nam - 1\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/Cong-bo-thanh-tra-co-phan-hoa-Hang-phim-truyen-Viet-Nam-1-1507887412-width480height320.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Hãng phim truyện Việt Nam số 4 Thụy Khuê</p>    <p>Đoàn thanh tra sẽ gồm 7 người. Tổng Tranh tra Chính phủ giao Vụ Giám sát, Thẩm định và Xử lý sau thanh tra giám sát hoạt động thanh tra.&nbsp;</p>    <p>Chủ trì buổi công bố, Phó Tổng Thanh tra Đặng Công Huấn cho biết, đây là cuộc thanh tra đột xuất do Thủ tướng Chính Phủ giao. Thủ tướng yêu cầu, báo cáo kết quả thanh tra trước ngày 1/12/2017.</p>    <p>Phó Tổng Thanh tra yêu cầu, Bộ Văn hóa - Thể thao - Du lịch phối hợp với Đoàn Thanh tra, cung cấp đầy đủ, kịp thời hồ sơ, chứng từ, tài liệu theo quy định, giao cho đơn vị đầu mối bố trí thời gian, cán bộ có trách nhiệm để đảm bảo cuộc thanh tra hoàn thành đúng thời gian.</p>    <p>Theo Phó Tổng Thanh tra, mục đích của thanh tra lần này là kiểm tra quá trình cổ phần hóa Hãng Phim truyện Việt Nam có được triển khai đúng quy định của pháp luật hay không, xem xét nội dung nào vướng mắc cần phải tháo gỡ, điều chỉnh.</p>    <p>Phó Tổng Thanh tra yêu cầu, Đoàn Thanh tra, Tổ Giám sát phải làm việc chất lượng, minh bạch; thực hiện nghiêm quy chế phát ngôn và bảo mật tài liệu theo quy định.</p>    <p>Thứ trưởng Bộ Văn hóa - Thể thao - Du lịch, Huỳnh Vĩnh Ái - người ký quyết định cổ phần hóa VFS cho rằng việc cổ phần hóa này là quá trình trình phức tạp, được tiến hành từ năm 2008 đến nay mới thực hiện được.&nbsp;</p>    <p>Vị Thứ trưởng khẳng định sẽ phối hợp với Đoàn Thanh tra, tập trung dồn lực, bảo đảm đúng thời gian, để báo cáo Thủ tướng Chính phủ trước ngày 1/12.</p>    <p>Trước đó, vấn đề cổ phần hóa hãng phim truyện Việt Nam gây xôn xao dư luận khi xuất hiện những xung đột giữa các nghệ sĩ làm việc tại hãng và cổ đông chiến lược. Các vấn đề bất cập như tranh chấp đất vàng, quy trình cổ phần hóa... cũng được dư luận quan tâm.</p>	  	', 'Sáng nay (13/10), Thanh tra Chính phủ đã chính thức công bố quyết định thanh tra việc cổ phần hóa Hãng phim truyện Việt Nam.', 'publish', 24, NULL, NULL, NULL, 5, 1, '2017-10-13 14:47:21', '2017-10-13 14:47:21', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `description`, `status`, `pos`, `seo_title`, `seo_keyword`, `seo_description`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(25, 'Hội Môi giới khuyến cáo nhà đầu tư tỉnh táo khi giao dịch ở Đông Anh, Long Biên', 'hoi-moi-gioi-khuyen-cao-nha-dau-tu-tinh-tao-khi-giao-dich-o-dong-anh-long-bien', 'http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507878642-thumbnail.jpg', 'Hội Môi giới khuyến cáo nhà đầu tư tỉnh táo khi giao dịch ở Đông Anh, Long Biên  		Theo Hội Môi giới BĐS, ở Đông Anh, Long Biên do có thông tin về việc chuẩn bị xây dựng một số cây cầu nên đã xuất hiện một số môi giới không chuyên hoạt động đầu cơ, tăng giá, trục lợi ở những khu đất không nằm trong quy hoạch phát triển dự án...  		<script type=\"text/javascript\">window.onload = function () {resizeNewsImage(\"news-image\", 699);}</script> <p>Số liệu từ báo cáo tình hình giao dịch <a class=\'TextlinkBaiviet\' href=\'http://www.24h.com.vn/chung-cu-nha-dat-bat-dong-san-c338.html\' title=\'bất động sản\'>bất động sản</a> quý 3 của Hội Môi giới Bất động sản Việt Nam cho thấy, tại thị trường Hà Nội, nguồn cung mới đến từ 20 dự án, ước đạt trên 5.000 sản phẩm căn hộ; 75 dự án vẫn đang tiếp tục bán trên thị trường từ các năm trước đến quý 3 tạo nguồn cung căn hộ cho thị trường Hà Nội khoảng trên 20.000 căn.</p>    <p>Nguồn cung căn hộ chủ yếu tập trung ở một số quận: Hoàng Mai, Thanh Xuân, Hà Đông, Nam Từ Liêm, Bắc Từ Liêm và Long Biên.</p>    <p>Giá bán bình quân khoảng 25,7 triệu đồng mỗi mét vuông, tăng khoảng hơn 2% so với quý 2/2017. Mức tăng chủ yếu tại những dự án thuộc phân khúc trung cấp.</p>    <p>Cũng theo Hội Môi giới, tổng lượng nhà ở chung cư giao dịch tại Hà Nội đạt 4.955 giao dịch, giảm khoảng 8,5% so với quý 2. Trong đó, căn hộ cao cấp chiếm 5,4%, trung cấp chiếm 36,4% và giá thấp chiếm 58,2% tổng lượng giao dịch căn hộ trên địa bàn thành phố.</p>    <p>Trên tổng lượng giao dịch nhà ở tại Hà Nội nêu trên, có 3.850 giao dịch đã được ký hợp đồng chính thức (Theo Cục quản lý Nhà và Thị trường BĐS – Bộ Xây dựng); các giao dịch còn lại là giao dịch đã ký thỏa thuận đặt cọc, xác nhận mua.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Hội Môi giới khuyến cáo nhà đầu tư tỉnh táo khi giao dịch ở Đông Anh, Long Biên - 1\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/Hoi-Moi-gioi-khuyen-cao-nha-dau-tu-tinh-tao-khi-giao-dich-o-dong-Anh-Long-Bien-datdonganh-1507878734-width480height336.jpg\" /></p>    <p style=\"color:#0000ff;font-style:italic;text-align:center;\">Hội Môi giới khuyến cáo nhà đầu tư nên tỉnh táo khi giao dịch ở khu vực thị trường Đông Anh, Long Biên (Hà Nội). (Ảnh minh họa)</p>    <p>Các sản phẩm đất nền, biệt thự, nhà liền kề và shophouse do hiện tại có quá ít dự án ở phân khúc sản phẩm này nên lượng giao dịch chỉ chiếm 2% tổng lượng giao dịch trên toàn thành phố, đạt 98 giao dịch.</p>    <p>Dự báo tình hình giao dịch tại Hà Nội quý cuối năm, Hội Môi giới nhận định, về nguồn cung dự kiến sẽ có khoảng 20 dự án đủ điều kiện bán hàng, với nguồn cung khoảng trên 6.000 sản phẩm, nâng tổng nguồn cung của quý 4 lên 19.842 căn. Trong đó vẫn chủ yếu là sản phẩm ở phân khúc trung cấp. Lượng giao dịch quý cuối năm dự kiến đạt khoảng 5.500 giao dịch.</p>    <p>Cũng theo nhận định của Hội này, giá bán bình quân căn hộ sẽ tăng nhẹ khoảng 2%. Các dự án mới ra hàng tập trung chủ yếu ở các khu vực quận Hoàng Mai, Thanh Xuân, Hà Đông, Nam Từ Liêm, Bắc Từ Liêm và Long Biên.</p>    <p>Ở khu vực trung tâm thành phố, không có dự án đất nền nào, tuy nhiên có thể phát triển một số dự án nhỏ nhưng với số lượng không nhiều, chủ yếu nằm ở ven đô như Hoài Đức, Thạch Thất, Đông Anh.</p>    <p>Hội Môi giới cảnh báo, ở thị trường mới nổi tại huyện Đông Anh, quận Long Biên do có một số nguồn thông tin về việc chuẩn bị xây dựng một số cây cầu nên đã xuất hiện một số môi giới không chuyên hoạt động đầu cơ, tăng giá, trục lợi ở những khu đất không nằm trong quy hoạch phát triển dự án.</p>    <p>“Đây là những hiện tượng và thông tin không lành mạnh, ảnh hưởng tiêu cực tới thị trường. Khuyến cáo các nhà đầu tư và người tiêu dùng hết sức tỉnh táo, tránh để bị lợi dụng, xảy ra hậu quả và gây tình trạng hỗn loạn thị trường”, Hội Môi giới khuyến cáo.</p>	  	', 'Theo Hội Môi giới BĐS, ở Đông Anh, Long Biên do có thông tin về việc chuẩn bị xây dựng một số cây cầu nên đã xuất hiện một số môi giới không chuyên hoạt động đầu cơ, tăng giá, trục lợi ở những khu đất không nằm trong quy hoạch phát triển dự án...', 'publish', 25, NULL, NULL, NULL, 5, 1, '2017-10-13 14:47:21', '2017-10-13 14:47:21', NULL),
(26, 'Giá vàng hôm nay (13/10): Vẫn neo ở mức cao', 'gia-vang-hom-nay-13-10-van-neo-o-muc-cao', 'http://image.24h.com.vn/upload/4-2017/images/2017-10-13/1507868150-thumbnail.jpg', 'Giá vàng hôm nay (13/10): Vẫn neo ở mức cao  		Bất chấp sự phục hồi của đồng USD, giá vàng thế giới vẫn neo ở mức cao trong khi giá vàng trong nước tiếp tục biến động nhẹ.  		<script type=\"text/javascript\">window.onload = function () {resizeNewsImage(\"news-image\", 699);}</script> <p><strong>Thị trường miền Bắc</strong></p>    <p>Tại thời điểm 9h hôm nay (13/10), <a class=\'TextlinkBaiviet\' href=\'http://www.24h.com.vn/ttcb/giavang/giavang.php\' title=\'giá vàng\'>giá vàng</a> SJC tại Hà Nội được công ty CP SJC Sài Gòn niêm yết ở mức 36,48 – 36,70 triệu đồng/lượng (MV – BR), giảm 10.000 đồng/lượng hai chiều mua vào - bán ra so với chốt phiên hôm qua.</p>    <p>Giá vàng SJC tại Bảo Tín Minh Châu được niêm yết ở mức 36,53 – 36,59 triệu đồng/lượng, tăng 20.000 đồng/lượng hai chiều mua vào - bán ra. Thương hiệu vàng Rồng Thăng Long đang được giao dịch ở mức 35,61 – 36,06 triệu đồng/lượng (MV – BR).</p>    <p>Trên sàn giao dịch DOJI, giá vàng SJC bán lẻ hiện được niêm yết 36,52 – 36,60 triệu đồng/lượng (MV – BR), tăng 20.000 đồng/lượng hai chiều mua vào - bán ra; giá vàng SJC bán buôn được niêm yết 36,53 – 36,59 triệu đồng/lượng (MV – BR), tăng 20.000 đồng/lượng hai chiều mua vào - bán ra.</p>    <p align=\"center\"><img class=\"news-image\" alt=\"Giá vàng hôm nay (13/10): Vẫn neo ở mức cao - 1\" onclick=\"return openNewImage(this, \'\')\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-13/Gia-vang-hom-nay-4013-1041-Van-neo-o-muc-cao-gia-vang-1-1507868226-width500height334.jpg\" /></p>    <p align=\"center\" style=\"color:#0000ff;font-style:italic;text-align:center;\">Hình minh họa</p>    <p><strong>Thị trường miền Trung và miền Nam</strong></p>    <p>Tại cùng thời điểm, tại thị trường TP.HCM, giá vàng SJC được Công ty CP SJC Sài Gòn niêm yết ở mức 36,48 – 36,68 triệu đồng/lượng (MV – BR), giảm 10.000 đồng/lượng hai chiều mua vào - bán ra.</p>    <p>Trên sàn giao dịch DOJI, giá vàng SJC bán lẻ được niêm yết 36,53 – 36,61 triệu đồng/lượng (MV – BR); giảm 20.000 đồng/lượng chiều bán ra; giá vàng SJC bán buôn được niêm yết 36,54 – 36,60 triệu đồng/lượng (MV – BR), giảm 20.000 đồng/lượng chiều bán ra.</p>    <p>Cùng thời điểm tại Đà Nẵng, giá vàng SJC được công ty CP SJC Sài Gòn giao dịch ở mức 36,48 – 36,70 triệu đồng/lượng (MV – BR), giảm 10.000 đồng/lượng hai chiều mua vào - bán ra.</p>    <p><strong>Thị trường thế giới</strong></p>    <p>Trên thị trường vàng thế giới, giá vàng đêm qua đã có phiên tăng thứ 5 liên tiếp. Hiện, giá vàng giao ngay đứng ở mức 1.296 USD/ounce.</p>    <p>Quy đổi theo tỷ giá của Vietcombank, giá vàng thế giới hiện tương đương 35,40 triệu đồng/lượng (chưa tính thuế, phí, gia công), thấp hơn giá vàng trong nước khoảng 1,3 triệu đồng/lượng.</p>    <p>Đồng USD tuy đã có sự phục hồi nhưng vẫn còn ở mức khá thấp.</p>    <p>Các nước châu Á như Trung Quốc, Ấn Độ đang vào mùa cao điểm tiêu thụ vàng nên cầu về vàng vẫn lớn. Trong bối cảnh bất ổn có thể gia tăng bất cứ lúc nào, vàng vẫn là sự lựa chọn an toàn cho nhiều nhà đầu tư.</p>    <p>Vàng tăng giá còn do đồng USD vẫn ở mức khá thấp cho dù đã hồi phục. Theo dự báo của một số chuyên gia, giá vàng có thể vượt ngưỡng 1.300 USD/ounce vào tuần tới. Giới đầu tư hiện đang chờ biên bản cuộc họp chính sách tháng 9 của cục Dự trữ Liên bang Mỹ (Fed) về lộ trình lãi suất trong thời gian tới.</p>	  	', 'Bất chấp sự phục hồi của đồng USD, giá vàng thế giới vẫn neo ở mức cao trong khi giá vàng trong nước tiếp tục biến động nhẹ.', 'publish', 26, NULL, NULL, NULL, 5, 1, '2017-10-13 14:47:22', '2017-10-13 14:47:22', NULL),
